    "/contracts/TheZeneNFT.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity =0.8.13;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC1155/extensions/ERC1155URIStorageUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC1155/extensions/ERC1155SupplyUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/common/ERC2981Upgradeable.sol";

/**
 * @title Zene NFT Contract
 * @author Kuldeep K Srivastava
 * @notice User's media content (images, videos, etc.) can be minted as NFTs.
 * @dev Token standard used is ERC1155 (Multi Token Standard).
 *      This contract is upgradeable using Openzeppelin's upgradeable library.
 *      Proxy pattern used is Transparent, upgradeability managed by ProxyAdmin.
 *      This is also RBAC enabled using Access Control.
 *
 *      Visit https://docs.openzeppelin.com/contracts/4.x/upgradeable for more details
 */
contract TheZeneNFT is
    Initializable,
    ERC1155URIStorageUpgradeable,
    ERC1155SupplyUpgradeable,
    AccessControlUpgradeable,
    ERC2981Upgradeable
{
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    address public marketplaceContract;

    modifier tokenIdShouldNotExist(uint256 tokenId) {
        require(!exists(tokenId), "Token Id already exist");
        _;
    }

    modifier tokenIdShouldExist(uint256 tokenId) {
        require(exists(tokenId), "Token Id does not exist");
        _;
    }

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    /**
     *  @notice To initialise the inherited contracts, any storage variable
     *  @dev Due to a requirement of the proxy-based upgradeability system, no constructors can be used in upgradeable contracts.
     *       Visit https://docs.openzeppelin.com/upgrades-plugins/1.x/writing-upgradeable#initializers for more details
     *  @param _baseURI base URI to be set for all token types
     *  @param _defaultURI default URI to be set for all token types
     *  @param _admin Admin account
     */
    function initialize(
        string memory _baseURI,
        string memory _defaultURI,
        address _admin
    ) public initializer {
        __ERC1155URIStorage_init();
        __ERC1155Supply_init();
        __ERC1155_init(_defaultURI);
        __AccessControl_init();
        __ERC2981_init();

        // set the base URI
        _setBaseURI(_baseURI);

        // Granting _admin account the ADMIN ROLE
        _setupRole(ADMIN_ROLE, _admin);
        _setRoleAdmin(MINTER_ROLE, ADMIN_ROLE);

        // WARNING: The `ADMIN_ROLE` is also its own admin: it has permission to
        // grant and revoke this role. Extra precautions should be taken to secure
        // accounts that have been granted it.
        _setRoleAdmin(ADMIN_ROLE, ADMIN_ROLE);
    }

    /**
     * @dev See {IERC165-supportsInterface}.
     * @param interfaceId the interfaceId you want to know if that contracts supports (in bytes4 format)
     * @return bool true/false statement if the Zene NFT smart-contract supports a given interface
     * (e.g. IERC1155Upgradeable, IAccessControlUpgradeable or any of their respective parent classes)
     */
    function supportsInterface(bytes4 interfaceId)
        public
        view
        override(
            AccessControlUpgradeable,
            ERC1155Upgradeable,
            ERC2981Upgradeable
        )
        returns (bool)
    {
        return
            AccessControlUpgradeable.supportsInterface(interfaceId) ||
            ERC2981Upgradeable.supportsInterface(interfaceId) ||
            ERC1155Upgradeable.supportsInterface(interfaceId);
    }

    /**
     * @notice To let accounts with MINTER_ROLE to mint NFTs.
     * @param user User address which will receive the minted NFT.
     * @param tokenId Token Id of the NFT to be minted.
     * @param tokenURI Token URI to be set for the NFT.
     * @dev Amount is set as 1 to mint only NFTs. Refer to mint(address user, uint256 tokenId, uint256 amount, bytes data) for more info.
     * @return true if minted NFT successfully else false
     */
    function mintNFT(
        address user,
        uint256 tokenId,
        string memory tokenURI,
        address royaltyReceiver,
        uint96 royaltyPercentage
    )
        public
        onlyRole(MINTER_ROLE)
        tokenIdShouldNotExist(tokenId)
        returns (bool)
    {
        return
            mint(
                user,
                tokenId,
                1,
                tokenURI,
                "",
                royaltyReceiver,
                royaltyPercentage
            );
    }

    /**
     * @notice To let accounts with MINTER_ROLE to mint tokens.
     * @param user User address which will receive the minted tokens.
     * @param tokenId Token Id of the token type to be minted.
     * @param amount Amount of tokens to be minted. In case of NFT, it should be 1.
     * @param tokenURI Token URI to be set for the token.
     * @param data Additional data with no specified format
     * @return true if minted tokens successfully else false
     */
    function mint(
        address user,
        uint256 tokenId,
        uint256 amount,
        string memory tokenURI,
        bytes memory data,
        address royaltyReceiver,
        uint96 royaltyPercentage
    )
        public
        onlyRole(MINTER_ROLE)
        tokenIdShouldNotExist(tokenId)
        returns (bool)
    {
        require(royaltyPercentage < 10000, "Royalty percentage too high");
        _mint(user, tokenId, amount, data);
        if (bytes(tokenURI).length > 0) {
            _setURI(tokenId, tokenURI);
        }
        // To check if token has been minted
        // User balance must not be less than the amount minted for this tokenId
        if (balanceOf(user, tokenId) < amount) {
            return false;
        }
        // Set Royalty on the NFT is applicable
        if (royaltyReceiver != address(0) && royaltyPercentage > 0) {
            _setTokenRoyalty(tokenId, royaltyReceiver, royaltyPercentage);
        }
        return true;
    }

    /**
     * @notice To let accounts with MINTER_ROLE to mint tokens for user account and then transfer to receiver account
     * @param user User address for which token will be minted to prove provenance on-chain
     * @param receiver User address which will receive the minted token
     * @param tokenId Token Id of the token type to be minted.
     * @param tokenURI Token URI to be set for the token.
     * @return true if minted tokens successfully else false
     */
    function mintAndTransferNFT(
        address user,
        address receiver,
        uint256 tokenId,
        string memory tokenURI,
        address royaltyReceiver,
        uint96 royaltyPercentage
    )
        public
        onlyRole(MINTER_ROLE)
        tokenIdShouldNotExist(tokenId)
        returns (bool)
    {
        require(royaltyPercentage < 10000, "Royalty percentage too high");
        _mint(user, tokenId, 1, "");
        if (bytes(tokenURI).length > 0) {
            _setURI(tokenId, tokenURI);
        }
        _safeTransferFrom(user, receiver, tokenId, 1, "");

        // To check if token has been minted
        // Receiver balance for this tokenId must be 1
        if (balanceOf(receiver, tokenId) != 1) {
            return false;
        }
        // Set Royalty on the NFT is applicable
        if (royaltyReceiver != address(0) && royaltyPercentage > 0) {
            _setTokenRoyalty(tokenId, royaltyReceiver, royaltyPercentage);
        }
        return true;
    }

    /**
     * @notice To let accounts with ADMIN_ROLE to update base URI
     * @param baseURI the new base URI to be updated
     */
    function updateBaseURI(string memory baseURI)
        external
        onlyRole(ADMIN_ROLE)
    {
        require(bytes(baseURI).length > 0, "Base URI can not be empty");
        _setBaseURI(baseURI);
    }

    /**
     * @notice To let accounts with ADMIN_ROLE to update default URI
     * @param defaultURI the new default URI to be updated
     */
    function updateDefaultURI(string memory defaultURI)
        external
        onlyRole(ADMIN_ROLE)
    {
        require(bytes(defaultURI).length > 0, "default URI can not be empty");
        _setURI(defaultURI);
    }

    /**
     * @notice To let accounts with ADMIN_ROLE to set marketplace contract address
     * @param marketplace the marketplace contract address
     */
    function setMarketplaceContract(address marketplace)
        external
        onlyRole(ADMIN_ROLE)
    {
        marketplaceContract = marketplace;
    }

    /**
     * @dev Override isApprovedForAll to auto-approve marketplace contract
     */
    function isApprovedForAll(address account, address operator)
        public
        view
        override
        returns (bool)
    {
        // if marketplace contract address is detected, auto-return true
        if (operator == marketplaceContract) {
            return true;
        }
        // otherwise, use the default ERC1155Upgradeable.isApprovedForAll()
        return super.isApprovedForAll(account, operator);
    }

    /**
     * @dev See {ERC1155-_beforeTokenTransfer}.
     */
    function _beforeTokenTransfer(
        address operator,
        address from,
        address to,
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data
    ) internal virtual override(ERC1155Upgradeable, ERC1155SupplyUpgradeable) {
        ERC1155SupplyUpgradeable._beforeTokenTransfer(
            operator,
            from,
            to,
            ids,
            amounts,
            data
        );
    }

    /**
     * @dev See {IERC1155MetadataURI-uri}.
     */
    function uri(uint256 tokenId)
        public
        view
        virtual
        override(ERC1155Upgradeable, ERC1155URIStorageUpgradeable)
        tokenIdShouldExist(tokenId)
        returns (string memory)
    {
        return ERC1155URIStorageUpgradeable.uri(tokenId);
    }
}
"
    },
