contract Shipwright is ERC1155URIStorage, Ownable, ReentrancyGuard {
    using Strings for uint256;
    using Address for address;

    bool public mintActive = true;
    bool public burnActive = true;

    address private minter_address;

    mapping (address => bytes32) private previousActionSalt;
    mapping (address => bytes32) private previousActionSaltBurn;

    bytes32 private INIT_HASH;

    string private baseURI_;

    event MintStatusChanges(bool status);
    event BurnStatusChanges(bool status);
    event FeeChanged(uint256 newFee);

    modifier onlyMinter() {
        require(_msgSender() == minter_address, "Shipwright: Unauthorised");
        _;
    }

    constructor(string memory uri_) ERC1155(uri_) {
        INIT_HASH = keccak256(abi.encodePacked(block.timestamp, address(this)));
    }

    function mint(address to, uint256 id, bytes32 newSalt) external nonReentrant onlyMinter returns (uint256) {
        require(mintActive, "Shipwright: Mint is currently paused");
        previousActionSalt[tx.origin] = newSalt;
        _mint(to, id, 1, new bytes(0));
        return totalSupply(id);
    }

    function burn(address from, uint256 id, bytes32 newSalt) external onlyMinter returns (bool) {
        require(burnActive, "Shipwright: Burn not active");
        require(balanceOf(tx.origin, id) > 0, "Shipwight: Not Enough Balance");
        previousActionSaltBurn[tx.origin] = newSalt;
        _burn(from, id, 1);
        return true;
    }

    /// @dev onlyOwner

    function changeMintStatus(bool value) external onlyOwner {
        mintActive = value;
        emit MintStatusChanges(value);
    }

    function changeBurnStatus(bool value) external onlyOwner {
        burnActive = value;
        emit BurnStatusChanges(value);
    }

    function changeMinterAddress(address minter) external onlyOwner {
        require(minter != address(0), "Shipwright: minter != address(0)");
        require(minter.isContract(), "Shipwright: Minter must be a contract");
        minter_address = minter;
    }

    function changeBaseURI(string memory newURI) public onlyOwner {
        _setURI(newURI);
    }

    /// @dev Internal functions 

    function getUserSalt(address user) public view returns (bytes32) {
        return previousActionSalt[user] == 0 ? INIT_HASH : previousActionSalt[user];
    }

    function getUserSaltBurn(address user) public view returns (bytes32) {
        return previousActionSaltBurn[user] == 0 ? INIT_HASH : previousActionSaltBurn[user];
    }

    function changeOperatorFilterRegistryAddress(address newRegistry) external onlyOwner {
        OperatorFilterer.OPERATOR_FILTER_REGISTRY = IOperatorFilterRegistry(newRegistry);
    }

}
