      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

interface IPool {
    struct Vote {
        address staker;
        uint256 totalVotes;
    }

    event OracleStatusChanged(address indexed oracle, bool status);
    event OracleCostOfCapitalSet(address indexed oracle, uint256 cycle, uint256 total);
    event OracleCapitalAssignedSet(address indexed oracle, uint256 cycle, uint256 total);
    event OracleCapitalAssignedAccepted(address indexed oracle, uint256 cycle, uint256 totalAccepted);
    event LoanStatusChanged(address indexed loan, bool status);
    event OracleStandByCapitalCommitted(address indexed oracle, uint256 cycle, uint256 total);
    event LoanFunded(uint256 indexed cycle, address indexed oracle, address indexed loan, uint256 loanAmount);
    event LoanRepaid(address indexed loan, address indexed oracle, uint256 indexed loanCycle, uint256 total);
    event FundsAddedAfterDistribution(uint256 indexed cycle, address indexed oracle, uint256 total);
    event StandByCapitalReturnedAfterDistribution(uint256 indexed cycle, address indexed oracle, uint256 total);

    event NewDepositRequest(address indexed liquidityProvider, uint indexed cycle, uint256 brzTokens);
    event NewWithdrawRequest(address indexed liquidityProvider, uint indexed cycle, uint256 lpTokens);

    event CanceledDepositRequest(address indexed liquidityProvider, uint indexed cycle, uint256 brzTokens);
    event CanceledWithdrawRequest(address indexed liquidityProvider, uint indexed cycle, uint256 lpTokens);

    event FinalDepositRequest(address indexed liquidityProvider, uint indexed cycle, uint256 brzTokens);
    event FinalWithdrawRequest(address indexed liquidityProvider, uint indexed cycle, uint256 lpTokens);

    event DirectDeposit(address indexed liquidityProvider, uint indexed cycle, uint256 brzTokens);
    event DirectWithdraw(address indexed liquidityProvider, uint indexed cycle, uint256 lpTokens);

    event NewRewardDeposit(address indexed liquidityProvider, uint indexed cycle, uint256 total);
    event ClaimRewardDeposit(address indexed liquidityProvider, uint indexed cycle, uint256 total);

    function konaGlobals() external view returns (address _globals);

    function getOracles() external view returns (address[] memory);

    function directDeposit(uint amount) external;

    function directWithdraw(uint amount) external;

    function queueDeposit(uint256 amount) external;

    function queueWithdraw(uint256 amount) external;

    function finalizeDepositQueue(uint amount, uint requestedCycle) external;

    function finalizeWithdrawQueue(uint amount, uint requestedCycle) external;

    function depositBalancePerCycle(uint cycle, address user) external returns (uint);

    function requestFunding(address _loan) external returns (bool);

    function claimRewardDeposit(address _beneficiary, uint _total, uint _cycle) external;

    function totalCapitalSupplyPerCycle(uint cycle) external view returns (uint);

    function totalCapitalsAcceptedManual(uint cycle) external view returns (uint);

    function totalCapitalsAccepted(uint cycle) external view returns (uint);

    function assignCapitalAfterVoting(address[] memory _oracles) external;

    function isDateInsideCurrentCycle(uint256 _maturity) external returns (bool);

    function commitStandByCapital(uint256 _total) external;

    function getBRZForShares(uint shares, uint cycleNumber) external view returns (uint);

    function getShares(uint _brzAmount, uint cycleNumber) external view returns (uint);

    function acceptCapitalAssigned(uint256 _total) external;

    function getOriginalCapitalAssigned(address _oracle, uint256 _cycleNumber) external view returns (uint256);

    function getAcceptedCapitalAssigned(address _oracle, uint256 _cycleNumber) external view returns (uint256);

    function getCommittedStandByCapital(address _oracle, uint256 _cycleNumber) external view returns (uint256);

    function changeOracleStatus(address _oracle, bool _status) external;

    function repay(address _loan, uint256 _total) external;

    function getFundsAvailableToSpend(address _oracle, uint256 _cycleNumber) external view returns (uint256);

    function getCostOfCapital(address _oracle, uint256 _cycleNumber) external view returns (uint256);

    function addFundsAfterDistribution(uint256 _cycleNumber, address _oracle, uint256 _total) external;

    function sendStandByBackToOracle(uint256 _cycleNumber, address _oracle, uint256 _total) external;

    function hasOracles() external view returns (bool);

    function createRewardDeposit(address _beneficiary, uint256 _total) external;

    function getBRZBalanceWithRewards(address user) external view returns (uint);

    function atLeastOneOraclesHasCapital(uint256 _cycleNumber) external view returns (bool);

    function onCycleRollOver(uint lastCycle) external;
}
