    "contracts/KonaStaking.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import '@openzeppelin/contracts/access/AccessControl.sol';
import './KonaToken.sol';
import './interfaces/IKonaStaking.sol';
import './interfaces/IKonaGlobals.sol';
import './interfaces/IKonaPools.sol';
import './interfaces/IPool.sol';
import 'hardhat/console.sol';

contract KonaStaking is AccessControl, IKonaStaking {
    bytes32 public constant SLASHER_ROLE = keccak256('SLASHER_ROLE');

    KonaToken public konaToken;

    // Mapping of staker addresses to their staked balances
    mapping(address => uint256) public stakedBalance;

    // AccuracyBase for all the rates
    uint256 private constant ACCURACY_BASE = 1e18;

    // The InflationRewardsRate should be with the accuracy base of 1e18
    mapping(uint => uint) public inflationRewardRateCycle;

    // Rewardrate per balance of Oracles - cycle -> pool -> oracle -> rate - 1e18 base used
    mapping(uint => mapping(address => mapping(address => int))) public rewardPerOracleRate;

    // Total votes per oracle - cycle -> user -> pool -> oracle -> votes
    mapping(uint => mapping(address => mapping(address => mapping(address => uint)))) public votesCyleUserPoolOracle;

    // Total list of oracles - cycle -> user -> pool -> oracles
    mapping(uint => mapping(address => mapping(address => address[]))) public votesCycleUserPoolOracles;

    // Total list of pools - cycle -> user -> pools
    mapping(uint => mapping(address => address[])) public votesCycleUserPools;

    // Total amount of votes used by the user per pool in the cycle
    mapping(uint => mapping(address => mapping(address => uint))) public userCyclePoolVotedAmount;


    // Total amount of votes a pool received in the cycle
    mapping(uint => mapping(address => uint)) public totalVotedPoolPerCycle;

    // Total amount of votes an oracle in a pool received in the cycle
    mapping(uint => mapping(address => mapping(address => uint))) public totalVotedPoolOraclePerCycle;

    // Total amount of votes in the cycle
    mapping(uint => uint) public totalVotesPerCycle;

    // The last cycle the user stake was updated
    mapping(address => uint) public userLastUpdated;

    // The last cycle the user lpReward was updated
    mapping(address => uint) public userLastUpdatedLPReward;

    // Address of the KonaGlobals contract
    address public konaGlobals;

    // Total amount of tokens staked by all users
    uint256 public totalStaked;

    // Total amount of new tokens that have been minted as a result of inflation
    uint256 public newMintedTokens;

    mapping(uint => mapping(address => mapping(address => uint))) public brzRewardPerOracleRate;

    /**
     * @dev Contract constructor
     * @param _konaToken The address of the KonaToken contract
     */
    constructor(KonaToken _konaToken, address _konaGlobals) {
        konaToken = _konaToken;
        konaGlobals = _konaGlobals;

        // Grant DEFAULT_ADMIN_ROLE and SLASHER_ROLE to the contract deployer
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(SLASHER_ROLE, msg.sender);
    }

    /**
     * @dev Calculates adjustments for a user's stake in a cycle
     * @param user The user's address
     * @param cycle The cycle number
     */
    function calculateStakeForCycle(address user, uint cycle) private {
        // Get list of pools voted by user
        address[] storage userPools = votesCycleUserPools[cycle][user];

        // Get voted pool length
        uint poolLength = userPools.length;

        // Loop through pools
        for (uint poolIndex = 0; poolIndex < poolLength; poolIndex++) {
            // Get current pool
            address userPool = userPools[poolIndex];

            // Get voted oracles for pool
            address[] storage userOracles = votesCycleUserPoolOracles[cycle][user][userPool];

            // Get voted oracle length
            uint oracleLength = userOracles.length;

            // Loop through voted oracles
            for (uint oracleIndex = 0; oracleIndex < oracleLength; oracleIndex++) {
                // Get current oracle
                address userOracle = userOracles[oracleIndex];

                // Get the votes the user made for a specific oracle
                int votes = int(votesCyleUserPoolOracle[cycle][user][userPool][userOracle]);

                // Calculate balance adjustment for votes
                int balanceAdjustment = calculateBalanceAdjustment(user, userPool, userOracle, cycle, votes);

                // Update user's staked balance
                balanceAdjustment = int(stakedBalance[user]) + balanceAdjustment;
                stakedBalance[user] = uint(balanceAdjustment);
            }
        }
    }

    /**
     * @dev Calculates adjustment to user's stake based on the votes and oracle status
     * @param user The user's address
     * @param userPool The pool address
     * @param userOracle The oracle address
     * @param cycle The cycle number
     * @param votes Number of votes
     * @return Adjustment amount
     */
    function calculateBalanceAdjustment(
        address user,
        address userPool,
        address userOracle,
        uint cycle,
        int votes
    ) private returns (int) {
        // Get reward rate for oracle
        int rewardRate = rewardPerOracleRate[cycle][userPool][userOracle];

        // Calculate base adjustment based on votes and rate
        int baseAdjustment = (votes * rewardRate) / int(ACCURACY_BASE);

        // Apply inflation adjustment if base is positive
        if (baseAdjustment > 0) {
            // Get inflation rate for cycle
            uint inflationRate = inflationRewardRateCycle[cycle];

            // Calculate inflation adjustment
            uint inflationAdjustment = (uint(votes) * uint(inflationRate)) / ACCURACY_BASE;

            // Increase user stake by inflation adjustment
            _increaseStakerAfterInflation(user, inflationAdjustment);
        }

        return baseAdjustment;
    }

    /**
     * @dev Modifier to calculate user's position before working with the balance (for example voting)
     * Updates user's staked balance/votes to current cycle
     * @param user The user's address
     */
    modifier calculateUserPosition(address user) {
        // Get last cycle user position was updated
        stakedBalance[user] = virtualCurrentStakeBalance(user);
        userLastUpdated[user] = getCurrentCycle();
        _;
    }

    /**
     * @dev not to be used in a contract (gasheavy - lots of reads + loops) - Function to virtually calculate user's position in for the frontend
     * Reads user's staked balance/voting power of current cycle
     * @param user The user's address
     */
    function virtualCurrentStakeBalance(address user) public view returns (uint) {
        uint lastUpdated = userLastUpdated[user];

        // Get current cycle
        uint currentCycle = getCurrentCycle();
        uint balance = stakedBalance[user];
        if (lastUpdated != currentCycle) {
            // Loop through cycles since last update
            for (uint i = lastUpdated; i < currentCycle; i++) {
                // Calculate stake adjustments for each cycle
                balance = virtualCalculateStakeForCycle(user, i, balance);
            }
        }
        return balance;
    }

    /**
     * @dev Calculates adjustments for a user's stake in a cycle
     * @param user The user's address
     * @param cycle The cycle number
     * @param balance The balance of the user
     */
    function virtualCalculateStakeForCycle(address user, uint cycle, uint balance) internal view returns (uint) {
        // Get list of pools voted by user
        address[] storage userPools = votesCycleUserPools[cycle][user];
        // Get voted pool length
        uint poolLength = userPools.length;
        // Loop through pools
        for (uint poolIndex = 0; poolIndex < poolLength; poolIndex++) {
            // Get current pool
            address userPool = userPools[poolIndex];

            // Get voted oracles for pool
            address[] storage userOracles = votesCycleUserPoolOracles[cycle][user][userPool];

            // Get voted oracle length
            uint oracleLength = userOracles.length;

            // Loop through voted oracles
            for (uint oracleIndex = 0; oracleIndex < oracleLength; oracleIndex++) {
                // Get current oracle
                address userOracle = userOracles[oracleIndex];

                // Get the votes the user made for a specific oracle
                int votes = int(votesCyleUserPoolOracle[cycle][user][userPool][userOracle]);

                // Calculate balance adjustment for votes
                int balanceAdjustment = virtualCalculateBalanceAdjustment(userPool, userOracle, cycle, votes);

                // Update user's staked balance
                balanceAdjustment = int(balance) + balanceAdjustment;
                balance = uint(balanceAdjustment);
            }
        }
        return balance;
    }

    /**
     * @dev Calculates adjustment to user's stake based on the votes and oracle status
     * @param userPool The pool address
     * @param userOracle The oracle address
     * @param cycle The cycle number
     * @param votes Number of votes
     * @return Adjustment amount
     */
    function virtualCalculateBalanceAdjustment(
        address userPool,
        address userOracle,
        uint cycle,
        int votes
    ) internal view returns (int) {
        // Get reward rate for oracle
        int rewardRate = rewardPerOracleRate[cycle][userPool][userOracle];

        // Calculate base adjustment based on votes and rate
        int baseAdjustment = (votes * rewardRate) / int(ACCURACY_BASE);

        uint inflationAdjustment;
        // Apply inflation adjustment if base is positive
        if (baseAdjustment > 0) {
            // Get inflation rate for cycle
            uint inflationRate = inflationRewardRateCycle[cycle];

            // Calculate inflation adjustment
            inflationAdjustment = (uint(votes) * uint(inflationRate)) / ACCURACY_BASE;

            baseAdjustment += int(inflationAdjustment);
        }

        return baseAdjustment;
    }

    /**
     * @dev Allows a user to vote for an oracle in a pool
     * @param _pool The pool address
     * @param _oracle The oracle address
     * @param _totalVotes Number of votes to cast
     */
    function vote(address _pool, address _oracle, uint256 _totalVotes) public calculateUserPosition(msg.sender) {
        address konaPools = IKonaGlobals(konaGlobals).konaPools();
        (uint256 currentCycle, , IKonaPools.Status status) = IKonaPools(konaPools).getCycleStatus();
        require(status == IKonaPools.Status.VOTING, 'Only in voting period');
        require(IKonaPools(konaPools).isValidPool(_pool), 'Pool not valid');

        // Get total votes for this user
        address staking = IKonaGlobals(konaGlobals).konaStaking();
        uint256 totalVotes = IKonaStaking(staking).getTotalStakedByUser(msg.sender);

        // Calculate remaining votes
        uint256 remainingVotes = totalVotes - userCyclePoolVotedAmount[currentCycle][msg.sender][_pool];

        // Check enough remaining votes
        require(_totalVotes <= remainingVotes, 'Not enough votes');

        // Update user's votes this cycle
        // Update vote counts
        if (userCyclePoolVotedAmount[currentCycle][msg.sender][_pool] == 0) {
            votesCycleUserPools[currentCycle][msg.sender].push(_pool);
        }
        if (votesCyleUserPoolOracle[currentCycle][msg.sender][_pool][_oracle] == 0) {
            votesCycleUserPoolOracles[currentCycle][msg.sender][_pool].push(_oracle);
        }
        userCyclePoolVotedAmount[currentCycle][msg.sender][_pool] += _totalVotes;
        votesCyleUserPoolOracle[currentCycle][msg.sender][_pool][_oracle] += _totalVotes;
        totalVotedPoolOraclePerCycle[currentCycle][_pool][_oracle] += _totalVotes;
        totalVotedPoolPerCycle[currentCycle][_pool] += _totalVotes;
        totalVotesPerCycle[currentCycle] += _totalVotes;

        // Emit event
        emit StakerVoted(msg.sender, _pool, _oracle, currentCycle, _totalVotes);
    }

    /**
     * @dev Gas Optimized way to vote for multiple Oracles in one tx
     * @param _pool The pool address
     * @param oracles List oracle addresses
     * @param votes List of votes to cast
     */
    function multiVotePool(address _pool, address[] calldata oracles, uint[] calldata votes) public {
        address konaPools = IKonaGlobals(konaGlobals).konaPools();
        (uint256 currentCycle, , IKonaPools.Status status) = IKonaPools(konaPools).getCycleStatus();
        require(status == IKonaPools.Status.VOTING, 'only in voting period');

        // Get total votes for this user
        address staking = IKonaGlobals(konaGlobals).konaStaking();
        uint256 totalVotes = IKonaStaking(staking).getTotalStakedByUser(msg.sender);

        uint256 remainingVotes = totalVotes - userCyclePoolVotedAmount[currentCycle][msg.sender][_pool];

        uint localTotalVotes = 0;

        // Sum up all the votes that are scheduled to be accounted
        for (uint arrayIndex = 0; arrayIndex < votes.length; arrayIndex++) {
            localTotalVotes += votes[arrayIndex];
        }

        // Check enough remaining votes
        require(localTotalVotes <= remainingVotes, 'Not enough votes');

        // Only write after confirming he still has sufficient votes available
        for (uint arrayIndex = 0; arrayIndex < votes.length; arrayIndex++) {
            if (votesCyleUserPoolOracle[currentCycle][msg.sender][_pool][oracles[arrayIndex]] == 0) {
                votesCycleUserPoolOracles[currentCycle][msg.sender][_pool].push(oracles[arrayIndex]);
            }
            votesCyleUserPoolOracle[currentCycle][msg.sender][_pool][oracles[arrayIndex]] += votes[arrayIndex];
            totalVotedPoolOraclePerCycle[currentCycle][_pool][oracles[arrayIndex]] += votes[arrayIndex];
        }

        if (userCyclePoolVotedAmount[currentCycle][msg.sender][_pool] == 0) {
            votesCycleUserPools[currentCycle][msg.sender].push(_pool);
        }
        // Global non-user and non-oracle-specific accounting
        userCyclePoolVotedAmount[currentCycle][msg.sender][_pool] += localTotalVotes;
        totalVotedPoolPerCycle[currentCycle][_pool] += localTotalVotes;
        totalVotesPerCycle[currentCycle] += localTotalVotes;
    }

    /**
     * @dev  Pooling the multivote for better "accessability"
     * @param pools List of addresses
     * @param oracles List of List oracle addresses
     * @param votes List of List of votes to cast
     */
    function multiVote(address[] calldata pools, address[][] calldata oracles, uint[][] calldata votes) external {
        for (uint poolIndex = 0; poolIndex < pools.length; poolIndex++) {
            multiVotePool(pools[poolIndex], oracles[poolIndex], votes[poolIndex]);
        }
    }

    function claimReward(address pool) external {
        // Get last cycle user position was updated
        claimOutstandingLpReward(msg.sender, pool);
        userLastUpdatedLPReward[msg.sender] = getCurrentCycle();
    }

    /**
     * Claims user's LP Rewards up to the current cycle
     * @param user The user's address
     * @param user The pool address
     */
    function claimOutstandingLpReward(address user, address pool) public returns (uint) {
        // Get last cycle user position was updated
        uint lastUpdated = userLastUpdatedLPReward[user];

        // Get current cycle
        uint currentCycle = getCurrentCycle();

        if (lastUpdated != currentCycle) {
            // Loop through cycles since last update
            for (uint i = lastUpdated; i < currentCycle; i++) {
                // Calculate stake adjustments for each cycle
                uint rewardBRZ = virtualCalculateLPForCycle(user, pool, i, 0);
                IPool(pool).claimRewardDeposit(user, rewardBRZ, i);
            }
        }
    }

    /**
     * @dev not to be used in a contract (gasheavy - lots of read)s + loops) - Function to virtually calculate user's position in for the frontend
     * Reads user's staked balance/voting power of current cycle
     * @param user The user's address
     * @param pool The pool address
     */
    function getOutstandingLpReward(address user, address pool) public view returns (uint) {
        // Get last cycle user position was updated
        uint lastUpdated = userLastUpdatedLPReward[user];

        // Get current cycle
        uint currentCycle = getCurrentCycle();

        uint reward;
        if (lastUpdated != currentCycle) {
            // Loop through cycles since last update
            for (uint i = lastUpdated; i < currentCycle; i++) {
                // Calculate stake adjustments for each cycle
                uint rewardBRZ = IPool(pool).getBRZForShares(reward, i);
                rewardBRZ = virtualCalculateLPForCycle(user, pool, i, reward);
                reward += IPool(pool).getShares(rewardBRZ, i);
            }
        }
        reward = IPool(pool).getBRZForShares(reward, currentCycle);
        return reward;
    }

    function setBrzRewardPerOracleRate(
        uint cycle,
        address pool,
        address oracle,
        uint newBrzRewardRate
    ) external onlyRole(SLASHER_ROLE) {
        brzRewardPerOracleRate[cycle][pool][oracle] = newBrzRewardRate;
    }

    /**
     * @dev Calculates adjustments for a user's stake in a cycle
     * @param user The user's address
     * @param pool The pool address
     * @param cycle The cycle number
     * @param reward The balance of the user
     */
    function virtualCalculateLPForCycle(
        address user,
        address pool,
        uint cycle,
        uint reward
    ) internal view returns (uint) {
        // Get voted oracles
        address[] storage userOracles = votesCycleUserPoolOracles[cycle][user][pool];

        // Get voted oracle length
        uint oracleLength = userOracles.length;

        // Loop through voted oracles
        for (uint oracleIndex = 0; oracleIndex < oracleLength; oracleIndex++) {
            // Get current oracle
            address userOracle = userOracles[oracleIndex];
            // Get the votes the user made for a specific oracle
            uint votes = votesCyleUserPoolOracle[cycle][user][pool][userOracle];

            // Calculate reward adjustment for votes
            uint rewardAdjustment = virtualCalculateRewardAdjustment(userOracle, pool, cycle, votes);

            reward += rewardAdjustment;
        }

        return reward;
    }

    /**
     * @dev Calculates adjustment to user's stake based on the votes and oracle status
     * @param userOracle The oracle the user voted for
     * @param pool The pool the user voted for
     * @param cycle The cycle number
     * @param votes Number of votes
     * @return Adjustment amount
     */
    function virtualCalculateRewardAdjustment(
        address userOracle,
        address pool,
        uint cycle,
        uint votes
    ) internal view returns (uint) {
        uint rewardRate = brzRewardPerOracleRate[cycle][pool][userOracle];
        uint baseAdjustment = (votes * rewardRate) / 1e32;
        return baseAdjustment;
    }

    /**
     * @dev Stakes tokens by transferring them from the sender's address to the staking contract.
     * @param _total The amount of tokens to stake
     */
    function stake(uint256 _total) external calculateUserPosition(msg.sender) {
        konaToken.transferFrom(msg.sender, address(this), _total);
        stakedBalance[msg.sender] += _total;

        totalStaked += _total;

        emit StakeIncreased(msg.sender, _total);
    }

    function forceCalcPosition() external calculateUserPosition(msg.sender) {
        return;
    }

    /**
     * @dev Unstakes tokens by transferring them from the staking contract to the sender's address.
     * @param _total The amount of tokens to unstake
     */
    function unstake(uint256 _total) external calculateUserPosition(msg.sender) {
        uint unstakeable = getUnstakeable(msg.sender);
        require(unstakeable >= _total, 'Insufficient free balance');

        stakedBalance[msg.sender] -= _total;
        konaToken.transfer(msg.sender, _total);

        totalStaked -= _total;

        emit StakeReduced(msg.sender, _total);
    }

    /**
     * @dev Gets the amount a user can currently unstake
     * @param user User address
     * @return Unstakeable amount
     */
    function getUnstakeable(address user) public view returns (uint) {
        // Get user's staked balance
        uint localStaked = stakedBalance[user];

        // Get current cycle
        address konaPools = IKonaGlobals(konaGlobals).konaPools();
        (uint256 currentCycle, , ) = IKonaPools(konaPools).getCycleStatus();

        // Get list of pools voted by user this cycle
        address[] memory pools = votesCycleUserPools[currentCycle][user];

        // Start with full balance as available
        uint availableVotes = localStaked;

        // Check each pool
        for (uint i = 0; i < pools.length; i++) {
            //readability not gas optimized

            // Get available votes in this pool
            uint localAvailableVotes = availableVotesPool(user, pools[i]);

            // Update lowest amount across pools
            availableVotes = localAvailableVotes < availableVotes ? localAvailableVotes : availableVotes;
            // require(!getUserHasVotedInPool(currentCycle, msg.sender, pools[i]), 'Cannot unstake as staker has voted');
        }
        return availableVotes;
    }

    /**
     * @dev Gets available votes for a user in a pool
     * @param user User address
     * @param pool Pool address
     * @return Available vote amount
     */
    function availableVotesPool(address user, address pool) public view returns (uint) {
        // Get current cycle
        address konaPools = IKonaGlobals(konaGlobals).konaPools();
        (uint256 currentCycle, , ) = IKonaPools(konaPools).getCycleStatus();

        // Get user's staked balance
        uint localStaked = stakedBalance[user];

        // Subtract votes already used in this pool
        return localStaked - userCyclePoolVotedAmount[currentCycle][user][pool];
    }

    /**
     * @dev Increases the staked balance of a user after inflation.
     * @param _user The address of the user
     * @param _total The amount of tokens to increase
     */
    function increaseStakerAfterInflation(address _user, uint256 _total) external onlyRole(SLASHER_ROLE) {
        _increaseStakerAfterInflation(_user, _total);
    }

    function _increaseStakerAfterInflation(address _user, uint256 _total) internal {
        require(newMintedTokens >= _total, 'Not enough new minted tokens');

        stakedBalance[_user] += _total;
        totalStaked += _total;

        emit StakeIncreasedAfterInflation(_user, _total);
    }

    /**
     * @dev Increases the total amount of new minted tokens.
     * @param _newMintedTokens The amount of new minted tokens to increase
     */
    function increaseNewMintedTokens(uint256 _newMintedTokens) external onlyRole(SLASHER_ROLE) {
        newMintedTokens += _newMintedTokens;
    }

    function getUserHasVotedInPool(uint _cycle, address _user, address _pool) public view returns (bool) {
        return votesCycleUserPoolOracles[_cycle][_user][_pool].length > 0;
    }

    /**
     * @dev Returns the total staked balance of a user.
     * @param _user The address of the user
     * @return The total staked balance of the user
     */
    function getTotalStakedByUser(address _user) external view returns (uint256) {
        return stakedBalance[_user];
    }

    /**
     * @dev Returns the total staked amount across all users.
     * @return The total staked amount
     */
    function getTotalStaked() external view returns (uint256) {
        return totalStaked;
    }

    /**
     * @dev Gets the current cycle number from the KonaPools contract
     * @return Current cycle number
     */
    function getCurrentCycle() public view returns (uint) {
        // Call getCycleStatus on KonaPools via interface
        (uint currentCycle, , ) = IKonaPools(IKonaGlobals(konaGlobals).konaPools()).getCycleStatus();

        // Return current cycle
        return currentCycle;
    }

    /**
     * @dev Gets the total amount of Oracles a user voted for in a pool in a cycle
     * @param cycle Cycle to request
     * @param user The user to request
     * @param pool The pool to request
     * @return Current cycle number
     */
    function getOraclesInPoolLength(uint cycle, address user, address pool) external view returns (uint) {
        return votesCycleUserPoolOracles[cycle][user][pool].length;
    }

    /**
     * @dev Sets reward rate for an oracle in a pool for a cycle
     * @param cycle The cycle number
     * @param pool The pool address
     * @param oracle The oracle address
     * @param rate The reward rate
     */
    function setRewardPerOracleRate(
        uint cycle,
        address pool,
        address oracle,
        int rate
    ) external onlyRole(SLASHER_ROLE) {
        // Set reward rate mapping
        rewardPerOracleRate[cycle][pool][oracle] += rate;
    }

    /**
     * @dev Sets inflation reward rate for a cycle
     * @param cycle The cycle number
     * @param inflationRewardRate The inflation reward rate
     */
    function setInflationRateCycle(uint cycle, uint inflationRewardRate) external onlyRole(SLASHER_ROLE) {
        // Set inflation rate mapping
        inflationRewardRateCycle[cycle] = inflationRewardRate;
    }
}
"
