      "content": "//SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.0;

import "./Questable.sol";

contract SporebarkQuest is Questable {

  // ---------- CONSTRUCTOR ----------

  constructor(
    address _raiders, 
    address _questing, 
    address _boosted, 
    address _rewardAddress, 
    address _professions, 
    uint _professionId, 
    uint _returnHomeTimeDivisor, 
    uint _experienceAmount, 
    uint _baseRewardTime, 
    uint _twentyFivePercentBoostExperience, 
    uint _fiftyPercentRewardsBoostExperience,
    uint _minProfessionExp) 
  {
    require(_professionId > 0, "Invalid profession id");
    require(_returnHomeTimeDivisor > 0, "Invalid return home time value");
    require(_experienceAmount > 0, "Invalid xp amount");
    require(_baseRewardTime > 0, "Invalid base reward time");
    require(_twentyFivePercentBoostExperience > 0, "Invalid 25% xp boost");
    require(_fiftyPercentRewardsBoostExperience > 0, "Invalid 50% xp boost");


    raidersAddress = _raiders;
    questingRaiders = _questing; 
    boostedRaiders = _boosted;
    rewardAddress = _rewardAddress;
    professionsAddress = _professions;

    minProfessionExp = _minProfessionExp;
    professionId = _professionId;
    returnHomeTimeDivisor = _returnHomeTimeDivisor;
    experienceAmount = _experienceAmount;
    baseRewardTime = _baseRewardTime;
    twentyFivePercentBoostExperience = _twentyFivePercentBoostExperience;
    fiftyPercentRewardsBoostExperience = _fiftyPercentRewardsBoostExperience;
  }

  //OVERRIDE EXAMPLE - TEST WILL FAILS TO INDICATE CHANGE IN PLACE.
  /*
  function calcReturnTime(uint _raiderId) onQuest(_raiderId) public view override returns (uint) {
    return 500;
  }
  */

}"
    },
    "contracts/Questable.sol": {
      "content": "//SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC20/presets/ERC20PresetMinterPauser.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "./QuestingRaiders.sol";
import "./BoostedRaiders.sol";
import "./RaiderProfessions.sol";

contract Questable is Pausable, Ownable {

  using SafeMath for uint;
  using SafeERC20 for IERC20;

  address raidersAddress;
  address questingRaiders;
  address professionsAddress;
  address boostedRaiders;
  address rewardAddress;

  uint public professionId;
  uint public minProfessionExp; // how much XP they need to do this quest

  uint public returnHomeTimeDivisor;
  uint public experienceAmount;
  uint public baseRewardTime;
  uint public twentyFivePercentBoostExperience;
  uint public fiftyPercentRewardsBoostExperience;

  mapping(uint => uint) public questStartedTime;
  mapping(uint => uint) public questEndedTime;
  mapping(uint => uint) public rewardEarned;
  mapping(uint => uint) public timeHome;
  mapping(uint => address) public transferredRaider;

  enum Status {
    None,
    Questing,
    Returning
  }

  mapping(uint => Status) public raiderStatus;

  // ---------- HELPERS ----------

  function raiders() internal view returns(IERC721) {
    return IERC721(raidersAddress);
  }

  function questing() internal view returns(QuestingRaiders) {
    return QuestingRaiders(questingRaiders);
  }

  function boosted() internal view returns(BoostedRaiders) {
    return BoostedRaiders(boostedRaiders);
  }

  function professions() internal view returns(RaiderProfessions) {
    return RaiderProfessions(professionsAddress);
  }

  // ---------- MODIFIERS ----------

  modifier onQuest(uint _raiderId) {
    require(questing().onQuest(_raiderId), "This Raider is not on a quest!");
    _;
  }

  modifier notOnQuest(uint _raiderId) {
    require(!questing().onQuest(_raiderId), "This Raider is on a quest!");
    _;
  }

  modifier ownsRaider(uint _raiderId) {
    require(raiders().ownerOf(_raiderId) == msg.sender, "You dont own this Raider!");
    _;
  }

  modifier sentRaider(uint _raiderId) {
    require(transferredRaider[_raiderId] == msg.sender, "You dont own this Raider!");
    _;
  }

  modifier humanOnly() {
    require(msg.sender == tx.origin, "Bad robot!");
    _;
  }

  // ---------- INTERNAL FUNCTIONS ----------

  function timeQuesting(uint _raiderId) public view returns(uint) {
    return (block.timestamp).sub(questStartedTime[_raiderId]);
  }

  // ---------- MAIN FUNCTIONS ----------

  function startQuest(uint _raiderId) notOnQuest(_raiderId) ownsRaider(_raiderId) humanOnly external whenNotPaused {
    require(professions().getRaiderExp(_raiderId, professionId) >= minProfessionExp, "You're not a high enough level for this quest!");
    questStartedTime[_raiderId] = block.timestamp;
    transferredRaider[_raiderId] = msg.sender;
    raiderStatus[_raiderId] = Status.Questing;
    questing().startQuest(_raiderId, msg.sender);
  }

  function endQuest(uint _raiderId) onQuest(_raiderId) sentRaider(_raiderId) humanOnly external whenNotPaused {
    rewardEarned[_raiderId] = calcEarned(_raiderId);
    raiderStatus[_raiderId] = Status.Returning;
    questEndedTime[_raiderId] = block.timestamp;
    timeHome[_raiderId] = block.timestamp.add(calcReturnTime(_raiderId));
  }

  function getRewards(uint _raiderId) onQuest(_raiderId) sentRaider(_raiderId) humanOnly external whenNotPaused {
    require(block.timestamp > timeHome[_raiderId], "Your Raider is still coming home!");
    require(raiderStatus[_raiderId] == Status.Returning, "Your Raider is still on their quest!");

    questStartedTime[_raiderId] = 0;
    timeHome[_raiderId] = 0;

    uint rewards = rewardEarned[_raiderId];

    rewardEarned[_raiderId] = 0;
    transferredRaider[_raiderId] = address(0);

    raiderStatus[_raiderId] = Status.None;

    questing().endQuest(_raiderId, msg.sender);
    professions().addExperience(_raiderId, professionId, rewards.mul(experienceAmount));
    ERC20PresetMinterPauser(rewardAddress).mint(msg.sender, rewards);
  }

  function runHome(uint _raiderId) onQuest(_raiderId) sentRaider(_raiderId) humanOnly external whenNotPaused {
    require(raiderStatus[_raiderId] == Status.Returning, "Your Raider is still on their quest!");
    require(block.timestamp < timeHome[_raiderId], "Your Raider is already home!");

    rewardEarned[_raiderId] = 0;
    questStartedTime[_raiderId] = 0;
    timeHome[_raiderId] = 0;
    transferredRaider[_raiderId] = address(0);
    raiderStatus[_raiderId] = Status.None;

    questing().endQuest(_raiderId, msg.sender);
  }

  // ---------- MATH ----------

  function calcEarned(uint _raiderId) onQuest(_raiderId) public view virtual returns(uint) {
    uint timePassed = timeQuesting(_raiderId);
    uint rewardRate = calcRaiderRewardTime(_raiderId); 

    return timePassed.div(rewardRate);
  }

  function nextReward(uint _raiderId) onQuest(_raiderId) external view virtual returns(uint) {
    uint timePassed = timeQuesting(_raiderId);
    uint rewardRate = calcRaiderRewardTime(_raiderId); 

    return rewardRate.sub(timePassed.mod(rewardRate));
  }

  function calcRaiderRewardTime(uint _raiderId) public view virtual returns(uint) {
    uint speedBoost = boosted().raiderSpeedBoost(_raiderId);
    uint professionBoost = boosted().getProfessionBoost(_raiderId, professionId);
    uint experience = professions().getRaiderExp(_raiderId, professionId);

    uint boost = speedBoost + professionBoost;

    if (speedBoost >= 100 && professionBoost >= 100) { // also need to count for the extra hundred here
      boost = boost.sub(100);
    }

    uint levelBoost = 100;
    if (experience > twentyFivePercentBoostExperience && experience < fiftyPercentRewardsBoostExperience) {
      levelBoost = 125;
    } else if (experience > fiftyPercentRewardsBoostExperience) {
      levelBoost = 150;
    }

    uint totalBoost = levelBoost;
    if (boost > 0) {
      totalBoost = boost + totalBoost - 100; // this will be in the hundreds, like 300%. Since both start at 100, we need to sub out one of the 100s here too
    }
    return (baseRewardTime.mul(100)).div(totalBoost);
  }

  function calcReturnTime(uint _raiderId) onQuest(_raiderId) public view virtual returns (uint) {
    uint returnBoost = boosted().raiderSpeedBoost(_raiderId);
    uint returnTime = timeQuesting(_raiderId).div(returnHomeTimeDivisor);

    if (returnBoost > 0) {
      returnTime = returnTime.mul(100).div(returnBoost);
    }
    return returnTime;
  }

  // ---------- ADMIN FUNCTIONS ----------

  function changeRewardTime(uint _time) external onlyOwner {
    baseRewardTime = _time;
  }

  function changeReturnHomeTimeDivisor(uint _amount) external onlyOwner {
    returnHomeTimeDivisor = _amount;
  }

  function pause() external onlyOwner {
    _pause();
  }

  function unpause() external onlyOwner {
    _unpause();
  }

  function changeBoostedContract(address _boosted) external onlyOwner {
    boostedRaiders = _boosted;
  }

  function changeTwentyFivePercentBoostAmount(uint _amount) external onlyOwner {
    twentyFivePercentBoostExperience = _amount;
  }

  function changeFiftyPercentBoostAmount(uint _amount) external onlyOwner {
    fiftyPercentRewardsBoostExperience = _amount;
  }

  function changeExperienceAmount(uint _amount) external onlyOwner {
    experienceAmount = _amount;
  }

  function changeMinExpAmount(uint _amount) external onlyOwner {
    minProfessionExp = _amount;
  }

  // ---------- VIEW FUNCTIONS ----------

  function timeTillHome(uint _raiderId) external view virtual returns(uint) {
    return timeHome[_raiderId].sub(block.timestamp);
  }
}"
