    "contracts/QuestingRaiders.sol": {
      "content": "//SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721Receiver.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/Pausable.sol";

contract QuestingRaiders is Ownable, Pausable, IERC721Receiver {

  address immutable raidersAddress;

  mapping(uint => bool) public onQuest;
  mapping(address => bool) approvedQuests;
  mapping(address => uint[]) public ownedRaiders;
  mapping(uint => address) public raiderQuest;
  mapping(uint => address) public ownsRaider;

  constructor(address _raiders) {
    raidersAddress = _raiders;
  }

  // ---------- HELPERS ----------

  function raiders() internal view returns(IERC721) {
    return IERC721(raidersAddress);
  }

  function removeRaider(uint _raider, address _raiderOwner) internal {
    uint raiderIndex; 
    for (uint i = 0; i < ownedRaiders[_raiderOwner].length; i++) {
      if (ownedRaiders[_raiderOwner][i] == _raider) {
        raiderIndex = i;
      }
    }

    uint length = ownedRaiders[_raiderOwner].length;

    ownedRaiders[_raiderOwner][raiderIndex] = ownedRaiders[_raiderOwner][length - 1]; // change the index to the last item in the array
    delete ownedRaiders[_raiderOwner][length - 1];
    ownedRaiders[_raiderOwner].pop();
  }

  // ---------- MODIFIERS ----------

  modifier onlyQuest {
    require(approvedQuests[msg.sender]);
    _;
  }

  // ---------- PRIMARY FUNCTIONS ----------

  function startQuest(uint _raiderId, address _raiderOwner) onlyQuest external whenNotPaused {
    require(!onQuest[_raiderId], "This Raider is already on a Quest!");
    onQuest[_raiderId] = true;
    ownsRaider[_raiderId] = _raiderOwner;
    ownedRaiders[_raiderOwner].push(_raiderId);
    raiderQuest[_raiderId] = msg.sender;
    raiders().safeTransferFrom(_raiderOwner, address(this), _raiderId);
  }

  function endQuest(uint _raiderId, address _raiderOwner) onlyQuest external whenNotPaused {
    require(onQuest[_raiderId], "This Raider is not on a Quest!");
    onQuest[_raiderId] = false;
    removeRaider(_raiderId, _raiderOwner);
    raiders().safeTransferFrom(address(this), _raiderOwner, _raiderId);
  }


  // ----------- ADMIN ONLY ----------

  function addQuest(address _quest) onlyOwner external whenNotPaused {
    approvedQuests[_quest] = true;
  }

  function removeQuest(address _quest) onlyOwner external whenNotPaused {
    approvedQuests[_quest] = false;
  }

  function pause() external onlyOwner {
    _pause();
  }

  function unpause() external onlyOwner {
    _unpause();
  }

  function emergencyRemoveRaider(uint _raiderId) external onlyOwner {
    raiders().safeTransferFrom(address(this), msg.sender, _raiderId);
  }

  function emergencyReturnRaider(uint _raiderId) external onlyOwner {
    address raiderOwner = ownsRaider[_raiderId];
    raiders().safeTransferFrom(address(this), raiderOwner, _raiderId);
  }

  function onERC721Received(address, address, uint256, bytes memory) public virtual override returns (bytes4) {
    return this.onERC721Received.selector;
  }

  // ----------- VIEW FUNCTIONS ----------

  function getOwnedRaiders(address _address) external view returns(uint[] memory) {
    return ownedRaiders[_address];
  }
}"
    },
    "contracts/BoostedRaiders.sol": {
      "content": "//SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/Pausable.sol";

contract BoostedRaiders is Ownable, Pausable {

  using SafeMath for uint;

  mapping(address => bool) approvedBoosts;
  mapping(uint => uint) public raiderSpeedBoost;
  mapping(uint => mapping(uint => uint)) public raiderProfessionBoost;

  // Boosts are in 100s to get around decimal issues. So if you want to give a 20% boost, you add 20. Then on the contract side, you have to adjust the time by 20% 
  // Professions are based on the profession keys which you can see internally. Make sure they line up properly! 

  modifier onlyBoost {
    require(approvedBoosts[msg.sender] == true);
    _;
  }

  function addSpeedBoost(uint _raiderId, uint _amount) onlyBoost public {
    raiderSpeedBoost[_raiderId] = raiderSpeedBoost[_raiderId].add(_amount);
  }

  function subSpeedBoost(uint _raiderId, uint _amount) onlyBoost public {
    raiderSpeedBoost[_raiderId] = raiderSpeedBoost[_raiderId].sub(_amount);
  }

  function addProfessionBoost(uint _raiderId, uint _profession, uint _amount) onlyBoost public {
    raiderProfessionBoost[_raiderId][_profession] = raiderProfessionBoost[_raiderId][_profession].add(_amount);
  }

  function subProfessionBoost(uint _raiderId, uint _profession, uint _amount) onlyBoost public {
    raiderProfessionBoost[_raiderId][_profession] = raiderProfessionBoost[_raiderId][_profession].sub(_amount);
  }

  // ----------- VIEWS ----------

  function getProfessionBoost(uint _raiderId, uint _profession) external view returns(uint) {
    return raiderProfessionBoost[_raiderId][_profession];
  }

  // ----------- ADMIN ONLY ----------

  function addBoost(address _Boost) onlyOwner public {
    approvedBoosts[_Boost] = true;
  }

  function removeBoost(address _Boost) onlyOwner public {
    approvedBoosts[_Boost] = false;
  }

  function pause() public onlyOwner {
    _pause();
  }

  function unpause() public onlyOwner {
    _unpause();
  }

  function adminSpeedBoostChange(uint _raiderId, uint _value) onlyOwner public {
    raiderSpeedBoost[_raiderId] = _value;
  }

  function adminProfessionBoostChange(uint _raiderId, uint _profession, uint _value) onlyOwner public {
    raiderProfessionBoost[_raiderId][_profession] = _value;
  }
}"
    },
    "contracts/RaiderProfessions.sol": {
      "content": "//SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/Pausable.sol";

contract RaiderProfessions is Ownable, Pausable {

  using SafeMath for uint;

  mapping(address => bool) approvedQuests;
  mapping(uint => string) public professions;
  mapping(uint => mapping(uint => uint)) raiderProfessionExp; // raider -> profession -> experience

  modifier onlyQuest {
    require(approvedQuests[msg.sender] == true);
    _;
  }

  function addExperience(uint raider, uint profession, uint experience) onlyQuest public whenNotPaused {
    raiderProfessionExp[raider][profession] = (raiderProfessionExp[raider][profession]).add(experience);
  }

  function subExperience(uint raider, uint profession, uint experience) onlyQuest public whenNotPaused {
    raiderProfessionExp[raider][profession] = (raiderProfessionExp[raider][profession]).sub(experience);
  }

  function getRaiderExp(uint _raider, uint _profession) public view returns(uint) {
    return raiderProfessionExp[_raider][_profession];
  }


  // ----------- ADMIN ONLY ----------

  function addQuest(address _quest) onlyOwner public {
    approvedQuests[_quest] = true;
  }

  function removeQuest(address _quest) onlyOwner public {
    approvedQuests[_quest] = false;
  }

  function manualExpUpdate(uint raider, uint profession, uint experience) onlyOwner public {
    raiderProfessionExp[raider][profession] = experience;
  }

  function pause() public onlyOwner {
    _pause();
  }

  function unpause() public onlyOwner {
    _unpause();
  }
}"
    },
