    "contracts/ERC1155Asset.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED

pragma solidity ^0.8.0;

import "./tokens/ERC1155/ERC1155Base/ERC1155Base.sol";
import "./ERC20UpgradeBooster.sol";
import "./structs/UpgradeAsset.sol";
import "./structs/UpdateRequest.sol";

import "@chainlink/contracts/src/v0.8/VRFConsumerBaseV2.sol";
import "@chainlink/contracts/src/v0.8/interfaces/VRFCoordinatorV2Interface.sol";
import "@openzeppelin/contracts/token/ERC1155/IERC1155Receiver.sol";
import {VRFConsumer} from "./main/VRFConsumer.sol";

contract ERC1155Asset is ERC1155Base, IERC1155Receiver {
    bytes32 public constant VRF_KEY_HASH =
        0x6e099d640cde6de9d40ac749b4b594126b0169747122711109c9985d47751f93;
    uint32 public constant VRF_MAX_GAS_LIMIT_FOR_UPGRADE = 1_000_000;
    // tokenId => name
    mapping(uint256 => string) public tokenNames;

    mapping(uint256 => uint256) public tokenTiers;

    ERC20UpgradeBooster public immutable boosters;
    VRFConsumer public immutable vrfConsumer;

    // assetId -> UpgradeAsset
    mapping(uint256 => UpgradeAsset) public upgradeAssets;

    event AssetUpgraded(
        uint256 indexed tokenId,
        bool indexed success,
        uint256 indexed requestId
    );
    event AddUpgradeAsset(uint256 indexed upgradeAssetId);
    event RequestCreated(uint256 indexed requestId);

    constructor(
        string memory uri_,
        IERC20 poles_,
        ERC20UpgradeBooster _boosters,
        VRFConsumer _vrfConsumer
    ) ERC1155Base(uri_, poles_) {
        boosters = _boosters;

        vrfConsumer = _vrfConsumer;
    }

    function addAsset(
        bytes memory data,
        uint256 price,
        string memory name
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        uint256 tokenId = ++currentTokenId;

        datas[tokenId] = data;
        prices[tokenId] = price;
        tokenNames[tokenId] = name;

        emit AddToken(tokenId, data, price);
    }

    function upgrade(
        uint256 tokenId,
        uint256 boostersAmount
    ) external returns (uint256) {
        UpgradeAsset memory upgradeAsset = upgradeAssets[tokenId];

        require(upgradeAsset.nextGradeAssetId > 0, "Cannot upgrade");

        _safeTransferFrom(
            msg.sender,
            address(this),
            tokenId,
            upgradeAsset.amountOfAssets,
            ""
        );

        poles.transferFrom(
            msg.sender,
            address(this),
            upgradeAsset.amountOfPoleses
        );

        uint256 chance = upgradeAsset.baseChance;

        if (boostersAmount != 0) {
            boosters.burnFrom(msg.sender, boostersAmount);
            chance += boostersAmount * upgradeAsset.upgradeBoosterChance;
        }

        uint256 requestId = requestRandomWordsForUpgrade(
            tokenId,
            chance,
            msg.sender
        );

        emit RequestCreated(requestId);

        return requestId;
    }

    function addUpgradeAsset(
        UpgradeAsset calldata upgradeAsset,
        uint256 tokenId,
        uint256 tier
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        upgradeAssets[tokenId] = upgradeAsset;
        tokenTiers[tokenId] = tier;
        if (tier == 1) {
            tokenTiers[upgradeAsset.nextGradeAssetId] = 2;
        }

        emit AddUpgradeAsset(tokenId);
    }

    function changeName(
        uint256 tokenId,
        string memory name
    ) public onlyRole(DEFAULT_ADMIN_ROLE) {
        tokenNames[tokenId] = name;
    }

    // solhint-disable private-vars-leading-underscore
    function requestRandomWordsForUpgrade(
        uint256 tokenId,
        uint256 randomChance,
        address tokenOwner
    ) internal returns (uint256) {
        return
            vrfConsumer.requestRandomWords(
                1,
                VRF_KEY_HASH,
                VRF_MAX_GAS_LIMIT_FOR_UPGRADE,
                address(this),
                this.onUpgradeFulfillRandomWords.selector,
                abi.encode(tokenId, randomChance, tokenOwner)
            );
    }

    // solhint-disable-next-line private-vars-leading-underscore
    function onUpgradeFulfillRandomWords(
        uint256 requestId,
        uint256[] memory _randomWords,
        bytes memory data
    ) external {
        require(msg.sender == address(vrfConsumer), "Invalid caller");
        (uint256 tokenId, uint256 randomChance, address tokenOwner) = abi
            .decode(data, (uint256, uint256, address));

        UpgradeAsset memory upgradeAsset = upgradeAssets[tokenId];
        uint256 randomNumber = ((_randomWords[0] % 100) + 1);

        bool success;

        if (randomChance >= randomNumber) {
            _burn(address(this), tokenId, upgradeAsset.amountOfAssets);
            _mint(tokenOwner, upgradeAsset.nextGradeAssetId, 1, "");
            success = true;
        } else {
            _safeTransferFrom(
                address(this),
                tokenOwner,
                tokenId,
                upgradeAsset.amountOfAssets,
                ""
            );
            success = false;
        }

        emit AssetUpgraded(tokenId, success, requestId);
    }

    function onERC1155Received(
        address,
        address,
        uint256,
        uint256,
        bytes calldata
    ) public pure returns (bytes4) {
        return this.onERC1155Received.selector;
    }

    function onERC1155BatchReceived(
        address,
        address,
        uint256[] calldata,
        uint256[] calldata,
        bytes calldata
    ) public pure returns (bytes4) {
        return this.onERC1155BatchReceived.selector;
    }
}
"
    },
    "contracts/ERC1155Consumable.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED

pragma solidity ^0.8.0;

import {ERC1155Base, IERC20} from "./tokens/ERC1155/ERC1155Base/ERC1155Base.sol";

contract ERC1155Consumable is ERC1155Base {
    constructor(string memory uri_, IERC20 poles_) ERC1155Base(uri_, poles_) {}
}
"
