    "contracts/ERC20UpgradeBooster.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;

import {ERC20Burnable} from "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";

import {ERC20Buyable, IERC20, ERC20AccessControl, ERC20} from "./tokens/ERC20/ERC20Buyable/ERC20Buyable.sol";

contract ERC20UpgradeBooster is ERC20Burnable, ERC20Buyable {
    constructor(
        IERC20 poles_,
        uint256 price_
    ) ERC20Buyable("Upgrade Booster", "UB", poles_, price_) {}

    function transferFrom(
        address from,
        address to,
        uint256 amount
    ) public virtual override(ERC20AccessControl, ERC20) returns (bool) {
        return ERC20AccessControl.transferFrom(from, to, amount);
    }

    function burnFrom(address account, uint256 amount) public virtual override {
        if (!hasRole(ERC20_CONTROLLER_ROLE, _msgSender())) {
            _spendAllowance(account, _msgSender(), amount);
        }

        _burn(account, amount);
    }

    function decimals() public view virtual override returns (uint8) {
        return 0;
    }
}
"
    },
    "contracts/ERC721Characters.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;

import {ERC721, IERC721} from "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {IERC1155Receiver} from "@openzeppelin/contracts/token/ERC1155/IERC1155Receiver.sol";
import {ERC1155Receiver} from "@openzeppelin/contracts/token/ERC1155/utils/ERC1155Receiver.sol";

import {ERC1155Asset} from "./ERC1155Asset.sol";
import {ERC1155Consumable} from "./ERC1155Consumable.sol";

import {DigiToadsAccessControl, AccessControl} from "./access/DigiToadsAccessControl.sol";

import {Character} from "./structs/Character.sol";
import {Stats} from "./structs/Stats.sol";
import {Asset} from "./structs/Asset.sol";

import {Class} from "./enums/Class.sol";
import {Slot} from "./enums/Slot.sol";
import {Consumable} from "./structs/Consumable.sol";

struct CharactersParameters {
    string name;
    string symbol;
    Stats[] stats;
    Stats[] lvlUpStats;
    uint256[] prices;
    uint256 expandRestrictionPrice;
    uint256 energyToAddPremium;
    uint256 polesAmountPremium;
    uint256 changeNamePrice;
    IERC20 poles;
    ERC1155Asset assets;
    ERC1155Consumable consumables;
    uint256 hpRegeneration;
    uint256 mpRegeneration;
    uint256 epRegeneration;
    uint256 epPerTime;
}

struct Lock {
    bool locked;
    uint256 lastLocked;
}

contract ERC721Characters is ERC721, DigiToadsAccessControl, ERC1155Receiver {
    IERC20 public immutable poles;
    ERC1155Asset public immutable assets;
    ERC1155Consumable public immutable consumables;

    uint256 public constant TIME_BETWEEN_LOCK = 60 * 6;

    /// @dev tokenId => price
    mapping(Class => uint256) public prices;
    /// @dev tokenId => character
    mapping(uint256 => Character) public characters;
    // Todo change 2 mappings to 1
    /// @dev address => charactersCount
    mapping(address => uint256) public userCharactersCount;
    /// @dev address => tokenCount
    mapping(address => uint256) public userRestrictions;
    /// @dev class => baseStats
    mapping(Class => Stats) public baseStats;
    /// @dev class => lvlUpStats
    mapping(Class => Stats) public lvlUpStats;
    /// @dev tokenId => timestamp of changing stats
    mapping(uint256 => uint256) public charactersStatsChanges;
    /// @dev tokenId => timestamp of last energy update
    mapping(uint256 => uint256) public lastEnergyUpdated;
    /// @dev tokenId => Lock struct
    mapping(uint256 => Lock) public locked;
    // user => tokenId
    mapping(address => uint256) public usedCharacters;
    // user => lastChange
    mapping(address => uint256) public lastCharacterChange;
    /// @dev tokenId => slot => assetId
    mapping(uint256 => mapping(Slot => uint256)) public slots;

    uint256 public currentTokenId;

    uint256 private immutable _expandRestrictionPrice;
    uint256 private immutable _hpRegeneration;
    uint256 private immutable _mpRegeneration;
    uint256 private immutable _epRegeneration;
    uint256 private immutable _epPerTime;

    uint256 private _energyToAddPremium;
    uint256 private _polesAmountPremium;
    uint256 private _changeNamePrice;

    event CharacterBought(uint256 indexed tokenId);
    event RestrictionExpanded(uint256 indexed amount);
    event ExperienceAdded(uint256 indexed tokenId);
    event LevelUpdated(uint256 indexed tokenId, uint256 newLevel);
    event StatsChanged(
        uint256 indexed tokenId,
        uint256 newHealth,
        uint256 newMana,
        uint256 energy
    );
    event AssetApplied(uint256 indexed tokenId, uint256 indexed assetId);
    event AssetWithdrawn(uint256 indexed tokenId, uint256 indexed assetId);
    event ConsumableApplied(
        uint256 indexed tokenId,
        uint256 indexed consumableId
    );
    event WithdrawPoles(address indexed to, uint256 amount);

    modifier onlyTokenOwner(uint256 tokenId) {
        require(ownerOf(tokenId) == msg.sender, "Not an owner");
        _;
    }

    constructor(
        CharactersParameters memory parameters
    ) ERC721(parameters.name, parameters.symbol) {
        _initiateStats(parameters);

        _expandRestrictionPrice = parameters.expandRestrictionPrice;
        poles = parameters.poles;
        assets = parameters.assets;
        consumables = parameters.consumables;
        _hpRegeneration = parameters.hpRegeneration;
        _mpRegeneration = parameters.mpRegeneration;
        _epRegeneration = parameters.epRegeneration;
        _epPerTime = parameters.epPerTime;
        _energyToAddPremium = parameters.energyToAddPremium;
        _polesAmountPremium = parameters.polesAmountPremium;
        _changeNamePrice = parameters.changeNamePrice;

        grantRole(CHARACTER_CONTROLLER_ROLE, msg.sender);
    }

    function changePrice(
        Class heroClass,
        uint256 newPrice
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        prices[heroClass] = newPrice;
    }

    function changeName(
        uint256 tokenId,
        string memory name
    ) external onlyTokenOwner(tokenId) {
        _transferPolesFromSender(_changeNamePrice);
        characters[tokenId].name = name;
    }

    function buyCharacter(
        Class heroClass,
        string memory name
    ) external returns (uint256) {
        _transferPolesFromSender(prices[heroClass]);

        return _mint(heroClass, msg.sender, name);
    }

    function burn(
        uint256 tokenId
    ) external onlyRole(CHARACTER_CONTROLLER_ROLE) {
        _burn(tokenId);
    }

    function mint(
        Class heroClass,
        address to,
        string memory name
    ) external onlyRole(CHARACTER_CONTROLLER_ROLE) returns (uint256) {
        return _mint(heroClass, to, name);
    }

    function upgradeLvl(uint256 tokenId, uint256 lvlToAdd) external {
        if (!hasRole(DEFAULT_ADMIN_ROLE, msg.sender)) {
            require(ownerOf(tokenId) == msg.sender, "Not an owner");
        }

        Character memory character = characters[tokenId];

        lvlToAdd = lvlToAdd > 100 - character.level
            ? 100 - character.level
            : lvlToAdd;

        uint256 _lvlToExp = lvlToExp(character.level + lvlToAdd);
        require(
            character.currentExperience >= _lvlToExp,
            "Character cannot be updated"
        );

        Stats memory lvlUpStat = lvlUpStats[character.heroType];

        character.stats.basicStats.healthPoints +=
            lvlUpStat.basicStats.healthPoints *
            lvlToAdd;
        character.stats.basicStats.manaPoints +=
            lvlUpStat.basicStats.manaPoints *
            lvlToAdd;
        character.stats.attackPoints += lvlUpStat.attackPoints * lvlToAdd;
        character.stats.defensePoints += lvlUpStat.defensePoints * lvlToAdd;
        character.changeableStats.healthPoints +=
            lvlUpStat.basicStats.healthPoints *
            lvlToAdd;
        character.changeableStats.manaPoints +=
            lvlUpStat.basicStats.manaPoints *
            lvlToAdd;
        character.level += lvlToAdd;

        characters[tokenId] = character;

        emit LevelUpdated(tokenId, character.level);
    }

    function useCharacter(uint256 tokenId) public {
        require(
            block.timestamp - lastCharacterChange[msg.sender] >= 60 * 60 * 24,
            "Change period didn't end"
        );
        require(ownerOf(tokenId) == msg.sender, "Not an owner");
        require(!locked[usedCharacters[msg.sender]].locked, "Token is locked");

        usedCharacters[msg.sender] = tokenId;
        lastCharacterChange[msg.sender] = block.timestamp;
    }

    function getCurrentCharacterStats(
        address user
    ) public view returns (Character memory, uint256) {
        uint256 tokenId = usedCharacters[user];
        return (characters[tokenId], tokenId);
    }

    function addExpToCharacter(
        uint256 tokenId,
        uint256 expToAdd
    ) external onlyRole(CHARACTER_CONTROLLER_ROLE) {
        characters[tokenId].currentExperience += expToAdd;

        emit ExperienceAdded(tokenId);
    }

    function wearAsset(
        uint256 tokenId,
        Slot slot,
        uint256 assetId
    ) public onlyTokenOwner(tokenId) {
        if (slots[tokenId][slot] != 0) {
            _takeOffAsset(tokenId, slot, slots[tokenId][slot]);
        }

        Character memory character = characters[tokenId];
        Asset memory asset = abi.decode(assets.datas(assetId), (Asset));

        require(character.heroType == asset.assetType, "Classes are not equal");
        require(slot == asset.slot, "Slot is not compatible");

        assets.safeTransferFrom(msg.sender, address(this), assetId, 1, "");

        character.stats.basicStats.manaPoints += asset.mana;
        character.stats.basicStats.healthPoints += asset.health;
        character.stats.defensePoints += asset.defense;
        character.stats.attackPoints += asset.attack;
        character.changeableStats.manaPoints += asset.mana;
        character.changeableStats.healthPoints += asset.health;

        characters[tokenId] = character;
        slots[tokenId][slot] = assetId;

        emit AssetApplied(tokenId, assetId);
    }

    function wearAssets(
        uint256[] calldata _tokenIds,
        Slot[] calldata _slots,
        uint256[] calldata _assetIds
    ) external {
        require(
            _tokenIds.length == _slots.length &&
                _slots.length == _assetIds.length,
            "Mismatch lengths"
        );

        for (uint i; i < _slots.length; i++) {
            uint256 tokenId = _tokenIds[i];
            Slot slot = _slots[i];
            uint256 assetId = _assetIds[i];
            wearAsset(tokenId, slot, assetId);
        }
    }

    function takeOffAsset(
        uint256 tokenId,
        Slot slot,
        uint256 assetId
    ) public onlyTokenOwner(tokenId) {
        require(slots[tokenId][slot] != 0, "Slot is already taken off");

        _takeOffAsset(tokenId, slot, assetId);
    }

    function takeOffAssets(
        uint256[] calldata _tokenIds,
        Slot[] calldata _slots,
        uint256[] calldata _assetIds
    ) external {
        require(
            _tokenIds.length == _slots.length &&
                _slots.length == _assetIds.length,
            "Mismatch lengths"
        );

        for (uint i; i < _slots.length; i++) {
            uint256 tokenId = _tokenIds[i];
            Slot slot = _slots[i];
            uint256 assetId = _assetIds[i];
            takeOffAsset(tokenId, slot, assetId);
        }
    }

    function applyConsumable(uint256 tokenId, uint256 consumableId) external {
        Character memory character = characters[tokenId];
        Consumable memory consumable = abi.decode(
            consumables.datas(consumableId),
            (Consumable)
        );

        consumables.safeTransferFrom(
            msg.sender,
            address(this),
            consumableId,
            1,
            ""
        );

        if (consumable.energy > 0) {
            character.energy = character.energy + consumable.energy <=
                character.maxEnergy
                ? character.energy + consumable.energy
                : character.maxEnergy;
        } else {
            if (consumable.health) {
                character.changeableStats.healthPoints = character
                    .stats
                    .basicStats
                    .healthPoints;
            }
            if (consumable.mana) {
                character.changeableStats.manaPoints = character
                    .stats
                    .basicStats
                    .manaPoints;
            }
        }

        characters[tokenId] = character;

        emit ConsumableApplied(tokenId, consumableId);
    }

    function changeCharacterStats(
        uint256 tokenId,
        uint256 health,
        uint256 mana,
        uint256 energy
    ) external onlyRole(CHARACTER_CONTROLLER_ROLE) {
        Character memory character = characters[tokenId];

        character.changeableStats.manaPoints = mana;
        character.changeableStats.healthPoints = health;
        character.energy = energy;
        charactersStatsChanges[tokenId] = block.timestamp;

        if (
            (character.energy == character.maxEnergy &&
                lastEnergyUpdated[tokenId] > 0) ||
            lastEnergyUpdated[tokenId] == 0
        ) {
            lastEnergyUpdated[tokenId] = block.timestamp;
        }

        characters[tokenId] = character;

        emit StatsChanged(
            tokenId,
            character.changeableStats.healthPoints,
            character.changeableStats.manaPoints,
            character.energy
        );
    }

    function buyPremium(uint256 tokenId) external onlyTokenOwner(tokenId) {
        require(
            characters[tokenId].maxEnergy != _energyToAddPremium + 5,
            "User has already bought premium"
        );

        _transferPolesFromSender(_polesAmountPremium);
        characters[tokenId].maxEnergy += _energyToAddPremium;
    }

    function recoverStats(uint256 tokenId) external {
        Character memory character = characters[tokenId];

        uint256 _statsChangedAt = charactersStatsChanges[tokenId];
        uint256 _epChangedAt = lastEnergyUpdated[tokenId];

        uint256 mpRegeneration = _mpRegeneration *
            (block.timestamp - _statsChangedAt);

        uint256 hpRegeneration = _hpRegeneration *
            (block.timestamp - _statsChangedAt);

        uint256 epRegeneration = _epRegeneration *
            ((block.timestamp - _epChangedAt) / _epPerTime);

        uint256 cMP = character.changeableStats.manaPoints;
        uint256 cHP = character.changeableStats.healthPoints;

        // if regenerated + current mana >= max
        // then max
        // else regenerated + current mana
        character.changeableStats.manaPoints = cMP + mpRegeneration >=
            character.stats.basicStats.manaPoints
            ? character.stats.basicStats.manaPoints
            : cMP + mpRegeneration;

        // if regenerated + current hp >= max
        // then max
        // else regenerated + current hp
        character.changeableStats.healthPoints = cHP + hpRegeneration >=
            character.stats.basicStats.healthPoints
            ? character.stats.basicStats.healthPoints
            : cHP + hpRegeneration;

        // if regenerated + current energy >= max
        // then max
        // else regenerated + current energy
        character.energy = character.energy + epRegeneration >=
            character.maxEnergy
            ? character.maxEnergy
            : character.energy + epRegeneration;

        characters[tokenId] = character;
        charactersStatsChanges[tokenId] = block.timestamp;

        if (character.energy == character.maxEnergy) {
            lastEnergyUpdated[tokenId] = block.timestamp;
        } else {
            lastEnergyUpdated[tokenId] =
                block.timestamp -
                ((block.timestamp - _epChangedAt) % _epPerTime);
        }
    }

    function expandUserRestriction(uint256 amount) external {
        _transferPolesFromSender(amount * _expandRestrictionPrice);

        userRestrictions[msg.sender] += amount;

        emit RestrictionExpanded(amount);
    }

    function withdrawPoles(
        address to,
        uint256 amount
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        poles.transfer(to, amount);

        emit WithdrawPoles(to, amount);
    }

    function changeLock(uint256 tokenId, bool lock) external {
        bool isController = hasRole(MAIN_CONTROLLER_ROLE, msg.sender);

        require(locked[tokenId].locked != lock, "Invalid lock");
        require(ownerOf(tokenId) == msg.sender || isController, "Not allowed");

        if (!isController) {
            require(
                block.timestamp - locked[tokenId].lastLocked >=
                    TIME_BETWEEN_LOCK,
                "Lock time hasn't passed"
            );
        }

        locked[tokenId].locked = lock;
        locked[tokenId].lastLocked = block.timestamp;
    }

    function onERC1155Received(
        address,
        address,
        uint256,
        uint256,
        bytes calldata
    ) external pure returns (bytes4) {
        return this.onERC1155Received.selector;
    }

    function onERC1155BatchReceived(
        address,
        address,
        uint256[] calldata,
        uint256[] calldata,
        bytes calldata
    ) external pure returns (bytes4) {
        return this.onERC1155BatchReceived.selector;
    }

    function lvlToExp(uint256 lvl) public pure returns (uint256) {
        if (lvl > 4) {
            return 300 * (lvl - 4);
        }
        if (lvl > 3) {
            return 300;
        }
        if (lvl > 2) {
            return 150;
        }
        if (lvl > 1) {
            return 50;
        }

        return 10;
    }

    function supportsInterface(
        bytes4 interfaceId
    )
        public
        view
        virtual
        override(ERC721, ERC1155Receiver, AccessControl)
        returns (bool)
    {
        return
            interfaceId == type(IERC721).interfaceId ||
            ERC721.supportsInterface(interfaceId) ||
            AccessControl.supportsInterface(interfaceId) ||
            ERC1155Receiver.supportsInterface(interfaceId);
    }

    function getExpandingRestrictionPrice() external view returns (uint256) {
        return _expandRestrictionPrice;
    }

    function setPolesAmountForPremium(
        uint256 newValue
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        _polesAmountPremium = newValue;
    }

    function getPolesAmountForPremium() external view returns (uint256) {
        return _polesAmountPremium;
    }

    function setEnergyToAddPremium(
        uint256 newValue
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        _energyToAddPremium = newValue;
    }

    function getEnergyToAddPremium() external view returns (uint256) {
        return _energyToAddPremium;
    }

    function setChangeNamePrice(
        uint256 newValue
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        _changeNamePrice = newValue;
    }

    function getChangeNamePrice() external view returns (uint256) {
        return _changeNamePrice;
    }

    function _transferPolesFromSender(uint256 amount) internal {
        if (amount == 0) return;
        poles.transferFrom(msg.sender, address(this), amount);
    }

    function _afterTokenTransfer(
        address from,
        address to,
        uint256,
        uint256 batchSize
    ) internal virtual override {
        userCharactersCount[to] += batchSize;
        if (address(0) != from) {
            userCharactersCount[from] -= batchSize;
        }

        if (!hasRole(BOT_CONTROLLER_ROLE, msg.sender)) {
            require(
                userCharactersCount[to] <= (userRestrictions[to] + 1),
                "ERC721Characters: limit reached"
            );
        }
    }

    function _initiateStats(CharactersParameters memory parameters) internal {
        for (uint256 i = 0; i < parameters.stats.length; i++) {
            require(parameters.prices[i] != 0, "Invalid price");

            baseStats[Class(i)] = parameters.stats[i];
            prices[Class(i)] = parameters.prices[i];
            lvlUpStats[Class(i)] = parameters.lvlUpStats[i];
        }
    }

    function _beforeTokenTransfer(
        address,
        address,
        uint256 firstTokenId,
        uint256
    ) internal virtual override {
        require(!locked[firstTokenId].locked, "Token is locked");
    }

    function _mint(
        Class heroClass,
        address to,
        string memory name
    ) private returns (uint256) {
        uint256 tokenId = ++currentTokenId;
        characters[tokenId] = Character(
            heroClass,
            baseStats[heroClass].basicStats,
            baseStats[heroClass],
            0,
            0,
            5,
            5,
            name
        );
        _mint(to, tokenId);

        if (usedCharacters[to] == 0) {
            usedCharacters[to] = tokenId;
        }

        emit CharacterBought(tokenId);

        return tokenId;
    }

    function _takeOffAsset(
        uint256 tokenId,
        Slot slot,
        uint256 assetId
    ) private onlyTokenOwner(tokenId) {
        Character memory character = characters[tokenId];
        Asset memory asset = abi.decode(assets.datas(assetId), (Asset));

        require(character.heroType == asset.assetType, "Classes are not equal");
        require(slot == asset.slot, "Slot is not compatible");

        assets.safeTransferFrom(address(this), msg.sender, assetId, 1, "");

        character.stats.basicStats.manaPoints -= asset.mana;
        character.stats.basicStats.healthPoints -= asset.health;
        character.stats.defensePoints -= asset.defense;
        character.stats.attackPoints -= asset.attack;

        character.changeableStats.manaPoints = asset.mana >
            character.changeableStats.manaPoints
            ? 0
            : character.changeableStats.manaPoints - asset.mana;

        character.changeableStats.healthPoints = asset.health >
            character.changeableStats.healthPoints
            ? 0
            : character.changeableStats.healthPoints - asset.health;

        characters[tokenId] = character;
        delete slots[tokenId][slot];

        emit AssetWithdrawn(tokenId, assetId);
    }
}
"
    },
    "contracts/access/DigiToadsAccessControl.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;

import {AccessControl} from "@openzeppelin/contracts/access/AccessControl.sol";

abstract contract DigiToadsAccessControl is AccessControl {
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    bytes32 public constant BURNER_ROLE = keccak256("BURNER_ROLE");
    bytes32 public constant CHARACTER_CONTROLLER_ROLE =
        keccak256("CHARACTER_CONTROLLER_ROLE");
    bytes32 public constant ERC1155_CONTROLLER_ROLE =
        keccak256("ERC1155_CONTROLLER_ROLE");
    bytes32 public constant ERC20_CONTROLLER_ROLE =
        keccak256("ERC20_CONTROLLER_ROLE");
    bytes32 public constant BATTLE_CONTROLLER_ROLE =
        keccak256("BATTLE_CONTROLLER_ROLE");
    bytes32 public constant MMPOOL_CONTROLLER_ROLE =
        keccak256("MMPOOL_CONTROLLER_ROLE");
    bytes32 public constant MAIN_CONTROLLER_ROLE =
        keccak256("MAIN_CONTROLLER_ROLE");
    bytes32 public constant BOT_CONTROLLER_ROLE =
        keccak256("BOT_CONTROLLER_ROLE");
    bytes32 public constant VRF_CONSUMER_ROLE = keccak256("VRF_CONSUMER_ROLE");
    bytes32 public constant SEASON_CONTROLLER_ROLE =
        keccak256("SEASON_CONTROLLER_ROLE");

    constructor() {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
    }
}
"
