    "contracts/main/VRFConsumer.sol": {
      "content": "// SPDX-License-Identifier: MIT
// An example of a consumer contract that relies on a subscription for funding.
pragma solidity ^0.8.7;

import "@chainlink/contracts/src/v0.8/interfaces/VRFCoordinatorV2Interface.sol";
import "@chainlink/contracts/src/v0.8/VRFConsumerBaseV2.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

import {DigiToadsAccessControl} from "../access/DigiToadsAccessControl.sol";

contract VRFConsumer is VRFConsumerBaseV2, DigiToadsAccessControl {
    struct RequestStatus {
        bool fulfilled; // whether the request has been successfully fulfilled
        bool exists; // whether a requestId exists
        address callbackAddress;
        bytes4 callbackFuncSelector;
        bytes callbackData;
    }

    uint16 public constant REQUEST_CONFIRMATIONS = 3;

    mapping(uint256 => RequestStatus) public vrfRequests;

    VRFCoordinatorV2Interface private immutable _coordinator;

    uint64 private _subscriptionId;

    uint256 public test;

    event RequestSent(uint256 requestId, uint32 numWords);
    event RequestFulfilled(uint256 requestId);

    constructor(
        address vrfCoordinator,
        uint64 subscriptionId
    ) VRFConsumerBaseV2(vrfCoordinator) {
        _coordinator = VRFCoordinatorV2Interface(vrfCoordinator);
        _subscriptionId = subscriptionId;
    }

    // Assumes the subscription is funded sufficiently.
    function requestRandomWords(
        uint32 numWords,
        bytes32 keyHash,
        uint32 callbackGasLimit,
        address callbackAddress,
        bytes4 callbackFuncSelector,
        bytes memory callbackData
    ) external onlyRole(VRF_CONSUMER_ROLE) returns (uint256 requestId) {
        // Will revert if subscription is not set and funded.
        requestId = _coordinator.requestRandomWords(
            keyHash,
            _subscriptionId,
            REQUEST_CONFIRMATIONS,
            callbackGasLimit,
            numWords
        );

        vrfRequests[requestId] = RequestStatus({
            callbackData: callbackData,
            exists: true,
            callbackAddress: callbackAddress,
            callbackFuncSelector: callbackFuncSelector,
            fulfilled: false
        });

        return requestId;
    }

    // solhint-disable-next-line private-vars-leading-underscore
    function fulfillRandomWords(
        uint256 _requestId,
        uint256[] memory _randomWords
    ) internal override {
        RequestStatus memory status = vrfRequests[_requestId];
        require(status.exists, "VRF: request not found");
        require(!status.fulfilled, "VRF: already filled");

        status.fulfilled = true;

        (bool success, ) = status.callbackAddress.call(
            abi.encodeWithSelector(
                status.callbackFuncSelector,
                _requestId,
                _randomWords,
                status.callbackData
            )
        );

        assert(success);

        vrfRequests[_requestId] = status;
        emit RequestFulfilled(_requestId);
    }

    function getRequestStatus(
        uint256 _requestId
    ) external view returns (RequestStatus memory) {
        require(vrfRequests[_requestId].exists, "request not found");
        return vrfRequests[_requestId];
    }

    function changeSubId(uint64 newSubId) public onlyRole(DEFAULT_ADMIN_ROLE) {
        _subscriptionId = newSubId;
    }
}
"
    },
