    "contracts/tokens/ERC1155/ERC1155Base/ERC1155Base.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED

pragma solidity ^0.8.0;

import {ERC1155Burnable, ERC1155} from "@openzeppelin/contracts/token/ERC1155/extensions/ERC1155Burnable.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";

import {DigiToadsAccessControl, AccessControl} from "../../../access/DigiToadsAccessControl.sol";

import {IERC1155Base} from "./IERC1155Base.sol";

contract ERC1155Base is ERC1155Burnable, IERC1155Base, DigiToadsAccessControl {
    IERC20 public poles;

    uint256 public currentTokenId;

    mapping(uint256 => bytes) public datas;
    mapping(uint256 => uint256) public prices;

    constructor(string memory uri_, IERC20 poles_) ERC1155(uri_) {
        poles = poles_;
    }

    function safeTransferFrom(
        address from,
        address to,
        uint256 id,
        uint256 amount,
        bytes memory data
    ) public virtual override {
        if (!hasRole(ERC1155_CONTROLLER_ROLE, _msgSender())) {
            require(
                from == _msgSender() || isApprovedForAll(from, _msgSender()),
                "ERC1155: caller is not token owner or approved"
            );
        }

        _safeTransferFrom(from, to, id, amount, data);
    }

    /**
     * @dev See {IERC1155-safeBatchTransferFrom}.
     */
    function safeBatchTransferFrom(
        address from,
        address to,
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data
    ) public virtual override {
        if (!hasRole(ERC1155_CONTROLLER_ROLE, _msgSender())) {
            require(
                from == _msgSender() || isApprovedForAll(from, _msgSender()),
                "ERC1155: caller is not token owner or approved"
            );
        }
        _safeBatchTransferFrom(from, to, ids, amounts, data);
    }

    function addToken(
        bytes memory data,
        uint256 price
    ) external virtual override onlyRole(DEFAULT_ADMIN_ROLE) {
        uint256 tokenId = ++currentTokenId;

        datas[tokenId] = data;
        prices[tokenId] = price;

        emit AddToken(tokenId, data, price);
    }

    function calculateTotalPrice(
        uint256 tokenId,
        uint256 amount
    ) public view virtual override returns (uint256) {
        uint256 price = prices[tokenId];

        require(price > 0, "ERC1155Base: Token not for sale");

        return price * amount;
    }

    function buy(uint256 tokenId, uint256 amount) external virtual override {
        poles.transferFrom(
            msg.sender,
            address(this),
            calculateTotalPrice(tokenId, amount)
        );

        _mint(msg.sender, tokenId, amount, "");

        emit Buy(tokenId, amount);
    }

    function changePrice(
        uint256 tokenId,
        uint256 newPrice
    ) external virtual override onlyRole(DEFAULT_ADMIN_ROLE) {
        prices[tokenId] = newPrice;

        emit ChangePrice(tokenId, newPrice);
    }

    function withdrawPoles(
        address to,
        uint256 amount
    ) external virtual override onlyRole(DEFAULT_ADMIN_ROLE) {
        poles.transfer(to, amount);

        emit WithdrawPoles(to, amount);
    }

    function supportsInterface(
        bytes4 interfaceId
    ) public view virtual override(ERC1155, AccessControl) returns (bool) {
        return
            interfaceId == type(IERC1155Base).interfaceId ||
            ERC1155.supportsInterface(interfaceId) ||
            AccessControl.supportsInterface(interfaceId);
    }
}
"
    },
    "contracts/tokens/ERC1155/ERC1155Base/IERC1155Base.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED

pragma solidity ^0.8.0;

interface IERC1155Base {
    event AddToken(uint256 indexed tokenId, bytes data, uint256 price);
    event Buy(uint256 indexed tokenId, uint256 amount);
    event ChangePrice(uint256 indexed tokenId, uint256 newPrice);
    event WithdrawPoles(address indexed to, uint256 amount);

    function addToken(bytes memory data, uint256 price) external;

    function calculateTotalPrice(
        uint256 tokenId,
        uint256 amount
    ) external view returns (uint256);

    function buy(uint256 tokenId, uint256 amount) external;

    function changePrice(uint256 tokenId, uint256 newPrice) external;

    function withdrawPoles(address to, uint256 amount) external;
}
"
    },
    "contracts/tokens/ERC20/ERC20AccessControl.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED

pragma solidity ^0.8.0;

import {ERC20PresetFixedSupply} from "@openzeppelin/contracts/token/ERC20/presets/ERC20PresetFixedSupply.sol";
import {DigiToadsAccessControl} from "../../access/DigiToadsAccessControl.sol";

contract ERC20AccessControl is ERC20PresetFixedSupply, DigiToadsAccessControl {
    constructor(
        string memory name_,
        string memory symbol_,
        uint256 initialSupply,
        address owner
    ) ERC20PresetFixedSupply(name_, symbol_, initialSupply, owner) {}

    function transferFrom(
        address from,
        address to,
        uint256 amount
    ) public virtual override returns (bool) {
        address spender = _msgSender();

        if (!hasRole(ERC20_CONTROLLER_ROLE, spender)) {
            _spendAllowance(from, spender, amount);
        }

        _transfer(from, to, amount);
        return true;
    }
}
"
    },
    "contracts/tokens/ERC20/ERC20Buyable/ERC20Buyable.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED

pragma solidity ^0.8.0;

import {ERC20, IERC20} from "@openzeppelin/contracts/token/ERC20/ERC20.sol";

import {DigiToadsAccessControl, AccessControl} from "../../../access/DigiToadsAccessControl.sol";

import {IERC20Buyable} from "./IERC20Buyable.sol";

import {ERC20AccessControl} from "../ERC20AccessControl.sol";

contract ERC20Buyable is ERC20AccessControl, IERC20Buyable {
    IERC20 public poles;

    uint256 public price;

    function _changePrice(uint256 newPrice) internal virtual {
        price = newPrice;

        emit ChangePrice(newPrice);
    }

    constructor(
        string memory name_,
        string memory symbol_,
        IERC20 poles_,
        uint256 price_
    ) ERC20AccessControl(name_, symbol_, 0, msg.sender) {
        poles = poles_;

        _changePrice(price_);
    }

    function buy(uint256 amount) external virtual override {
        poles.transferFrom(msg.sender, address(this), amount * price);

        _mint(msg.sender, amount);

        emit Buy(amount);
    }

    function changePrice(
        uint256 newPrice
    ) external virtual override onlyRole(DEFAULT_ADMIN_ROLE) {
        _changePrice(newPrice);
    }

    function withdrawPoles(
        address to,
        uint256 amount
    ) external virtual override onlyRole(DEFAULT_ADMIN_ROLE) {
        poles.transfer(to, amount);

        emit WithdrawPoles(to, amount);
    }

    function supportsInterface(
        bytes4 interfaceId
    ) public view virtual override returns (bool) {
        return
            interfaceId == type(IERC20Buyable).interfaceId ||
            AccessControl.supportsInterface(interfaceId);
    }
}
"
    },
    "contracts/tokens/ERC20/ERC20Buyable/IERC20Buyable.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED

pragma solidity ^0.8.0;

interface IERC20Buyable {
    event Buy(uint256 amount);
    event ChangePrice(uint256 newPrice);
    event WithdrawPoles(address indexed to, uint256 amount);

    function buy(uint256 amount) external;

    function changePrice(uint256 newPrice) external;

    function withdrawPoles(address to, uint256 amount) external;
}
"
    }
  }
