// File: contracts/Sweeep.sol


pragma solidity ^0.8.19;




interface IEP is IERC20 {
    function claimRewards() external returns (uint256);
    function mintEP(uint256 amountEP) external;
    function stakeEP(uint256 amountEP) external;
    function unstakeEP(uint256 amountEP) external;
    function userInfos(address account) external view returns (uint256 stakedEP, uint256 lastClaim, uint256 amountClaimed);
    function claimableRewards(address account) external view returns (uint256 totalRewards, uint256 teamRewards, uint256 userRewards);
}

interface IERC721Receiver {
    function onERC721Received(address operator, address from, uint256 tokenId, bytes calldata data) external returns (bytes4);
}

contract Sweeep is Ownable, IERC721Receiver {
 
    // Contract parameters
    IERC721 public constant COLLECTION = IERC721(0xE9eeE7294dc7c3bb64FD57A514E755022a333295);
    uint256 public minBuyPriceEP = 1000;

    using SafeERC20 for IERC20;
    using SafeERC20 for IEP;

    IERC20 public constant FBX = IERC20(0xD125443F38A69d776177c2B9c041f462936F8218);
    IEP public constant EP = IEP(0x60Ed6aCEF3a96F8CDaF0c0D207BbAfA66e751af2);
    address public constant SWEEEP_FEE_RECEIVER = 0x960d43BE128585Ca45365CD74a7773B9d814dfBE;

    uint256 public withdrawnByOwner = 0;
    uint256 public lastClaim = block.timestamp;
    uint256 public constant DAILY_UNLOCK = 20 * 1e14; // 0.20%
    uint256 public constant UNLOCK_CAP = 600 * 1e14; // 6.0% => after 30 days, it stops accruing

    // Events
    event nftBought(address buyer, uint256 id, uint256 priceEP, uint256 timestamp);
    event nftSold(address seller, uint256 id, uint256 priceFBX, uint256 timestamp);
    event minBuyPriceEPChanged(uint256 newMinBuyPriceEP);
    event ownerWithdrawal(uint256 amount);
    
    // For safe transfers
    function onERC721Received(address, address, uint256, bytes memory) public virtual returns (bytes4) {
        return this.onERC721Received.selector;
    }

    // Returns the amount of EP in the contract
    function lockedLiquidity() public view returns (uint256) {
        (uint256 stakedEP,,) = EP.userInfos(address(this));
        return stakedEP + EP.balanceOf(address(this));
    }

    // Returns the max amount of EP the owner of the contract can withdraw
    function ownerMaxWithdrawal() public view returns (uint256) {
        uint256 unlock = DAILY_UNLOCK * (block.timestamp - lastClaim) / 3600 / 24;
        if (unlock > UNLOCK_CAP) {
            unlock = UNLOCK_CAP;
        }
        return lockedLiquidity() * unlock / 1e18;
    }

    // Returns current buy price in EP
    function buyPriceEP() public view returns (uint256) {
        uint256 balanceNFT = COLLECTION.balanceOf(address(this));
        if (balanceNFT == 0) {
            return 0;
        }
        uint256 priceEP = (lockedLiquidity() - ownerMaxWithdrawal()) / balanceNFT;
        if (priceEP < minBuyPriceEP) {
            priceEP = minBuyPriceEP;
        }
        return priceEP;
    }

    // Returns current sell price in FBX
    function sellPriceFBX() public view returns (uint256) {
        (,,uint256 rewards) = EP.claimableRewards(address(this));
        return (rewards + FBX.balanceOf(address(this)));
    }

    // Stake available EP
    function stakeAll() public {
        uint256 unstakedEP = EP.balanceOf(address(this));
        if (unstakedEP > 0) {
            EP.stakeEP(unstakedEP);
        }
    }

    // To claim every 30 days if there is no activity on the contract to keep FBX rewards accruing
    function claimContractRewards() public {
        EP.claimRewards();
    }
    
    // Buy an NFT from the contract using EP or FBX
    function buyNFT(uint256 id, uint256 maxPriceEP, bool useFBX, uint256 deadline) public {
        require(block.timestamp <= deadline, "Deadline has passed");
        require(COLLECTION.ownerOf(id) == address(this), "This NFT is not for sale");
        uint256 priceEP = buyPriceEP();
        require (priceEP > 0, "Currently, there is no NFT for sale");
        require(priceEP <= maxPriceEP, "The slippage is too low");

        if (useFBX) {
            uint256 priceFBX = priceEP * 300;
            FBX.safeTransferFrom(msg.sender, address(this), priceFBX);
            FBX.approve(address(EP), priceFBX);
            EP.mintEP(priceEP);
        } else {
            EP.safeTransferFrom(msg.sender, address(this), priceEP);
        }
        stakeAll();
        COLLECTION.safeTransferFrom(address(this), msg.sender, id);

        emit nftBought(msg.sender, id, priceEP, block.timestamp);
    }

    // Sell an NFT to the contract in exchange for FBX
    function sellNFT(uint256 id, uint256 minPriceFBX, uint256 deadline) public {
        require(block.timestamp <= deadline, "Deadline has passed");
        require(COLLECTION.ownerOf(id) == msg.sender, "You do not own this NFT");
        EP.claimRewards();
        uint256 priceFBX = FBX.balanceOf(address(this));
        require(priceFBX > 0, "No rewards in this block");
        require(priceFBX >= minPriceFBX, "The slippage is too low");

        COLLECTION.safeTransferFrom(msg.sender, address(this), id);
        FBX.safeTransfer(msg.sender, priceFBX);

        emit nftSold(msg.sender, id, priceFBX, block.timestamp);
    }

    // Owner functions
    function setMinBuyPriceEP(uint256 newMinBuyPriceEP) public onlyOwner {
        minBuyPriceEP = newMinBuyPriceEP;
        emit minBuyPriceEPChanged(minBuyPriceEP);
    }

    function withdrawEPFromPool(uint256 amountEP) public onlyOwner {
        require(amountEP <= ownerMaxWithdrawal(), "You cannot withdraw this much");
        lastClaim = block.timestamp;
        withdrawnByOwner = withdrawnByOwner + amountEP;
        uint256 balanceEP = EP.balanceOf(address(this));
        if (balanceEP < amountEP) {
            EP.unstakeEP(amountEP - balanceEP);
        }
        uint256 fee = amountEP * 3 / 100;
        EP.safeTransfer(SWEEEP_FEE_RECEIVER, fee);
        EP.safeTransfer(msg.sender, amountEP - fee);

        emit ownerWithdrawal(amountEP);
    }
}
