    "contracts/rewards/MasterChefCustom.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";

// Genetic & Genome intakes one token and allows the user to farm another token.

contract MasterChefCustom is Ownable {
  using SafeERC20 for IERC20;
  // Info of each user.
  struct UserInfo {
    uint256 amount; // How many tokens the user has provided.
    uint256 rewardDebt; // Reward debt. See explanation below.
    uint256 remainingMldTokenReward;  // MLD Tokens that weren't distributed for user per pool.
    //
    // Any point in time, the amount of MLD entitled to a user but is pending to be distributed is:
    // pending reward = (user.amount * pool.accMLDPerShare) - user.rewardDebt
    //
    // Whenever a user deposits or withdraws Staked tokens to a pool. Here's what happens:
    //   1. The pool's `accMLDPerShare` (and `lastRewardTime`) gets updated.
    //   2. User receives the pending reward sent to his/her address.
    //   3. User's `amount` gets updated.
    //   4. User's `rewardDebt` gets updated.
  }
  // Info of each pool.
  struct PoolInfo {
    IERC20 stakingToken; // Contract address of staked token
    uint256 stakingTokenTotalAmount; //Total amount of deposited tokens
    uint256 accMldPerShare; // Accumulated MLD per share, times 1e12. See below.
    uint32 lastRewardTime; // Last timestamp number that MLD distribution occurs.
    uint16 allocPoint; // How many allocation points assigned to this pool. MLD to distribute per second.
  }

  IERC20 immutable public mld; // The MLD token

  uint256 public mldPerSecond; // MLD tokens vested per second.

  PoolInfo[] public poolInfo; // Info of each pool.

  mapping(uint256 => mapping(address => UserInfo)) public userInfo; // Info of each user that stakes tokens.

  uint256 public totalAllocPoint = 0; // Total allocation points. Must be the sum of all allocation points in all pools.

  uint32 immutable public startTime; // The timestamp when MLD farming starts.

  uint32 public endTime; // Time on which the reward calculation should end

  event Deposit(address indexed user, uint256 indexed pid, uint256 amount);
  event Withdraw(address indexed user, uint256 indexed pid, uint256 amount);
  event EmergencyWithdraw(address indexed user, uint256 indexed pid, uint256 amount);

  constructor(
    IERC20 _mld,
    uint256 _mldPerSecond,
    uint32 _startTime
  ) {
    mld = _mld;
    mldPerSecond = _mldPerSecond;
    startTime = _startTime;
    endTime = _startTime + 7 days;
  }

  function changeEndTime(uint32 addSeconds) external onlyOwner {
    endTime += addSeconds;
  }

  // Changes MLD token reward per second. Use this function to moderate the `lockup amount`.
  // Essentially this function changes the amount of the reward which is entitled to the user
  // for his token staking by the time the `endTime` is passed.
  // Good practice to update pools without messing up the contract
  function setMldPerSecond(uint256 _mldPerSecond,  bool _withUpdate) external onlyOwner {
    if (_withUpdate) {
      massUpdatePools();
    }
    mldPerSecond= _mldPerSecond;
  }

  // How many pools are in the contract
  function poolLength() external view returns (uint256) {
    return poolInfo.length;
  }

  // Add a new staking token to the pool. Can only be called by the owner.
  // VERY IMPORTANT NOTICE
  // ----------- DO NOT add the same staking token more than once. Rewards will be messed up if you do. -------------
  // Good practice to update pools without messing up the contract
  function add(
    uint16 _allocPoint,
    IERC20 _stakingToken,
    bool _withUpdate
  ) external onlyOwner {
    if (_withUpdate) {
      massUpdatePools();
    }
    uint256 lastRewardTime = block.timestamp > startTime ? block.timestamp : startTime;
    totalAllocPoint +=_allocPoint;
    poolInfo.push(
      PoolInfo({
        stakingToken: _stakingToken,
        stakingTokenTotalAmount: 0,
        allocPoint: _allocPoint,
        lastRewardTime: uint32(lastRewardTime),
        accMldPerShare: 0
      })
    );
  }

  // Update the given pool's MLD allocation point. Can only be called by the owner.
  // Good practice to update pools without messing up the contract
  function set(
    uint256 _pid,
    uint16 _allocPoint,
    bool _withUpdate
  ) external onlyOwner {
    if (_withUpdate) {
      massUpdatePools();
    }
    totalAllocPoint = totalAllocPoint - poolInfo[_pid].allocPoint + _allocPoint;
    poolInfo[_pid].allocPoint = _allocPoint;
  }

  // Return reward multiplier over the given _from to _to time.
  function getMultiplier(uint256 _from, uint256 _to)
    public
    view
    returns (uint256)
  {
    _from = _from > startTime ? _from : startTime;
    if (_from > endTime || _to < startTime) {
      return 0;
    }
    if (_to > endTime) {
      return endTime - _from;
    }
    return _to - _from;
  }

  // View function to see pending MLD on frontend.
  function pendingMld(uint256 _pid, address _user)
    external
    view
    returns (uint256)
  {
    PoolInfo storage pool = poolInfo[_pid];
    UserInfo storage user = userInfo[_pid][_user];
    uint256 accMldPerShare = pool.accMldPerShare;
    if (block.timestamp > pool.lastRewardTime && pool.stakingTokenTotalAmount != 0) {
      uint256 multiplier = getMultiplier(pool.lastRewardTime, block.timestamp);
      uint256 mldReward = multiplier * mldPerSecond * pool.allocPoint / totalAllocPoint;
      accMldPerShare += mldReward * 1e12 / pool.stakingTokenTotalAmount;
    }
    return user.amount * accMldPerShare / 1e12 - user.rewardDebt + user.remainingMldTokenReward;
  }

  // Update reward variables for all pools. Be careful of gas spending!
  function massUpdatePools() public {
    uint256 length = poolInfo.length;
    for (uint256 pid = 0; pid < length; ++pid) {
      updatePool(pid);
    }
  }

  // Update reward variables of the given pool to be up-to-date.
  function updatePool(uint256 _pid) public {
    PoolInfo storage pool = poolInfo[_pid];
    if (block.timestamp <= pool.lastRewardTime) {
      return;
    }
    if (pool.stakingTokenTotalAmount == 0) {
      pool.lastRewardTime = uint32(block.timestamp);
      return;
    }
    uint256 multiplier = getMultiplier(pool.lastRewardTime, block.timestamp);
    uint256 mldReward = multiplier * mldPerSecond * pool.allocPoint / totalAllocPoint;
    pool.accMldPerShare += mldReward * 1e12 / pool.stakingTokenTotalAmount;
    pool.lastRewardTime = uint32(block.timestamp);
  }

  // Deposit staking tokens to Genetic & Genome for MLD allocation.
  function deposit(uint256 _pid, uint256 _amount) public {
    PoolInfo storage pool = poolInfo[_pid];
    UserInfo storage user = userInfo[_pid][msg.sender];
    updatePool(_pid);
    if (user.amount > 0) {
      uint256 pending = user.amount * pool.accMldPerShare / 1e12 - user.rewardDebt + user.remainingMldTokenReward;
      user.remainingMldTokenReward = safeRewardTransfer(msg.sender, pending);
    }
    pool.stakingToken.safeTransferFrom(address(msg.sender), address(this), _amount);
    user.amount += _amount;
    pool.stakingTokenTotalAmount += _amount;
    user.rewardDebt = user.amount * pool.accMldPerShare / 1e12;
    emit Deposit(msg.sender, _pid, _amount);
  }

  // Withdraw staked tokens from Genetic & Genome.
  function withdraw(uint256 _pid, uint256 _amount) public {
    PoolInfo storage pool = poolInfo[_pid];
    UserInfo storage user = userInfo[_pid][msg.sender];
    require(user.amount >= _amount, "Genetic & Genome: you do not have enough tokens to complete this operation");
    updatePool(_pid);
    uint256 pending = user.amount * pool.accMldPerShare / 1e12 - user.rewardDebt + user.remainingMldTokenReward;
    user.remainingMldTokenReward = safeRewardTransfer(msg.sender, pending);
    user.amount -= _amount;
    pool.stakingTokenTotalAmount -= _amount;
    user.rewardDebt = user.amount * pool.accMldPerShare / 1e12;
    pool.stakingToken.safeTransfer(address(msg.sender), _amount);
    emit Withdraw(msg.sender, _pid, _amount);
  }

  // Withdraw without caring about rewards. EMERGENCY ONLY.
  function emergencyWithdraw(uint256 _pid) public {
    PoolInfo storage pool = poolInfo[_pid];
    UserInfo storage user = userInfo[_pid][msg.sender];
    uint256 userAmount = user.amount;
    pool.stakingTokenTotalAmount -= userAmount;
    user.amount = 0;
    user.rewardDebt = 0;
    user.remainingMldTokenReward = 0;
    pool.stakingToken.safeTransfer(address(msg.sender), userAmount);
    emit EmergencyWithdraw(msg.sender, _pid, userAmount);
  }

  // Safe MLD transfer function. Just in case if the pool does not have enough MLD token,
  // The function returns the amount which is owed to the user
  function safeRewardTransfer(address _to, uint256 _amount) internal returns(uint256) {
    uint256 mldTokenBalance = mld.balanceOf(address(this));
    if (_amount > mldTokenBalance) {
      mld.safeTransfer(_to, mldTokenBalance);
      return _amount - mldTokenBalance;
    }
    mld.safeTransfer(_to, _amount);
    return 0;
  }
