      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.19;

import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721Receiver.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract CarelessAliensStaking is Ownable {
    using EnumerableSet for EnumerableSet.UintSet;

    struct NFTData 
    {
        address lastOwner;
        uint256 lastStakeTime;
        uint256 index;
    }

    mapping(uint256 => NFTData) private nftData;
    mapping(address => EnumerableSet.UintSet) private stakedNFTs;

    uint256 private timeDuration = 86400; // time in seconds, 86400 for a day
    uint256 private ticketsPerDay = 1;

    IERC721 private nftContract;

    constructor(address _nftContract) {
        nftContract = IERC721(_nftContract);
    }

    function setTimeDuration(uint256 _timeDuration) public onlyOwner 
    {
        timeDuration = _timeDuration;
    }

    function setTicketsPerDay(uint256 _ticketsPerDay) public onlyOwner
    {
        ticketsPerDay = _ticketsPerDay;
    }

    function setNFTContract (address _nftContract) public onlyOwner
    {
        nftContract = IERC721(_nftContract);
    }

    function getTimeDuration() public view returns (uint256)
    {
        return timeDuration;
    }

    function Stake(uint256[] calldata tokenIds) external 
    {
        for (uint256 i = 0; i < tokenIds.length; i++) 
        {
            uint256 tokenId = tokenIds[i];

            require(
                nftContract.ownerOf(tokenId) == msg.sender,
                "You are not the owner of this NFT"
            );

            require(
                nftData[tokenId].lastOwner != msg.sender,
                "You have already staked this NFT"
            );
            
            EnumerableSet.UintSet storage userNFTs = stakedNFTs[msg.sender];
            uint256 userNFTCount = userNFTs.length();

            // Check for NFTs that are no longer owned by the user
            uint256[] memory nftsToRemove = new uint256[](userNFTCount);
            uint256 removeIndex = 0;

            for (uint256 j = 0; j < userNFTCount; j++) 
            {
                uint256 nftId = userNFTs.at(j);
                if (nftContract.ownerOf(nftId) != msg.sender) 
                {
                    // Add the NFT ID to the removal array
                    nftsToRemove[removeIndex] = nftId;
                    removeIndex++;
                }
            }

            // Remove the NFTs that are no longer owned by the user
            for (uint256 j = 0; j < removeIndex; j++) 
            {
                uint256 nftId = nftsToRemove[j];
                userNFTs.remove(nftId);
                nftData[nftId].lastStakeTime = 0;
                nftData[nftId].lastOwner = 0x0000000000000000000000000000000000000000;
                nftData[nftId].index = 0;
            }

            // Stake the new NFT
            stakedNFTs[msg.sender].add(tokenId);
            nftData[tokenId].lastStakeTime = block.timestamp;
            nftData[tokenId].lastOwner = msg.sender;

            // Update the index property for the new NFT
            nftData[tokenId].index = userNFTs.length();
        }
    }

    function calculateTickets(uint256[] calldata tokenIds)
        public
        view
        returns (uint256[] memory)
    {
        uint256[] memory ticketCounts = new uint256[](tokenIds.length);

        for (uint256 i = 0; i < tokenIds.length; i++) 
        {
            uint256 tokenId = tokenIds[i];
            if (nftContract.ownerOf(tokenId) == msg.sender && nftData[tokenId].lastOwner == msg.sender) 
            {
                uint256 timeElapsed = block.timestamp - nftData[tokenId].lastStakeTime;
                uint256 numTickets = timeElapsed / timeDuration;
                ticketCounts[i] = numTickets * ticketsPerDay;
            }
        }

        return ticketCounts;
    }

    function calculateTotalTickets(address user) public view returns (uint256) 
    {
        EnumerableSet.UintSet storage nftSet = stakedNFTs[user];
        uint256 totalTickets = 0;

        for (uint256 i = 0; i < nftSet.length(); i++) 
        {
            uint256 tokenId = nftSet.at(i);
            if (nftContract.ownerOf(tokenId) == user && nftData[tokenId].lastOwner == msg.sender) 
            {
                uint256 timeElapsed = block.timestamp - nftData[tokenId].lastStakeTime;
                uint256 numTickets = timeElapsed / timeDuration;
                totalTickets += numTickets * ticketsPerDay;
            } 
        }
        return totalTickets;
    }

    function userStakedNFTCount(address user) public view returns (uint256) 
    {
        EnumerableSet.UintSet storage nftSet = stakedNFTs[user];
        uint256 realNFTCount = 0;

        for (uint256 i = 0; i < nftSet.length(); i++) 
        {
            uint256 tokenId = nftSet.at(i);
            if (nftContract.ownerOf(tokenId) == user && nftData[tokenId].lastOwner == msg.sender) 
            {
                realNFTCount++;
            }
        }

        return realNFTCount;
    }

    function userStakedNFTIDs(address user)
    public
    view
    returns (uint256[] memory)
    {
        EnumerableSet.UintSet storage nftSet = stakedNFTs[user];
        uint256[] memory stakedNFTsArray = new uint256[](userStakedNFTCount(user));
        uint256 counter = 0;

        for (uint256 i = 0; i < nftSet.length(); i++) {
            uint256 tokenId = nftSet.at(i);
            if (nftContract.ownerOf(tokenId) == user && nftData[tokenId].lastOwner == msg.sender) {
                stakedNFTsArray[counter] = tokenId;
                counter++;
            }
        }

        return stakedNFTsArray;
    }

    function readNFTData(uint256 tokenId) public view returns (address, uint256, uint256)
    {
        return (nftData[tokenId].lastOwner, nftData[tokenId].lastStakeTime, nftData[tokenId].index);
    }

}
"
