{{
  "language": "Solidity",
  "sources": {
    "contracts/PopularFarm.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import "@openzeppelin/contracts-upgradeable/utils/math/MathUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/math/SafeMathUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";

import "./libraries/OwnableUpgradeable.sol";
import "./libraries/NativeMetaTransaction.sol";

contract PopularFarm is
    Initializable,
    ReentrancyGuardUpgradeable,
    UUPSUpgradeable,
    OwnableUpgradeable,
    NativeMetaTransaction
{
    using SafeMathUpgradeable for uint256;
    using SafeERC20Upgradeable for IERC20Upgradeable;

    /* ========== STATE VARIABLES ========== */

    struct UserVestingInfo {
        bool hasOptForVesting;
        bool hasSetConfig;
    }
    IERC20Upgradeable public stakingToken;
    uint256 public startTime;
    uint256 public periodFinish;
    uint256 public rewardsDuration;
    uint256 public burnRate;
    uint256 public vestingPeriod;
    uint256 public splits;
    uint256 public claim;
    uint256 public splitWindow;
    mapping(address => uint256) public totalBurnableTokens;
    address[] public rewardTokens;
    address public deployer;
    mapping(address => uint256) public rewardsPerTokenMap;
    mapping(address => uint256) public tokenRewardRate;
    mapping(address => uint256) public rewardLastUpdatedTime;
    mapping(address => mapping(address => uint256)) public userRewardPerTokenPaid;
    mapping(address => UserVestingInfo) public userVestingInfoByUser;
    mapping(address => mapping(address => uint256)) public rewards;
    mapping(address => mapping(address => uint256)) public totalEarnedReward;
    mapping(address => uint256) public claimedSplits;
    mapping(address => bool) public hasClaimed;
    mapping(address => mapping(address => uint256)) public totalVestedRewardForUser;

    uint256 private _totalSupply;
    mapping(address => uint256) private _balances;

    /* ========== Error Codes ========== */

    // TLM-Token Length Mismatch
    //ETITE-End Time Is Too Early
    //CSZ-Cannot Stake Zero
    //CWZ-Cannot Withdraw Zero
    //CUVSN- Cannot update vesting schedule now
    //CCCAC- Cannot change config after claimed
    //CCTN-Cannot claims token now
    //TF-Transfer Failed
    //ROSTNA-rescue of staking token not allowed

    /* ========== INITIALIZER ========== */
    function initialize(bytes memory _data, address _deployer) external initializer {
        require(_deployer != address(0), "Deployer Cant be empty");
        deployer = _deployer;
        uint256[] memory _rewardAmounts;
        bytes memory _encodedData;
        address _stakingToken;
        (_stakingToken, startTime, periodFinish, rewardTokens, _rewardAmounts, _encodedData) = dataDecoder(_data);

        (burnRate, vestingPeriod, splits, claim) = vestingDataDecoder(_encodedData);
        require(periodFinish != block.timestamp && startTime != periodFinish, "ETITE");
        rewardsDuration = periodFinish.sub(startTime);
        stakingToken = IERC20Upgradeable(_stakingToken);
        splitWindow = vestingPeriod.div(splits);
        rewardDataIntializer(_rewardAmounts);
        __Ownable_init();
        __ReentrancyGuard_init();
        _initializeEIP712("PopularFarm");
    }

    function rewardDataIntializer(uint256[] memory _rewardAmounts) internal {
        uint256 rewardTokensLength = rewardTokens.length;
        uint256 rewardAmountlength = _rewardAmounts.length;
        require(rewardTokensLength == rewardAmountlength, "TLM");
        for (uint256 i = 0; i < rewardTokensLength; i++) {
            address rewardToken=rewardTokens[i];
            rewardsPerTokenMap[rewardToken] = rewardPerToken(rewardToken);
            tokenRewardRate[rewardToken] = _rewardAmounts[i].div(rewardsDuration);
            require(tokenRewardRate[rewardToken]!=0,"Low reward");
            rewardLastUpdatedTime[rewardToken] = lastTimeRewardApplicable();
        }
    }

    /* ========== UPGRADABLE FUNCTION ========== */
    function _authorizeUpgrade(address newImplementation) internal override onlyOwner {}

    /* ========== VIEWS ========== */

    function totalSupply() external view returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(address account) external view returns (uint256) {
        return _balances[account];
    }

    function lastTimeRewardApplicable() public view returns (uint256) {
        if (block.timestamp < startTime) {
            return startTime;
        }
        return MathUpgradeable.min(block.timestamp, periodFinish);
    }

    function rewardPerToken(address rewardToken) public view returns (uint256) {
        if (_totalSupply == 0) {
            return rewardsPerTokenMap[rewardToken];
        }
        return
            rewardsPerTokenMap[rewardToken].add(
                lastTimeRewardApplicable()
                    .sub(rewardLastUpdatedTime[rewardToken])
                    .mul(getTokenRewardRate(rewardToken))
                    .mul(1e18)
                    .div(_totalSupply)
            );
    }

    function getTokenRewardRate(address token) public view returns (uint256) {
        if (block.timestamp < startTime) {
            return 0;
        }
        return tokenRewardRate[token];
    }

    function earned(address account, address rewardToken) public view returns (uint256) {
        return
            _balances[account]
                .mul(rewardPerToken(rewardToken).sub(userRewardPerTokenPaid[account][rewardToken]))
                .div(1e18)
                .add(rewards[account][rewardToken]);
    }

    function tokensEarned(address _account) external view returns (address[] memory,uint256[] memory) {
        uint256 _rewardTokensLength = rewardTokens.length;
        uint256[] memory _totalEarnedAmount = new uint256[](_rewardTokensLength);
        for (uint256 i = 0; i < _rewardTokensLength; i++) {
            _totalEarnedAmount[i] = earned(_account, rewardTokens[i]);
        }
        return (rewardTokens,_totalEarnedAmount);
    }

    function tokensRewardRate() external view returns (address[] memory,uint256[] memory) {
         uint256 _rewardTokensLength = rewardTokens.length;
        uint256[] memory _rewardRates = new uint256[](_rewardTokensLength);
        for (uint256 i = 0; i < rewardTokens.length; i++) {
            _rewardRates[i] = getTokenRewardRate(rewardTokens[i]);
        }
        return (rewardTokens,_rewardRates);
    }

    function totalVestedRewards(address _account) external view returns (address[] memory, uint256[] memory) {
        uint256 _rewardTokensLength = rewardTokens.length;
        uint256[] memory _vestedAmount = new uint256[](_rewardTokensLength);
        for (uint256 i = 0; i < _rewardTokensLength; i++) {
            _vestedAmount[i] = totalVestedRewardForUser[_account][rewardTokens[i]];
        }
        return (rewardTokens, _vestedAmount);
    }

    function totalEarnedRewards(address _account) external view returns (address[] memory, uint256[] memory) {
        uint256 _rewardTokensLength = rewardTokens.length;
        uint256[] memory _totalEarnedAmount = new uint256[](_rewardTokensLength);
        for (uint256 i = 0; i < _rewardTokensLength; i++) {
            _totalEarnedAmount[i] = totalEarnedReward[_account][rewardTokens[i]];
        }
        return (rewardTokens, _totalEarnedAmount);
    }

    function getRewardForDuration(address rewardToken) external view returns (uint256) {
        return getTokenRewardRate(rewardToken).mul(rewardsDuration);
    }

    function getUserVestingInfo(address account) external view returns (UserVestingInfo memory) {
        return userVestingInfoByUser[account];
    }

    /* ========== MUTATIVE FUNCTIONS ========== */

    function stakeWithPermit(
        uint256 amount,
        uint256 deadline,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external nonReentrant updateReward(_msgSender()) {
        require(amount > 0, "CSZ");
        _totalSupply = _totalSupply.add(amount);
        _balances[_msgSender()] = _balances[_msgSender()].add(amount);

        // permit
        IUniswapV2ERC20(address(stakingToken)).permit(_msgSender(), address(this), amount, deadline, v, r, s);

        stakingToken.safeTransferFrom(_msgSender(), address(this), amount);
        emit Staked(_msgSender(), amount);
    }

    function stake(uint256 amount) external nonReentrant updateReward(_msgSender()) {
        require(amount > 0, "CSZ");
        _totalSupply = _totalSupply.add(amount);
        _balances[_msgSender()] = _balances[_msgSender()].add(amount);
        stakingToken.safeTransferFrom(_msgSender(), address(this), amount);
        emit Staked(_msgSender(), amount);
    }

    function withdraw(uint256 amount) public nonReentrant updateReward(_msgSender()) {
        require(amount > 0, "CWZ");
        _totalSupply = _totalSupply.sub(amount);
        _balances[_msgSender()] = _balances[_msgSender()].sub(amount);
        stakingToken.safeTransfer(_msgSender(), amount);
        emit Withdrawn(_msgSender(), amount);
    }

    function setVestingConfig(bool _setConfig) external {
        require(block.timestamp <= periodFinish, "CUVSN");
        UserVestingInfo storage info = userVestingInfoByUser[_msgSender()];
        info.hasSetConfig = true;
        require(!hasClaimed[_msgSender()], "CCCAC");
        info.hasOptForVesting = _setConfig;
    }

    // Before calling this function caller must set the config for vesting
    // if caller set false for hasOptForVesting 50% of his reward will give right way
    // and remaining will account for burn
    // if set true, his reward will get vested for configured period.
    function getReward() public nonReentrant updateReward(_msgSender()) {
        require(block.timestamp >= periodFinish && periodFinish > 0, "CCTN");
        UserVestingInfo storage info = userVestingInfoByUser[_msgSender()];

        if (!info.hasSetConfig) {
            info.hasOptForVesting = true;
            info.hasSetConfig = true;
        }
        uint256 reward;
        uint256 currentSplit;
       uint256 _rewardTokensLength = rewardTokens.length;
        if (!info.hasOptForVesting) {
            hasClaimed[_msgSender()] = true;
            for (uint256 i = 0; i < _rewardTokensLength; i++) {
            address rewardToken=rewardTokens[i];
                uint256 burnable = rewards[_msgSender()][rewardToken].mul(burnRate).div(100);
                reward = rewards[_msgSender()][rewardToken].sub(burnable);
                totalBurnableTokens[rewardToken] = totalBurnableTokens[rewardToken].add(burnable);
                rewards[_msgSender()][rewardToken] = 0;
                IERC20Upgradeable(rewardToken).safeTransfer(_msgSender(), reward);
                emit RewardPaid(_msgSender(), rewardToken, reward);
            }
        } else {
            uint256 claimedSplitsForUser = claimedSplits[_msgSender()];
            uint256 currentDate = block.timestamp;
            for (uint256 i = 0; i < _rewardTokensLength; i++) {
                address rewardToken=rewardTokens[i];
                reward = 0;
                currentSplit = 0;
                if (claimedSplitsForUser == 0 && !hasClaimed[_msgSender()]) {
                    totalEarnedReward[_msgSender()][rewardToken] = rewards[_msgSender()][rewardToken];
                    reward = reward.add((rewards[_msgSender()][rewardToken].mul(claim).div(100)));
                    totalVestedRewardForUser[_msgSender()][rewardToken] = rewards[_msgSender()][rewardToken]
                        .sub(reward);
                }
                if (claimedSplitsForUser < splits) {
                    currentSplit = (currentDate.sub(periodFinish)).div(splitWindow);
                    currentSplit = currentSplit > splits ? splits : currentSplit;
                    reward = reward.add(
                        (
                            totalVestedRewardForUser[_msgSender()][rewardToken].mul(
                                (currentSplit.sub(claimedSplitsForUser))
                            )
                        ).div(splits)
                    );

                    if (claimedSplitsForUser != currentSplit) {
                        claimedSplits[_msgSender()] = currentSplit;
                    }
                    if (reward > 0) {
                        if (i == _rewardTokensLength - 1) {
                            hasClaimed[_msgSender()] = true;
                        }
                        rewards[_msgSender()][rewardToken] = rewards[_msgSender()][rewardToken].sub(reward);
                        IERC20Upgradeable(rewardToken).safeTransfer(_msgSender(), reward);
                        emit RewardPaid(_msgSender(), rewardToken, reward);
                    }
                }
            }
        }
    }

    function exit() external {
        withdraw(_balances[_msgSender()]);
        if (block.timestamp >= periodFinish && periodFinish > 0) getReward();
    }

    /* ========== RESTRICTED FUNCTIONS ========== */

    function rescueBurnableFunds(address receiver, address tokenAddress) external onlyOwner {
        require(tokenAddress != address(stakingToken), "ROSTNA");
        uint256 burnAmount = totalBurnableTokens[tokenAddress];
        totalBurnableTokens[tokenAddress] = 0;
        require(IERC20Upgradeable(tokenAddress).transfer(receiver, burnAmount), "TF");
        emit Burned(receiver, tokenAddress, burnAmount);
    }

    function rescueFunds(address tokenAddress, address receiver) external onlyOwner {
        require(tokenAddress != address(stakingToken), "ROSTNA");
        require(
            IERC20Upgradeable(tokenAddress).transfer(
                receiver,
                IERC20Upgradeable(tokenAddress).balanceOf(address(this))
            ),
            "TF"
        );
    }

    function dataDecoder(bytes memory data)
        internal
        pure
        returns (
            address _stakingToken,
            uint256 _startTime,
            uint256 _endTime,
            address[] memory _rewardTokens,
            uint256[] memory _rewardAmounts,
            bytes memory _basedata
        )
    {
        (_stakingToken, _startTime, _endTime, _rewardTokens, _rewardAmounts, _basedata) = abi.decode(
            data,
            (address, uint256, uint256, address[], uint256[], bytes)
        );
    }

    function vestingDataDecoder(bytes memory _data)
        internal
        pure
        returns (
            uint256 _burnRate,
            uint256 _vesting,
            uint256 _splits,
            uint256 _claim
        )
    {
        (_burnRate, _vesting, _splits, _claim) = abi.decode(_data, (uint256, uint256, uint256, uint256));
    }

    /* ========== MODIFIERS ========== */

    modifier updateReward(address account) {
        uint256 _rewardTokensLength = rewardTokens.length;
        for (uint256 i = 0; i < _rewardTokensLength; i++) {
            address rewardToken=rewardTokens[i];
            rewardsPerTokenMap[rewardToken] = rewardPerToken(rewardToken);
            rewardLastUpdatedTime[rewardToken] = lastTimeRewardApplicable();
            if (account != address(0)) {
                rewards[account][rewardToken] = earned(account, rewardToken);
                userRewardPerTokenPaid[account][rewardToken] = rewardsPerTokenMap[rewardToken];
            }
        }
        _;
    }

    modifier onlyDeployer(address _account) {
        require(_account == deployer, "OnlyWhitelisted");
        _;
    }

    /* ========== EVENTS ========== */

    event RewardAdded(uint256 reward);
    event Staked(address indexed user, uint256 amount);
    event Withdrawn(address indexed user, uint256 amount);
    event RewardPaid(address indexed user, address token, uint256 reward);
    event Burned(address receiver, address rewardToken, uint256 burnAmount);
}

interface IUniswapV2ERC20 {
    function permit(
        address owner,
        address spender,
        uint256 value,
        uint256 deadline,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external;
}
"
