pragma solidity ^0.8.0;

contract IndiGGBadgeNFT is ERC721, EIP712MetaTransaction("IndiGG Badge Minter", "1"), Ownable {
    
    using Counters for Counters.Counter;
    Counters.Counter private _tokenIds;
    mapping(address => bool) public isWhitelisted;
    mapping(address => bool) public isMinted;
    mapping(bytes32 => bool) public isUserAdded;
    mapping(bytes32 => address) public userId_address;

    event User(uint256 timestamp, address user);
    event Mint(uint256 timestamp, address user, uint256 tokenId);

    constructor() public ERC721("IndiGG Badge", "IndiGG") {}

    function mintToCaller(address caller) 
        public
        returns (uint256)
    {
        require(isWhitelisted[caller], "IndiGG: caller not authorised"); // Makes sure that only the discord members can mint the badge
        require(isMinted[caller] == false, "IndiGG: NFT already minted");
        _tokenIds.increment();

        uint256 newItemId = _tokenIds.current(); 

        emit Mint(block.timestamp, caller, newItemId);
        isMinted[caller] = true;
        _mint(caller, newItemId);
        return newItemId;
    }

    function batchMint(address[] memory caller) external onlyOwner {
        for (uint256 i = 0; i<caller.length; i++) {
            mintToCaller(caller[i]);
        }
    }

    function setWhitelist(bytes32 userId, address user) public onlyOwner {
        require(!isUserAdded[userId], "IndiGG: user already added");
        isUserAdded[userId] = true;
        isWhitelisted[user] = true;
        userId_address[userId] = user;
        emit User(block.timestamp, user);
    }

    function setBaseURI(string memory baseURI) external onlyOwner {
        _setBaseURI(baseURI);
    }
    
}
