    bool bridgeInitState;
    IethPrice public priceContract;
    address public gateway;
    uint256 public bridgeFee; // 2.75 usd = 275 
    uint256 public totalLockedAmount ;
    
    constructor () {
   _disableInitializers();
    }

    function initializeBridge (address _gateway ,address _childTokenAddress, address _priceOracle) onlyOwner external {
        gateway = _gateway;
        mainToken = IERC20Child(_childTokenAddress);
        priceContract = IethPrice(_priceOracle);
        bridgeInitState = true;
        if(totalLockedAmount == 0){
        totalLockedAmount = mainToken.balanceOf(address(this));
       }
    }

    function lockTokens (uint256 _chainId, uint _bridgedAmount, bytes32 _mainDepositHash)verifyInitialization  external payable  {
        uint256 usdtAmount = usdCalculation() ;
        require(usdtAmount <= msg.value , "bridge fee required");
        mainToken.transferFrom(msg.sender, address(this),_bridgedAmount);
        totalLockedAmount = totalLockedAmount + _bridgedAmount ;
       emit TokensLocked(_chainId, msg.sender, _mainDepositHash, _bridgedAmount);
    }

    function unlockTokens (address _requester, uint _bridgedAmount, bytes32 _sideDepositHash) verifyInitialization onlyGateway  external {
        require(mainToken.balanceOf(address(this)) >= _bridgedAmount , "contract have not sufficient fund" ) ;
        mainToken.transfer(_requester,_bridgedAmount);
        totalLockedAmount = totalLockedAmount - _bridgedAmount ;
       emit TokensUnlocked(_requester, _sideDepositHash, _bridgedAmount, block.timestamp);
    }

