      "content": "//SPDX-License-Identifier: BUSL-1.1
//Reality NFT Contracts
pragma solidity 0.8.9;

import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/utils/cryptography/draft-EIP712.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

 /// @title A recurring payment system for voucher holders
 /// @author Imapp
 /// @notice This contract pays token value shown in the signed voucher less value already paid.
 /// The payee is part of the message. The payment can be initiated by any party.
 /// @dev This implements the long term payment channel with one payer and many payees.
 /// The EIP-712 standard is still under review, but has became widely used de facto industry standard.
 /// The signed message type is defined as per VOUCHER__TYPE
contract VoucherPayment is EIP712, Ownable {
    string internal constant VOUCHER__TYPE = "Voucher(address receiver,uint256 totalAmount)";
    bytes32 internal constant VOUCHER__TYPEHASH = keccak256(abi.encodePacked(VOUCHER__TYPE));

    struct Voucher {
        address receiver;
        uint256 totalAmount;
    }

    event VoucherPaid(address indexed receiver, uint256 amount);
    event NewSigner(address indexed signer);
    event CloseInitiated(uint expirationDate);
    event Close();

    address public signer;

    /// @dev This is a specific token, transfers return true or revert, no need for safeTransfer.
    IERC20 public immutable token;

    /// @notice Date after which the contract can be closed and the remainder of the funds is returned to the owner.
    uint256 public expirationDate;

    /// @notice Total amount withdrawn to date per payee.
    mapping(address => uint256) internal totalWithdrawn;

    /// @param token_ Adddress of the payment token used as currency, ERC20 compliant
    /// @param signer_ Account signing vouchers
    constructor(IERC20 token_, address signer_) EIP712("Reality Voucher Payment", "1.0.0") {
        require(signer_ != address(0), "VoucherPayment: Signer cannot be null");
        require(address(token_) != address(0), "VoucherPayment: Token cannot be null");

        signer = signer_;
        token = token_;
        expirationDate = 0;
        emit NewSigner(signer_);
    }

    /// @notice Validates the signature is correct for the given voucher and signer
    function isValid(Voucher calldata voucher, bytes calldata signature) public view returns (bool) {
        bytes32 digest = _hashTypedDataV4(
            keccak256(abi.encode(VOUCHER__TYPEHASH, voucher.receiver, voucher.totalAmount))
        );
        address recoveredSigner = ECDSA.recover(digest, signature);

        return recoveredSigner == signer;
    }

    /// @notice Calculates amount due to the payee. This is the difference between value paid to date and the total voucher value.
    function calculateWithdrawal(Voucher calldata voucher) public view returns (uint256) {
        require(voucher.totalAmount > totalWithdrawn[voucher.receiver], "VoucherPayment: Already withdrawn");

        return voucher.totalAmount - totalWithdrawn[voucher.receiver];
    }

    /// @notice Retrieves amount paid to date
    function getWithdrawn(address receiver) external view returns (uint256) {
        return totalWithdrawn[receiver];
    }

    /// @notice Given valid signature, pays calculated amount to the payee and updates records
    function withdraw(Voucher calldata voucher, bytes calldata signature) external {
        require(isValid(voucher, signature), "VoucherPayment: Invalid signature");
        uint256 withdrawalAmount = calculateWithdrawal(voucher);

        require(token.balanceOf(address(this)) >= withdrawalAmount, "VoucherPayment: Insufficient funds");

        totalWithdrawn[voucher.receiver] = voucher.totalAmount;

        emit VoucherPaid(voucher.receiver, withdrawalAmount);

        token.transfer(voucher.receiver, withdrawalAmount);
    }

    /// @notice Initiates 30 days period after which the contract can be closed
    function initiateClose() external onlyOwner {
        require(expirationDate == 0, "VoucherPayment: Contract already closing");
        expirationDate = block.timestamp + 30 days;
        emit CloseInitiated(expirationDate);
    }

    /// @notice After the expriation date the contract can be closed and the remainder is returned to the owner
    function close() external onlyOwner {
        require(expirationDate != 0, "VoucherPayment: Close not initiated");
        require(block.timestamp >= expirationDate, "VoucherPayment: Live contract");

        uint256 remainingBalance = token.balanceOf(address(this));
        emit Close();

        token.transfer(msg.sender, remainingBalance);
        selfdestruct(payable(msg.sender));
    }

    /// @notice Owner can change the signer
    function setSigner(address signer_) external onlyOwner {
        require(signer_ != address(0), "VoucherPayment: The signer cannot be null");
        signer = signer_;
        emit NewSigner(signer);
    }
}
"
