    "contracts/ERC1155Verifier.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import "./lib/GenesisUtils.sol";
import "./interfaces/ICircuitValidator.sol";
import "./verifiers/ZKPVerifier.sol";

contract ERC1155Verifier is ERC1155, ZKPVerifier {
    string public name = "Polygon ID early adopter n1";
    uint64 public constant MINT_REQUEST_ID = 1;

    uint256 private trustedIssuer;

    mapping(uint256 => address) public idToAddress;
    mapping(address => uint256) public addressToId;

    uint256 public constant NFT_ID = 1;


    constructor(string memory url_, uint256 trustedIssuer_)
    ERC1155(url_)
    {
        trustedIssuer = trustedIssuer_;
    }

    function setTrustedIssuer(uint256 issuer) public onlyOwner {
        trustedIssuer = issuer;
    }

    function getTrustedIssuer() public view returns (uint256) {
        return trustedIssuer;
    }

    function _beforeProofSubmit(
        uint64, /* requestId */
        uint256[] memory inputs,
        ICircuitValidator validator
    ) internal view override {
        // check that  challenge input is address of sender
        address addr = GenesisUtils.int256ToAddress(
            inputs[validator.getChallengeInputIndex()]
        );
        // this is linking between msg.sender and
        require(
            _msgSender() == addr,
            "address in proof is not a sender address"
        );

        // only valid for signature validator
        // polygon-mumbai: 0xF2D4Eeb4d455fb673104902282Ce68B9ce4Ac450
        // polygon-mainnet: 0x9ee6a2682Caa2E0AC99dA46afb88Ad7e6A58Cd1b
        uint256 issuerID = inputs[7];
        require(
            issuerID == trustedIssuer,
            "proof is not issued by trusted issuer"
        );
    }

    function _afterProofSubmit(
        uint64 requestId,
        uint256[] memory inputs,
        ICircuitValidator validator
    ) internal override {
        require(
            requestId == MINT_REQUEST_ID && addressToId[_msgSender()] == 0,
            "proof can not be submitted more than once"
        );

        // get user id
        uint256 id = inputs[validator.getChallengeInputIndex()];
        // additional check didn't get airdrop tokens before
        if (idToAddress[id] == address(0) && addressToId[_msgSender()] == 0 ) {
            super._mint(_msgSender(), NFT_ID, 1, "");
            addressToId[_msgSender()] = id;
            idToAddress[id] = _msgSender();
        }
    }

    function _beforeTokenTransfer(
        address,
        address,
        address to,
        uint256[] memory,
        uint256[] memory,
        bytes memory
    ) internal view override {
        require(
            proofs[to][MINT_REQUEST_ID] == true,
            "only identities who provided proof are allowed to receive tokens"
        );
    }

}
"
    },
