      "content": "//SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.0;

// TODO: Rem console.sol before deployment
import "hardhat/console.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./FinspotInvestmentIndex.sol";
import {FinspotShared} from "./FinspotShared.sol";

contract FinspotToken is ERC20, Ownable {
    mapping(address => bool) private _indexAddresses;
    mapping(address => bool) private _investorWhitelist;

    uint32 private _currentInvestmentBlockchainId;

    event InvestmentCreated(address indexed investor, address indexed investmentIndex, uint256 value, uint32 investmentBlockchainId);
    event InvestmentCompleted(uint32 investmentBlockchainId, uint256 investedAmount, uint256 profit);
    event TokensSold(address indexed investor, uint256 amount);

    constructor() ERC20("FinspotFaktoringToken", "FIN") Ownable() {
        _mint(_msgSender(), 35250000000000000000000);
        _currentInvestmentBlockchainId = 0;
    }

    function transfer(address recipient, uint256 amount) public override returns (bool) {
        require(balanceOf(_msgSender()) >= amount, "not enough balance");

        // Finspot is selling tokens to an investor, whitelist him
        if (_msgSender() == owner()) {
            require(owner() != recipient, "Owner cant sent to self");
            return whitelistInvestorAndPerformTransfer(recipient, amount);
        }

        // Is investor trying to invest? Perform the investment as he is already whitelisted as token holder
        bool isIndexAddress = _indexAddresses[recipient];
        if (isIndexAddress) {
            return performInvestment(recipient, amount);
        }

        // Investor is selling tokens back to Finspot
        if (recipient == owner()) {
            return performTokenSale(recipient, amount);
        }

        //  If investor is trying to send tokens to address that is not index or finspot, nothing happens

        return false;
    }

    function registerIndexAddress(address indexContractAddress) public onlyOwner {
        _indexAddresses[indexContractAddress] = true;
    }

    function removeInvestorFromWhitelist(address investorAddress) public onlyOwner {
        _investorWhitelist[investorAddress] = false;
    }

    function complete(uint32 investmentBlockchainId, address indexContractAddress) public onlyOwner returns (bool) {
        require(indexContractAddress != address(0), "index cannot be zero address");
        require(_indexAddresses[indexContractAddress] == true, "address is not an index address");

        FinspotInvestmentIndex indexContract = FinspotInvestmentIndex(indexContractAddress);

        FinspotShared.InvestorReturns memory investorReturn = indexContract.complete(investmentBlockchainId);

        _transfer(indexContractAddress, investorReturn.investor, investorReturn.investedAmount);

        emit InvestmentCompleted(investmentBlockchainId, investorReturn.investedAmount, investorReturn.profit);

        return true;
    }

    function whitelistInvestorAndPerformTransfer(address recipient, uint256 amount) private returns (bool) {
        _investorWhitelist[recipient] = true;
        _transfer(_msgSender(), recipient, amount);
        
        return true;
   }

    function performInvestment(address recipient, uint256 amount) private returns (bool) {
        FinspotInvestmentIndex indexContract = FinspotInvestmentIndex(recipient);

        _currentInvestmentBlockchainId++;

        indexContract.stake(_msgSender(), amount, _currentInvestmentBlockchainId);

        _transfer(_msgSender(), recipient, amount);
        
        emit InvestmentCreated(_msgSender(), recipient, amount, _currentInvestmentBlockchainId);
        return true;
   }
   
    function performTokenSale(address recipient, uint256 amount) private returns (bool) {
        _transfer(_msgSender(), recipient, amount);
        emit TokensSold(_msgSender(), amount);
        
        return true;
   }

    function checkIfAddressWhiteListed(address investor) public onlyOwner view returns (bool) {
        return _investorWhitelist[investor];
    }

    function checkIfIndexRegistered(address index) public onlyOwner view returns (bool) {
        return _indexAddresses[index];
    }
    
    function allowance(address owner, address spender) public pure override returns (uint256) {
        revert("Method not allowed");
    }

    function approve(address spender, uint256 amount) public pure override returns (bool) {
        revert("Method not allowed");
    }

    function increaseAllowance(address spender, uint256 addedValue) public pure override returns (bool) {
        revert("Method not allowed");
    }

    function decreaseAllowance(address spender, uint256 addedValue) public pure override returns (bool) {
        revert("Method not allowed");
    }
}"
    },
