    "contracts/FinspotInvestmentIndex.sol": {
      "content": "//SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.0;

// TODO: Rem console.sol before deployment
import "hardhat/console.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import {FinspotShared} from "./FinspotShared.sol";

contract FinspotInvestmentIndex is Ownable {
    uint constant private  SECONDS_PER_DAY = 24 * 60 * 60;
    
    string private _indexId;
    string private _indexDescription;
    uint256 private _indexDuration;
    uint256 private _indexOpenForInvestingDuration;
    uint256 private _indexInterest;
    uint256 private _originalyAvailable;
    address private _tokenContract;
    uint256 private _totalInvested;
    uint256 private _activationTimestamp;

    enum InvestmentStatus { ACTIVE, COMPLETED }

    struct Investment {
        uint32 id;
        address investorAddress;
        uint256 timestamp;
        uint256 amount;
        InvestmentStatus status;
        uint256 investmentReturn;
    }

    uint32 private _investorCount;
    mapping(address => Investment[]) private _investorParticipation;
    mapping(uint256 => Investment) private _investmentsLookup;

    constructor(
        string memory id,
        string memory description,
        uint256 duration,
        uint256 openForInvestingDuration,
        uint256 interest,
        uint256 openAmount,
        address tokenContract
    ) Ownable() {
        _indexId = id;
        _indexDescription = description;
        _indexDuration = duration;
        _indexOpenForInvestingDuration = openForInvestingDuration;
        _indexInterest = interest;
        _originalyAvailable = openAmount;
        _tokenContract = tokenContract;
        // warning suppresion - it is ok to relly on block.timestamp here because we allow for multi-hour difference
        _activationTimestamp = block.timestamp;
    }

    function indexId() public view returns (string memory) {
        return _indexId;
    }

    function indexDescription() public view returns (string memory) {
        return _indexDescription;
    }

    function indexDuration() public view returns (uint256) {
        return _indexDuration;
    }

    function indexInterest() public view returns (uint256) {
        return _indexInterest;
    }

    function originalyAvailable() public view returns (uint256) {
        return _originalyAvailable;
    }

    function totalInvested() public view returns (uint256) {
        return _totalInvested;
    }

    function totalAvailable() public view returns (uint256) {
        return _originalyAvailable - _totalInvested;
    }

    function getInvestorCount() public view returns (uint32) {
        return _investorCount;
    }

    function getInvestmentsForSpecificInvestor(address investor) public view returns (Investment[] memory)
    {
        return _investorParticipation[investor];
    }

    function getInvestmentById(uint256 investmentBlockchainId) public view returns (Investment memory)
    {
        return _investmentsLookup[investmentBlockchainId];
    }

    function stake(address investor, uint256 amount, uint32 currentInvestmentBlockchainId) public {
        require(_msgSender() == _tokenContract, "Sender is not token contract");

        // warning suppresion - it is ok to relly on block.timestamp here because we allow for multi-hour difference  
        uint timestamp = block.timestamp;

        require(
            timestamp > _activationTimestamp && timestamp <= _activationTimestamp + (_indexDuration * 1 days),
            "Timestamp outside bounds"
        );

        require(
            timestamp > _activationTimestamp && timestamp <= _activationTimestamp + (_indexOpenForInvestingDuration * 1 days),
            "Investing period has expired"
        );
        require(totalAvailable() >= amount, "amount > total available");

        Investment[] storage existingInvestments = _investorParticipation[investor];

        if (existingInvestments.length == 0) {
            _investorCount++;
        }

        Investment memory newInvestment = Investment(currentInvestmentBlockchainId, investor, timestamp, amount, InvestmentStatus.ACTIVE, 0);

        existingInvestments.push(newInvestment);
        _investmentsLookup[currentInvestmentBlockchainId] = newInvestment;

        _totalInvested = _totalInvested + newInvestment.amount;
    }

    function complete(uint32 investmentBlockchainId) public returns (FinspotShared.InvestorReturns memory)
    {
        require(_msgSender() == _tokenContract, "Sender is not token contract");

        Investment storage existingInvestment = _investmentsLookup[investmentBlockchainId];

        require(existingInvestment.timestamp != 0, "Investment does not exist");

        require(existingInvestment.status == InvestmentStatus.ACTIVE, "Investment already completed");

        require(
            block.timestamp >= _activationTimestamp + (_indexDuration * 1 days),
            "Index has not expired yet"
        );

        uint256 investmentDuration = diffDays(existingInvestment.timestamp, _activationTimestamp + (_indexDuration * 1 days));

        uint256 returnOfThisInvestment = (existingInvestment.amount *_indexInterest * investmentDuration) / 1000000000000000000;

        FinspotShared.InvestorReturns memory returnsOfThisInvestor = FinspotShared.InvestorReturns(existingInvestment.investorAddress, existingInvestment.amount, returnOfThisInvestment);

        existingInvestment.investmentReturn = returnOfThisInvestment;
        existingInvestment.status = InvestmentStatus.COMPLETED;
        
        return returnsOfThisInvestor;
    }

    function diffDays(uint fromTimestamp, uint toTimestamp) private pure returns (uint _days) {
        require(fromTimestamp <= toTimestamp, "From later than to");
        uint _diff = toTimestamp - fromTimestamp;
        _days = _diff / SECONDS_PER_DAY;
        uint _modDays = _diff % SECONDS_PER_DAY;

        if (_modDays > 0) {
            _days = _days + 1;
        }

        return _days;
    }
}
"
    },
