        whitelisted[_user][_id] = true;
    }

    /**
     * Removes an address from the whitelist for a specific token ID (see Option 2).
     *
     * Access: WHITELISTER_ROLE only accounts
     *
     * @param _user account address
     * @param _id token ID
     */
    function removeWhitelistUser(address _user, uint256 _id)
        public
        nonReentrant
    {
        require(
            hasRole(WHITELISTER_ROLE, msg.sender),
            "PGERC1155: WHITELISTER_ROLE required"
        );
        whitelisted[_user][_id] = false;
    }

    /**
     * Marks an array of addresses as whitelisted to mint a specific token ID (see Option 2).
     *
     * Access: WHITELISTER_ROLE only accounts
     *
     * @param _users array of account addresses
     * @param _id token ID
     */
    function whitelistBatch(address[] memory _users, uint256 _id)
        public
        nonReentrant
    {
        require(
            hasRole(WHITELISTER_ROLE, msg.sender),
            "PGERC1155: WHITELISTER_ROLE required"
        );
        for (uint256 i = 0; i < _users.length; i++) {
            whitelisted[_users[i]][_id] = true;
        }
    }

    /**
     * Removes an array of address from the whitelist for a specific token ID (see Option 2).
     *
     * Access: WHITELISTER_ROLE only accounts
     *
     * @param _users array of account addresses
     * @param _id token ID
     */
    function removeWhitelistBatch(address[] memory _users, uint256 _id)
        public
        nonReentrant
    {
        require(
            hasRole(WHITELISTER_ROLE, msg.sender),
            "PGERC1155: WHITELISTER_ROLE required"
        );
        for (uint256 i = 0; i < _users.length; i++) {
            whitelisted[_users[i]][_id] = false;
        }
    }

    /**
     * Limits the supply of tokens with a specific ID to a given number.
     * Already existing supply for a given token cannot be reduced!
     *
     * Access: MINTER_ROLE only accounts
     *
     * @param _id token ID
     * @param supply new supply
     */
    function setSupply(uint256 _id, uint256 supply) public nonReentrant {
        require(
            hasRole(MINTER_ROLE, msg.sender),
            "PGERC1155: MINTER_ROLE required"
        );
        require(
            supplies[_id] < supply,
            "PGERC1155: can't reduce supply because there might be minted tokens"
        );
        supplies[_id] = supply;
    }

    /**
     * Limits the amont of tokens with a specific ID one user can mint.
     *
     * Access: MINTER_ROLE only accounts
     *
     * @param _id token ID
     * @param newMaxAmount new Max Amount
     */
    function setMaxMintAmount(uint256 _id, uint256 newMaxAmount)
        public
        nonReentrant
    {
        require(
            hasRole(MINTER_CAPPED_ROLE, msg.sender),
            "PGERC1155: MINTER_CAPPED_ROLE required"
        );
        require(
            newMaxAmount < supplies[_id],
            "PGERC1155: can't set maxAmount to be greater than the entire supply"
        );

        maxMintAmount[_id] = newMaxAmount;
    }

    /**
     * Returns true if the sender provides an expanded signature (v, r, s) signed by a WHITELISTER_ROLE.
     *
     * Access: all accounts
     *
     * @param _add address that is part of the verified hash
     * @param _id token ID that is part of the verified hash
     * @param _v v signature parameter
     * @param _r r signature parameter
     * @param _s s signature parameter
     */
    function validateAccessMessage(
        address _add,
        uint256 _id,
        uint8 _v,
        bytes32 _r,
        bytes32 _s
    ) public view returns (bool) {
        bytes32 hash = keccak256(abi.encode(address(this), _add, _id));

        address signer = ecrecover(
            keccak256(
                abi.encodePacked("\x19Ethereum Signed Message:\n32", hash)
            ),
            _v,
            _r,
            _s
        );
        return hasRole(WHITELISTER_ROLE, signer);
    }

    /**
     * Returns the max supply for a given token ID.
     *
     * @param _id tokenID
     */
    function getSupplies(uint256 _id) public view returns (uint256) {
        return supplies[_id];
    }

    function getWhitelistedUser(address add, uint256 _id)
        public
        view
        returns (bool)
    {
        return whitelisted[add][_id];
    }
}

