pragma solidity 0.8.11;








/**
 * Mint and exchange functions for PGERC1155 tokens at predefined prices set in different ERC20 tokens or native coins.
 * The native coins accumulated amount can be transfered to the caller (contract owner) via withdraw() function.
 * The ERC20 tokens will be transferred directly to _receiver account.
 *
 * In order to delegate the mint call to PGERC1155 instance, this contract must have the `MINTER_CAPPED_ROLE` as operated by PGERC1155 contract,
 * a given token ID must have a set supply (changeable by a MINTER_ROLE) and a maxMintAmount must be set in PGERC1155.
 * Also maxMintPerTransaction and maxMintPerWallet ceilings must be set in this contract.
 */
contract PayableMinterV2 is Ownable, ReentrancyGuard, EIP712 {
    using SafeERC20 for IERC20;

    /**
     * Cost in native coins (wei) or specific payment token (ERC20 tokens) per PGERC1155 token ID.
     * Convention: address(0) means native token (e.g. MATIC)
     */
    mapping(uint256 => mapping(address => uint256)) costPerIdAndToken;

    /**
     * Pause flag for mintPrivateSale function
     */
    bool public pausedPrivateSale = true;

    /**
     * Pause flag for mintPresaleA function
     */
    bool public pausedPresaleA = true;

    /**
     * Paused flag for mintPresaleB function
     */
    bool public pausedPresaleB = true;

    /**
     * Paused flag for mintPublicSale function
     */
    bool public pausedPublicSale = true;

    /**
     * Ticket Id for mintPresaleA exchange
     */
    uint256 public presaleTicketA;

    /**
     * Ticket Id for mintPresaleB exchange
     */
    uint256 public presaleTicketB;

    /**
     * Max amount of tokens one account can mint.
     * mapping from address to TokenId to the maximum amount of tokens one user can mint
     */
    mapping(address => mapping(uint256 => uint256)) public maxMintAmountPerUser;

    /**
     * Receiver address for all token transfers.
     */
    address public receiver;

    /**
     * The PGERC1155 instance called for minting.
     */
    PGERC1155 public pgerc1155;

    /**
     * Max amount of tokens one account can mint in one transaction.
     */
    mapping(uint256 => uint256) public maxMintPerTransaction;

    /**
     * Max amount of tokens one account can mint.
     */
    mapping(uint256 => uint256) public maxMintPerWallet;

    /**
     * Stores the number of each token ID a given account has minted since contract deployment.
     */
    mapping(address => mapping(uint256 => uint256)) public mintedAmountPerUser;

    /**
     * Emitted when a mint was executed.
     */
    event Minted(
        address indexed sender,
        uint256 indexed id,
        uint256 amount,
        uint256 cost
    );

    /**
     * Emitted when a setter was executed
     */
    event SetterCalled(address indexed sender, string indexed setterName);

    /**
     * Emitted when withdraw was executed
     */
    event Withdraw(
        address indexed sender,
        uint256 amount
    );

    /**
     * Sets the PGERC1155 instance and the receiver for this contract deployment.
     */
    constructor(PGERC1155 _pgerc1155, address _receiver)
        EIP712("PayableMinterV2", "4")
    {
        require(
            address(_pgerc1155) != address(0),
            "PayableMinterV2: _pgerc1155 is a zero address"
        );
        require(
            _receiver != address(0),
            "PayableMinterV2: _receiver is a zero address"
        );

        pgerc1155 = _pgerc1155;
        receiver = _receiver;
    }

    /**
     * Mints an amount of tokens with given ID to sender wallet (PGERC1155 mintCapped restrictions apply)
     * in exchange for exact cost per token denominated in paymentToken.
     *
     * The maximum amount allowed per address must not be exceeded.
     * Caller must provide the address of the token they want to pay with.
     *
     * @param id token ID to be minted
     * @param paymentToken ERC20 address or zero address for native coins
     * @param amount number of tokens to be minted
     * @param data as data in ERC1155 mint function
     * @param _v v signature parameter
     * @param _r r signature parameter
     * @param _s s signature parameter
     */
    function mintPrivateSale(
        uint256 id,
        address paymentToken,
        uint256 amount,
        bytes memory data,
        uint8 _v,
        bytes32 _r,
        bytes32 _s
    ) public payable nonReentrant {
        require(
            pausedPrivateSale != true,
            "PayableMinterV2: PrivateSale is inactive"
        );
        require(
            costPerIdAndToken[id][paymentToken] != 0,
            "PayableMinterV2: paymentToken is invalid or cost is not set"
        );
        require(
            maxMintAmountPerUser[msg.sender][id] != 0,
            "PayableMinterV2: user is not whitelisted"
        );

        require(
            mintedAmountPerUser[msg.sender][id] + amount <=
                maxMintAmountPerUser[msg.sender][id],
            "PayableMinterV2: user can't mint more than the whitelist ammount"
        );

        require(
            validateSignature(msg.sender, id, 1, _v, _r, _s),
            "PayableMinterV2: invalid signature"
        );

        if (paymentToken == address(0)) {
            require(
                msg.value == costPerIdAndToken[id][paymentToken] * amount,
                "PayableMinterV2: user must send exactly the cost of NFT * amount"
            );
        } else {
            IERC20 token = IERC20(paymentToken);
            token.safeTransferFrom(
                msg.sender,
                receiver,
                costPerIdAndToken[id][paymentToken] * amount
            );
        }

        pgerc1155.mintCapped(msg.sender, id, amount, data);
        mintedAmountPerUser[msg.sender][id] += amount;

        emit Minted(
            msg.sender,
            id,
            amount,
            costPerIdAndToken[id][paymentToken] * amount
        );
    }

    /**
     * Mints an amount of tokens with given ID to sender wallet (PGERC1155 mintCapped restrictions apply)
     * in exchange for exact cost per token made in paymentToken + tiketID.
     *
     * Caller must provide the correct address of the token they want to pay with.
     *
     * @param id token ID to be minted
     * @param paymentToken ERC20 address or zero address for native coins
     * @param amount number of tokens to be minted
     * @param data as data in ERC1155 mint function
     * @param _v v signature parameter
     * @param _r r signature parameter
     * @param _s s signature parameter
     */
    function mintPresaleA(
        uint256 id,
        address paymentToken,
        uint256 amount,
        uint256 ticketId,
        bytes memory data,
        uint8 _v,
        bytes32 _r,
        bytes32 _s
    ) public payable nonReentrant {
        require(
            pausedPresaleA != true,
            "PayableMinterV2: PresaleA is inactive"
        );
        require(
            ticketId == presaleTicketA,
            "PayableMinterV2: ticketId != presaleTicketA id"
        );
        require(
            costPerIdAndToken[id][paymentToken] != 0,
            "PayableMinterV2: paymentToken is invalid or cost is not set"
        );

        require(
            pgerc1155.balanceOf(msg.sender, ticketId) >= amount,
            "PayableMinterV2: not enough tickets A for this mint"
        );

        require(
            validateSignature(msg.sender, id, ticketId, _v, _r, _s),
            "PayableMinterV2: invalid signature"
        );

        pgerc1155.safeTransferFrom(
            msg.sender,
            receiver,
            ticketId,
            amount,
            "0x"
        );

        if (paymentToken == address(0)) {
            require(
                msg.value == costPerIdAndToken[id][paymentToken] * amount,
                "PayableMinterV2: user must send exactly the cost of NFT * amount"
            );
        } else {
            IERC20 token = IERC20(paymentToken);
            token.safeTransferFrom(
                msg.sender,
                receiver,
                costPerIdAndToken[id][paymentToken] * amount
            );
        }

        pgerc1155.mintCapped(msg.sender, id, amount, data);
        mintedAmountPerUser[msg.sender][id] += amount;

        emit Minted(
            msg.sender,
            id,
            amount,
            costPerIdAndToken[id][paymentToken] * amount
        );
    }

    /**
     * Mints an amount of tokens with given ID to sender wallet (PGERC1155 mintCapped restrictions apply)
     * in exchange for exact cost per token made in paymentToken + tiketID
     *
     * Caller must provide the correct address of the token they want to pay with.
     *
     * @param id token ID to be minted
     * @param paymentToken ERC20 address or zero address for native coins
     * @param amount number of tokens to be minted
     * @param data as data in ERC1155 mint function
     * @param _v v signature parameter
     * @param _r r signature parameter
     * @param _s s signature parameter
     */
    function mintPresaleB(
        uint256 id,
        address paymentToken,
        uint256 amount,
        uint256 ticketId,
        bytes memory data,
        uint8 _v,
        bytes32 _r,
        bytes32 _s
    ) public payable nonReentrant {
        require(
            pausedPresaleB != true,
            "PayableMinterV2: PresaleB is inactive"
        );
        require(
            ticketId == presaleTicketB,
            "PayableMinterV2: ticketId != presaleTicketB id"
        );
        require(
            costPerIdAndToken[id][paymentToken] != 0,
            "PayableMinterV2: paymentToken is invalid or cost is not set"
        );

        require(
            pgerc1155.balanceOf(msg.sender, ticketId) >= amount,
            "PayableMinterV2: not enough tickets B for this mint"
        );

        require(
            validateSignature(msg.sender, id, ticketId, _v, _r, _s),
            "PayableMinterV2: invalid signature"
        );

        pgerc1155.safeTransferFrom(
            msg.sender,
            receiver,
            ticketId,
            amount,
            "0x"
        );

        if (paymentToken == address(0)) {
            require(
                msg.value == costPerIdAndToken[id][paymentToken] * amount,
                "PayableMinterV2: user must send exactly the cost of NFT * amount"
            );
        } else {
            IERC20 token = IERC20(paymentToken);
            token.safeTransferFrom(
                msg.sender,
                receiver,
                costPerIdAndToken[id][paymentToken] * amount
            );
        }

        pgerc1155.mintCapped(msg.sender, id, amount, data);
        mintedAmountPerUser[msg.sender][id] += amount;

        emit Minted(
            msg.sender,
            id,
            amount,
            costPerIdAndToken[id][paymentToken] * amount
        );
    }

    /**
     * Mints an amount of tokens with given ID to sender wallet (PGERC1155 mintCapped restrictions apply)
     * in exchange for exact cost per token made in paymentToken.
     *
     * The maximum amount allowed per address must not be exceeded.
     *
     * User must provide the correct address of the token they want to pay with.
     *
     * @param id token ID to be minted
     * @param paymentToken ERC20 address or zero address for native coins
     * @param amount number of tokens to be minted
     * @param data as data in ERC1155 mint function
     * @param _v v signature parameter
     * @param _r r signature parameter
     * @param _s s signature parameter
     */
    function mintPublicSale(
        uint256 id,
        address paymentToken,
        uint256 amount,
        bytes memory data,
        uint8 _v,
        bytes32 _r,
        bytes32 _s
    ) public payable nonReentrant {
        require(
            pausedPublicSale != true,
            "PayableMinterV2: PublicSale is inactive"
        );
        require(
            costPerIdAndToken[id][paymentToken] != 0,
            "PayableMinterV2: paymentToken is invalid or cost is not set"
        );
        require(
            mintedAmountPerUser[msg.sender][id] + amount <=
                maxMintPerWallet[id],
            "PayableMinterV2: user can't mint more than maxMintPerWallet"
        );

        require(
            amount <= maxMintPerTransaction[id],
            "PayableMinterV2: user can't mint more in one transaction than maxMintPerTransaction"
        );

        require(
            validateSignature(
                msg.sender,
                id,
                0,
                _v,
                _r,
                _s
            ),
            "PayableMinterV2: invalid signature"
        );

        if (paymentToken == address(0)) {
            require(
                msg.value == costPerIdAndToken[id][paymentToken] * amount,
                "PayableMinterV2: user must send exactly the cost of NFT * amount"
            );
        } else {
            IERC20 token = IERC20(paymentToken);
            token.safeTransferFrom(
                msg.sender,
                receiver,
                costPerIdAndToken[id][paymentToken] * amount
            );
        }

        pgerc1155.mintCapped(msg.sender, id, amount, data);
        mintedAmountPerUser[msg.sender][id] += amount;

        emit Minted(
            msg.sender,
            id,
            amount,
            costPerIdAndToken[id][paymentToken] * amount
        );
    }

    /**
     * Sets the max amount of tokens with given ID that one account may mint in a single transaction.
     *
     * Access: Contract Owner
     *
     * @param id token ID
     * @param maxAmount the maximum number
     */
    function setMaxMintPerTransaction(uint256 id, uint256 maxAmount)
        external
        onlyOwner
    {
        require(maxAmount > 0, "PayableMinterV2: maxAmount should be > 0");

        maxMintPerTransaction[id] = maxAmount;
        emit SetterCalled(msg.sender, "setMaxMintPerTransaction");
    }

    /**
     * Sets the max amount of tokens with given ID that one account may mint.
     *
     * Access: Contract Owner
     *
     * @param id token ID
     * @param maxAmount the maximum number
     */
    function setMaxMintPerWallet(uint256 id, uint256 maxAmount)
        external
        onlyOwner
    {
        require(maxAmount > 0, "PayableMinterV2: maxAmount should be > 0");

        maxMintPerWallet[id] = maxAmount;
        emit SetterCalled(msg.sender, "setMaxMintPerWallet");
    }

    /**
     * Transfers the total native coin balance to contract's owner account.
     * The balance must be > 0 so a zero transfer is avoided.
     *
     * Access: Contract Owner
     */
    function withdraw() public onlyOwner nonReentrant {
        uint256 balance = address(this).balance;
        require(balance != 0, "PayableMinterV2: contract balance is zero");
        _sendViaCall(payable(owner()), balance);
        emit Withdraw(msg.sender, balance);
    }

    /**
     * @dev Function to transfer coins (the native cryptocurrency of the platform, i.e.: ETH)
     * from this contract to the specified address.
     *
     * @param to the address to transfer the coins to
     * @param amount amount (in wei)
     */
    function _sendViaCall(address payable to, uint256 amount) private {
        (bool sent, ) = to.call{value: amount}("");
        require(sent, "PayableMinterV2: failed to send amount");
    }

    /**
     * Returns the cost to mint one token of a given ID in a specific payment method (token).
     * @param id  PGERC1155 token ID
     * @param token specific payment method (token)
     */
    function getCost(uint256 id, address token)
        external
        view
        returns (uint256)
    {
        return costPerIdAndToken[id][token];
    }

    /**
     * Sets the max amount of tokens with given ID that one account can mint in total.
     *
     * Access: Contract Owner
     *
     * @param user user address
     * @param id token ID
     * @param quantity the maximum amount
     */
    function setMaxMintAmountPerUser(
        address user,
        uint256 id,
        uint256 quantity
    ) external onlyOwner {
        maxMintAmountPerUser[user][id] = quantity;
        emit SetterCalled(msg.sender, "setMaxMintAmountPerUser");
    }

    /**
     * Increments the number of PGERC1155 tokens (with specific token ID) a batch of addresses is allowed to mint.
     *
     * Access: Contract Owner
     *
     * @param users array of account addresses
     * @param id PGERC1155 token ID
     */
    function inc1MaxMintAmountPerUserBatch(address[] memory users, uint256 id)
        external
        onlyOwner
    {
        for (uint256 index = 0; index < users.length; index++) {
            maxMintAmountPerUser[users[index]][id] += 1;
        }
        emit SetterCalled(msg.sender, "inc1MaxMintAmountPerUserBatch");
    }

    /**
     * Sets the "paused" state for the Private Sale.
     *
     * Access: Contract Owner
     *
     * @param state true means stopped, false means active
     */
    function setPausedPrivateSale(bool state) external onlyOwner {
        pausedPrivateSale = state;
        emit SetterCalled(msg.sender, "setPausedPrivateSale");
    }

    /**
     * Sets the "paused" state for Presale A.
     *
     * Access: Contract Owner
     *
     * @param state true means stopped, false means active
     */
    function setPausedPresaleA(bool state) external onlyOwner {
        pausedPresaleA = state;
        emit SetterCalled(msg.sender, "setPausedPresaleA");
    }

    /**
     * Sets the "paused" state for Presale B.
     *
     * Access: Contract Owner
     *
     * @param state true means stopped, false means active
     */
    function setPausedPresaleB(bool state) external onlyOwner {
        pausedPresaleB = state;
        emit SetterCalled(msg.sender, "setPausedPresaleB");
    }

    /**
     * Sets the "paused" state for the Public Sale.
     *
     * Access: Contract Owner
     *
     * @param state true means stopped, false means active
     */
    function setPausedPublicSale(bool state) external onlyOwner {
        pausedPublicSale = state;
        emit SetterCalled(msg.sender, "setPausedPublicSale");
    }

    /**
     * Sets the cost for a PGERC1155 ID, denominated in tokens of the given ERC20 address.
     *
     * Access: Contract Owner
     *
     * If cost = 0 for a specific ID and token address, the users cannot mint.
     *
     * @param id PGERC1155 token ID
     * @param token address of the ERC20 token used for payment
     * @param cost price in smallest unit (e.g. wei)
     */
    function setCostPerIdAndToken(
        uint256 id,
        address token,
        uint256 cost
    ) external onlyOwner {
        costPerIdAndToken[id][token] = cost;
        emit SetterCalled(msg.sender, "setCostPerIdAndToken");
    }

    /**
     * Sets the receiving address of the ERC20 mint transfers.
     *
     * Access: Contract Owner
     *
     * @param _receiver receiver of the ERC20 tokens
     */
    function setReceiver(address _receiver) external onlyOwner {
        require(
            _receiver != address(0),
            "PayableMinterV2: _receiver is a zero address"
        );
        receiver = _receiver;
        emit SetterCalled(msg.sender, "setReceiver");
    }

    /**
     * Sets the Ticket that is used for Presale A.
     *
     * Access: Contract Owner
     *
     * @param id token id of Ticket A in PGERC1155
     */
    function setPresaleTicketA(uint256 id) external onlyOwner {
        require(id != presaleTicketB, "PayableMinterV2: presaleTicketA cannot be equal to presaleTicketB");
        require(id != 0 && id != 1, "PayableMinterV2: presaleTicketA cannot be equal to 0 or 1");
        presaleTicketA = id;
        emit SetterCalled(msg.sender, "setPresaleTicketA");
    }

    /**
     * Sets the Ticket that is used for Presale B.
     *
     * Access: Contract Owner
     *
     * @param id id of Ticket B in PGERC1155
     */
    function setPresaleTicketB(uint256 id) external onlyOwner {
        require(id != presaleTicketA, "PayableMinterV2: presaleTicketB cannot be equal to presaleTicketA");
        require(id != 0 && id != 1, "PayableMinterV2: presaleTicketB cannot be equal to 0 or 1");
        presaleTicketB = id;
        emit SetterCalled(msg.sender, "setPresaleTicketB");
    }

    /**
     * Used by signed versions of the PayableMinterV2 functions to validate a signature.
     * A signature is valid if the signer address has signed the first three parameters and
     * it is currently granted with WHITELISTER_ROLE as specified by the PGERC1155 contract.
     *
     * @param _add sender address
     * @param tokenA token ID
     * @param ticketId token ID (can be zero for normal Mint)
     * @param _v v signature parameter
     * @param _r r signature parameter
     * @param _s s signature parameter
     * @return true if signature valid
     */
    function validateSignature(
        address _add,
        uint256 tokenA,
        uint256 ticketId,
        uint8 _v,
        bytes32 _r,
        bytes32 _s
    ) public view returns (bool) {
        bytes32 digest = _hashTypedDataV4(keccak256(abi.encode(
            keccak256("SigValidation(address verifyingContract,address callerAddress,uint256 token,uint256 tokenID)"),
            address(this),
            _add,
            tokenA,
            ticketId
            ))
        );

        address signer = ECDSA.recover(digest, _v, _r, _s);
        bytes32 role = pgerc1155.WHITELISTER_ROLE();
        return pgerc1155.hasRole(role, signer);
    }
}
