    "@murphy-labs/strateg-protocol-libraries/contracts/DataTypes.sol": {
      "content": "// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.20;

library DataTypes {
    struct VaultConfigurationMap {
        //bit 0-15: LTV
        //bit 16-31: Liq. threshold
        //bit 32-47: Liq. bonus
        //bit 48-55: Decimals
        //bit 56: reserve is active
        //bit 57: reserve is frozen
        //bit 58: borrowing is enabled
        //bit 59: stable rate borrowing enabled
        //bit 60: asset is paused
        //bit 61: borrowing in isolation mode is enabled
        //bit 62: siloed borrowing enabled
        //bit 63: flashloaning enabled
        //bit 64-79: reserve factor
        //bit 80-115 borrow cap in whole tokens, borrowCap == 0 => no cap
        //bit 116-151 supply cap in whole tokens, supplyCap == 0 => no cap
        //bit 152-167 liquidation protocol fee
        //bit 168-175 eMode category
        //bit 176-211 unbacked mint cap in whole tokens, unbackedMintCap == 0 => minting disabled
        //bit 212-251 debt ceiling for isolation mode with (ReserveConfiguration::DEBT_CEILING_DECIMALS) decimals
        //bit 252-255 unused
        uint256 data;
    }

    enum BlockExecutionType {
        ENTER,
        EXIT,
        HARVEST
    }

    enum DynamicParamsType {
        NONE,
        PORTAL_SWAP
    }

    struct DynamicSwapParams {
        address fromToken;
        address toToken;
        uint256 value;
        bool isPercent;
    }

    struct DynamicSwapData {
        uint8 route;
        address sourceAsset;
        address approvalAddress;
        address targetAsset;
        uint256 amount;
        bytes data;
    }

    struct OracleState {
        address vault;
        address[] tokens;
        uint256[] tokensAmount;
    }

    struct StrategVaultInfo {
        address owner;
        address asset;
        uint256 totalSupply;
        uint256 totalAssets;
        uint256 gasAvailable;
        uint256 bufferAssetsAvailable;
        uint256 bufferSize;
        uint256 bufferDerivation;
        uint256 lastHarvestIndex;
        uint256 currentVaultIndex;
        uint256 harvestFee;
        uint256 creatorFee;
        uint256 minSupplyForActivation;
    }

    struct StrategyBlockExecutionInfo {
        bool dynParamsNeeded;
        DynamicParamsType dynParamsType;
        bytes dynParamsInfo;
        address blockAddr;
        OracleState oracleStatus;
    }

    struct StrategVaultExecutionInfo {
        uint256 blocksLength;
        OracleState startOracleStatus;
        StrategyBlockExecutionInfo[] blocksInfo;
    }

    struct PositionManagerRebalanceExecutionInfo {
        address vault;
        uint256 blockIndex;
        DynamicParamsType dynParamsType;
        bytes dynParamsInfo;
        StrategVaultExecutionInfo partialExit;
        StrategVaultExecutionInfo partialEnter;
    }

    struct PermitParams {
        uint256 deadline;
        uint8 v;
        bytes32 r;
        bytes32 s;
    }

    struct Permit2Params {
        uint256 deadline;
        uint256 nonce;
        bytes signature;
    }
}
"
