    "contracts/StrategUserInteractions.sol": {
      "content": "// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { IStrategVault, StrategVaultSettings } from "./interfaces/IStrategVault.sol";
import { IStrategVaultFactory } from "./interfaces/IStrategVaultFactory.sol";
import { LibPermit } from "@murphy-labs/strateg-protocol-libraries/contracts/LibPermit.sol";
import { ERC2771Context } from "@gelatonetwork/relay-context/contracts/vendor/ERC2771Context.sol";

/**
 * @title Strateg interactions helper
 * @notice Additional contract to implement permit1, permit2 anf Gelato relay
 */
contract StrategUserInteractions is ERC2771Context {
    using SafeERC20 for IERC20;

    IStrategVaultFactory factory;
    address permit2;
    address relayer;

    constructor(
        address _relayer,
        address _permit2,
        address _factory
    ) ERC2771Context(relayer) {
        factory = IStrategVaultFactory(_factory);
        permit2 = _permit2;
        relayer = _relayer;
    }

    /**
     * Vault owner actions
     */
    function deployNewVault(
        string memory _name,
        string memory _symbol,
        address _asset,
        uint256 _strategy,
        uint256 _bufferSize,
        uint256 _creatorFees,
        uint256 _harvestFees,
        string memory _ipfsHash
    ) public {
        factory.deployNewVault(
            _name,
            _symbol,
            _msgSender(),
            _asset,
            _strategy,
            _bufferSize,
            _creatorFees,
            _harvestFees,
            _ipfsHash
        );
    }

    function setVaultStrategy(
        address vault,
        address[] memory _positionManagers,
        address[] memory _stratBlocks,
        bytes[] memory _stratBlocksParameters,
        address[] memory _harvestBlocks,
        bytes[] memory _harvestBlocksParameters
    ) public {
        factory.setVaultStrat(
            _msgSender(),
            vault,
            _positionManagers,
            _stratBlocks,
            _stratBlocksParameters,
            _harvestBlocks,
            _harvestBlocksParameters
        );
    }

    function editVaultParams(
        address _vault,
        StrategVaultSettings[] memory settings,
        bytes[] memory data
    ) public {
        factory.editVaultParams(
            _msgSender(),
            _vault,
            settings,
            data
        );
    }

    /**
     * Vault invest actions
     */
    function vaultDeposit(
        address _vault,
        uint256 _assets,
        bytes memory _permitParams
    ) public {

        address sender = _msgSender();
        IStrategVault vault = IStrategVault(_vault);
        IERC20 asset = IERC20(vault.asset());

        if(_permitParams.length != 0) {
            LibPermit.executePermit(address(asset), sender, _assets, _permitParams);
        }

        asset.safeTransferFrom(sender, address(this), _assets);
        asset.safeIncreaseAllowance(_vault, _assets);

        vault.deposit(_assets, sender);
    }

    function vaultDepositWithPermit2(
        address _vault,
        uint256 _assets,
        bytes memory _permitParams
    ) public {
        address sender = _msgSender();
        IStrategVault vault = IStrategVault(_vault);
        IERC20 asset = IERC20(vault.asset());
        LibPermit.executeTransferFromPermit2(
            permit2,
            address(asset), 
            sender, 
            address(this), 
            _assets, 
            _permitParams
        );

        asset.safeIncreaseAllowance(_vault, _assets);

        vault.deposit(_assets, sender);
    }

    function vaulWithdraw(
        address _vault,
        uint256 _shares,
        bytes memory _permitParams
    ) public {
        address sender = _msgSender();
        IStrategVault vault = IStrategVault(_vault);
        LibPermit.executePermit(address(vault), sender, _shares, _permitParams);

        vault.transferFrom(sender, address(this), _shares);
        vault.redeem(_shares, sender, address(this));
    }
}
"
    },
    "contracts/interfaces/IStrategVault.sol": {
      "content": "// SPDX-License-Identifier: AGPL-3.0
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC4626.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Permit.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import {DataTypes} from "@murphy-labs/strateg-protocol-libraries/contracts/DataTypes.sol";

enum StrategVaultUpdateType {
    Transfer,
    MiddlewareInit,
    NewTimelockParams,
    NewDepositLimits,
    NewHoldingParams,
    NewBufferParams,
    EditWhitelist,
    NewFeeParams,
    StrategyInitialized
}

enum StrategVaultSettings {
    TimelockParams,
    DepositLimits,
    HoldingParams,
    EditWhitelist,
    FeeParams,
    BufferParams
}

interface IStrategVault  {
    event StrategVaultUpdate(StrategVaultUpdateType indexed update, bytes data);

    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(address indexed owner, address indexed spender, uint256 value);
    event Deposit(address indexed sender, address indexed owner, uint256 assets, uint256 shares);
    event Withdraw(
        address indexed sender, address indexed receiver, address indexed owner, uint256 assets, uint256 shares
    );

    event StrategyInitialized(
        address[] _stratBlocksIndex,
        bytes[] _stratBlocksParameters,
        address[] _harvestBlocksIndex,
        bytes[] _harvestBlocksParameters
    );

    function initialize(
        address _owner,
        address _erc3525,
        string memory _name,
        string memory _symbol,
        address _asset,
        uint256 _strategy,
        uint256 _bufferSize,
        uint256 _creatorFee,
        uint256 _harvestFee
    ) external;

    function transferFrom(address from, address to, uint256 amount) external;

    function setStrat(
        address[] memory _positionManagers,
        address[] memory _stratBlocks,
        bytes[] memory _stratBlocksParameters,
        address[] memory _harvestBlocks,
        bytes[] memory _harvestBlocksParameters
    ) external;

    function configuration() external view returns (DataTypes.VaultConfigurationMap memory config);

    function totalSupply() external view returns (uint256);
    function totalAssets() external view returns (uint256);
    function userMinDeposit() external view returns (uint256);
    function userMaxDeposit() external view returns (uint256);
    function vaultMinDeposit() external view returns (uint256);
    function vaultMaxDeposit() external view returns (uint256);

    function registry() external view returns (address);
    function buffer() external view returns (address);
    function feeCollector() external view returns (address);
    function factory() external view returns (address);
    function asset() external view returns (address);

    function harvest(uint256[] memory _dynParamsIndex, bytes[] memory _dynParams) external;

    /**
     * @dev See {IERC4262-deposit}.
     */
    function deposit(uint256 assets, address receiver) external returns (uint256);

    /**
     * @dev See {IERC4262-mint}.
     */
    function mint(uint256 shares, address receiver) external returns (uint256);

    /**
     * @dev See {IERC4262-withdraw}.
     */
    function withdraw(uint256 assets, address receiver, address owner) external returns (uint256);

    /**
     * @dev See {IERC4262-redeem}.
     */
    function redeem(uint256 shares, address receiver, address owner) external returns (uint256);

    function permit(address owner, address spender, uint256 amount, uint256 deadline, uint8 _v, bytes32 _r, bytes32 _s)
        external;

    function getStrat()
        external
        view
        returns (
            address[] memory _strategyBlocks,
            bytes[] memory _strategyBlocksParameters,
            address[] memory _harvestBlocks,
            bytes[] memory _harvestBlocksParameters
        );

    function owner() external view returns (address);
    function rebalance(
        uint256[] memory _dynParamsIndexEnter,
        bytes[] memory _dynParamsEnter,
        uint256[] memory _dynParamsIndexExit,
        bytes[] memory _dynParamsExit
    ) external;

    function stopStrategy(uint256[] memory _dynParamsIndex, bytes[] memory _dynParams) external;

    function withdrawalRebalance(
        address _user,
        uint256 _amount,
        uint256[] memory _dynParamsIndexEnter,
        bytes[] memory _dynParamsEnter,
        uint256[] memory _dynParamsIndexExit,
        bytes[] memory _dynParamsExit
    ) external;

    function partialStrategyEnter(uint256 _from, uint256[] memory _dynParamsIndex, bytes[] memory _dynParams)
        external;

    function partialStrategyExit(uint256 _to, uint256[] memory _dynParamsIndex, bytes[] memory _dynParams) external;

        function setDepositLimits(
        uint256 _minUserDeposit,
        uint256 _maxUserDeposit,
        uint256 _minVaultDeposit,
        uint256 _maxVaultDeposit
    ) external ;

    function whitelist(bool _add, address addr) external;
    function setTimelockParams(bool _enabled, uint256 _duration) external;
    function setHoldingParams(address _token, uint256 _amount) external;
    function setFeeParams(uint256 _creatorFees, uint256 _harvestFees) external;
    function setBufferParams(uint256 _bufferSize, uint256 _bufferDerivation) external;

    function emergencyExecution(address[] memory _targets, bytes[] memory _datas) external;
}
"
    },
    "contracts/interfaces/IStrategVaultFactory.sol": {
      "content": "// SPDX-License-Identifier: AGPL-3.0
pragma solidity ^0.8.0;

import { StrategVaultSettings } from "./IStrategVault.sol";

interface IStrategVaultFactory {
    struct PermitParams {
        uint256 deadline;
        uint8 v;
        bytes32 r;
        bytes32 s;
    }

    event NewVault(
        uint256 indexed id,
        address indexed addr,
        string name,
        string symbol,
        address asset,
        address indexed owner,
        address erc3525,
        address implementation,
        string ipfsHash
    );

    event NewVaultImplementation(uint256 indexed version, address implementation);

    event NewERC2535Implementation(uint256 indexed version, address implementation);

    function vaultsLength() external view returns (uint256);
    function vaults(uint256) external view returns (address);
    function relayer() external view returns (address);
    function protocolFee() external view returns (uint256);

    function deployNewVault(
        string memory _name,
        string memory _symbol,
        address _owner,
        address _asset,
        uint256 _strategy,
        uint256 _bufferSize,
        uint256 _creatorFees,
        uint256 _harvestFees,
        string memory ipfsHash
    ) external;

    function setVaultStrat(
        address user,
        address vault,
        address[] memory _positionManagers,
        address[] memory _stratBlocks,
        bytes[] memory _stratBlocksParameters,
        address[] memory _harvestBlocks,
        bytes[] memory _harvestBlocksParameters
    ) external;

    function editVaultParams(
        address user,
        address vault,
        StrategVaultSettings[] memory settings,
        bytes[] calldata data
    ) external;

    function getBatchVaultAddresses(uint256[] memory indexes) external view returns (address[] memory);
