    "contracts/MetaBingoStaking.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.13;

import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {IERC721A} from "erc721a/contracts/IERC721A.sol";
import {IERC721Receiver} from "@openzeppelin/contracts/token/ERC721/IERC721Receiver.sol";
import {EnumerableSet} from "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract MetaBingoStaking is Ownable, IERC721Receiver {
    using EnumerableSet for EnumerableSet.UintSet;

    // Contract Addresses
    IERC721A public immutable erc721Address;
    IERC20 public immutable erc20Address;

    uint256 private constant IntervalReward = 57900000000000;

    bool public isStopped;
    uint256 public stopTime;
    // Staking Tracking
    mapping(address => EnumerableSet.UintSet) private _stakings;

    mapping(address => mapping(uint256 => uint256)) public _stakingTimes;

    // Events
    event Stake(address addr, uint256[] tokenIds);
    event Unstake(address addr, uint256[] tokenIds);
    event Claim(address addr, uint256 amount);

    constructor(address _erc721Address, address _erc20Address) {
        erc721Address = IERC721A(_erc721Address);
        erc20Address = IERC20(_erc20Address);
    }

    /**
     * Track stakings of an account
     */
    function stakingsOf(
        address account
    ) external view returns (uint256[] memory) {
        EnumerableSet.UintSet storage stakingSet = _stakings[account];
        uint256[] memory tokenIds = new uint256[](stakingSet.length());
        for (uint256 i; i < stakingSet.length(); i++) {
            tokenIds[i] = stakingSet.at(i);
        }
        return tokenIds;
    }

    /**
     * Get the stakings information as an array of packed byte strings
     */
    function stakingsOfAdvanced(
        address account
    ) external view returns (bytes[] memory) {
        EnumerableSet.UintSet storage stakingSet = _stakings[account];
        bytes[] memory stakingTimes = new bytes[](stakingSet.length());
        for (uint256 i; i < stakingSet.length(); i++) {
            uint256 tokenId = stakingSet.at(i);
            stakingTimes[i] = abi.encodePacked(
                tokenId,
                _stakingTimes[account][tokenId]
            );
        }
        return stakingTimes;
    }

    /**
     * Stake NFTs into the contract
     */
    function stake(uint256[] calldata tokenIds) external {
        require(!isStopped, "This staking contract has been deprecated");
        for (uint256 i; i < tokenIds.length; i++) {
            IERC721A(erc721Address).safeTransferFrom(
                msg.sender,
                address(this),
                tokenIds[i],
                ""
            );
            _stakingTimes[msg.sender][tokenIds[i]] = block.timestamp;
            _stakings[msg.sender].add(tokenIds[i]);
        }
        emit Stake(msg.sender, tokenIds);
    }

    /**
    @notice unstaking a token that has unrealized BNG forfeits the BNG associated
     * with the token(s) being unstaked. This was done intentionally as a holder may
     * not to pay the gas costs associated with claiming BNG. Please see unstakeAndClaim
     * to also claim BNG.
     *
     * Unstaking your NFT transfers ownership back to the address that staked it.
     * When an NFT is unstaked, you will no longer be earning BNG.
     */
    function unstake(uint256[] calldata tokenIds) public {
        for (uint256 i; i < tokenIds.length; i++) {
            require(
                _stakings[msg.sender].contains(tokenIds[i]),
                "Token not staked"
            );
            _stakings[msg.sender].remove(tokenIds[i]);
            IERC721A(erc721Address).safeTransferFrom(
                address(this),
                msg.sender,
                tokenIds[i],
                ""
            );
        }
        emit Unstake(msg.sender, tokenIds);
    }

    /**
     * @dev unstakeAndClaim will unstake the token and realize the BNG that it has earned.
     * If you are not interested in earning BNG you can call unstaske and save the gas.
     * Unstaking your NFT transfers ownership back to the address that staked it.
     * When an NFT is unstaked you will no longer be earning BNG.
     */
    function unstakeAndClaim(uint256[] calldata _tokenIds) external {
        claimBNG();
        unstake(_tokenIds);
    }

    /**
     * @dev BNG earned as a result of staking but not yet claimed/realized
     */
    function unclaimedBNG() external view returns (uint256) {
        uint256 stakedTime;
        EnumerableSet.UintSet storage stakingSet = _stakings[msg.sender];
        if (!isStopped) {
            for (uint256 i = 0; i < stakingSet.length(); i++) {
                stakedTime +=
                    block.timestamp -
                    _stakingTimes[msg.sender][stakingSet.at(i)];
            }
        } else {
            for (uint256 i = 0; i < stakingSet.length(); i++) {
                stakedTime +=
                    stopTime -
                    _stakingTimes[msg.sender][stakingSet.at(i)];
            }
        }
        return stakedTime * IntervalReward;
    }

    /**
     * @dev claim earned BNG without unstaking
     */
    function claimBNG() public {
        EnumerableSet.UintSet storage stakingSet = _stakings[msg.sender];
        require(stakingSet.length() > 0, "No tokens are staked");

        uint256 stakedTime;
        if (!isStopped) {
            for (uint256 i = 0; i < stakingSet.length(); i++) {
                stakedTime +=
                    block.timestamp -
                    _stakingTimes[msg.sender][stakingSet.at(i)];
                _stakingTimes[msg.sender][stakingSet.at(i)] = block.timestamp;
            }
        } else {
            for (uint256 i = 0; i < stakingSet.length(); i++) {
                stakedTime +=
                    stopTime -
                    _stakingTimes[msg.sender][stakingSet.at(i)];
                _stakingTimes[msg.sender][stakingSet.at(i)] = stopTime;
            }
        }

        uint256 totalReward = stakedTime * IntervalReward;
        require(totalReward > 0, "Nothing to claim");
        require(
            erc20Address.balanceOf(address(this)) > totalReward,
            "Not enough tokens in the contract. Contact Team."
        );
        bool success = erc20Address.transfer(msg.sender, totalReward);
        require(success, "Error while transfering tokens");
        emit Claim(msg.sender, totalReward);
    }

    /**
     * Allows contract owner to withdraw some token from the contract
     */
    function withdrawTokens(IERC20 _erc20Address) external onlyOwner {
        uint256 tokenSupply = _erc20Address.balanceOf(address(this));
