    "src/ERC20.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.17;

import "./interfaces/IERC20.sol";
import "./libraries/StringUtils.sol";

contract ERC20 is IERC20Metadata {

    string token_name;
    string sym;
    mapping(address => uint256) internal balances;
    uint256 total_supply = 0;

    address caller;

    mapping(address => mapping(address => uint256)) internal allowances;

    modifier onlyMinter {
        require (msg.sender == caller ,"only minter can mint");
        _;
    }

    constructor (string memory name, string memory symbol) {
        token_name = name;
        sym = symbol;
        caller = msg.sender;
    }

    function name() external view returns (string memory) {
        return token_name;
    }

    function symbol() external view returns (string memory) {
        return sym;
    }

    function decimals() external view returns (uint8) {
        return 18;
    }

    function totalSupply() external view returns (uint256) {
        return total_supply ;
    }

    function balanceOf(address account) external view returns (uint256) {
        return balances[account];
    }

    function transfer(address to, uint256 amount) external returns (bool) {
        require (balances[msg.sender] >= amount,"insufficient balance");
        balances[msg.sender] -= amount ;
        balances[to] += amount;
        emit Transfer(msg.sender, to, amount);
        return true;
    }

    function allowance(address owner, address spender) external view returns (uint256)
    {
        return allowances[owner][spender];
    }

    function approve(address spender, uint256 amount) external returns (bool) {
        allowances[msg.sender][spender] = 0;
        allowances[msg.sender][spender] = amount;
        emit Approval(msg.sender, spender, amount);
        return true;
    }

    function transferFrom(
        address from,
        address to,
        uint256 amount
    ) external returns (bool) {

        require (this.allowance(from, msg.sender) >= amount,"insufficient allowance");
        allowances[from][msg.sender] -= amount;

        require (balances[from] >= amount,"insufficient balance");
        balances[from] -= amount ;
        balances[to] += amount;
        emit Transfer(from, to, amount);
        return true;
    }

    function mint(address to, uint256 amount) onlyMinter external {
        total_supply += amount;
        balances[to] += amount;
    }
}"
