import "@chainlink/contracts/src/v0.8/interfaces/AggregatorInterface.sol";
import "@openzeppelin/contracts/utils/Strings.sol";

abstract contract PriceFeed {
    using Strings for uint256;
    AggregatorInterface immutable AGGREGATOR_POL_USD;

    uint256 public POL_USD;
    uint256 public USD_POL;

    uint256 public lastUpdatePrice;

    constructor(
        address aggregatorAddr
    ) {
        AGGREGATOR_POL_USD = AggregatorInterface(aggregatorAddr);
        _updateMaticePrice();
    }

    function USD_POL_Multiplier(uint256 num) public view returns(uint256) {
        return num * USD_POL;
    }

    function USD_POL_Multiplier_String(uint256 num) public view returns(string memory) {
        return string.concat("s", (num * USD_POL).toString());
    }

    function get_POL_USD() private view returns(uint256) {
        return uint256(AGGREGATOR_POL_USD.latestAnswer());
    }

    function _updateMaticePrice() internal {
        uint256 POL_USD_8 = get_POL_USD();
        POL_USD = POL_USD_8 * 10 ** 10;
        USD_POL = 10 ** 26 / POL_USD_8;
    }

    function updateMaticPrice() public {
        require(
            block.timestamp > lastUpdatePrice + 4 hours,
            "time exception"
        );
        lastUpdatePrice = block.timestamp;
        _updateMaticePrice();
    }
}"
