    function toString(uint256 value) private pure returns (string memory) {
        // Inspired by OraclizeAPI's implementation - MIT license
        // https://github.com/oraclize/ethereum-api/blob/b42146b063c7d6ee1358846c198246239e9360e8/oraclizeAPI_0.4.25.sol

        if (value == 0) {
            return "0";
        }
        uint256 temp = value;
        uint256 digits;
        while (temp != 0) {
            digits++;
            temp /= 10;
        }
        bytes memory buffer = new bytes(digits);
        while (value != 0) {
            digits -= 1;
            buffer[digits] = bytes1(uint8(48 + uint256(value % 10)));
            value /= 10;
        }
        return string(buffer);
    }

    function pluck(
        uint256 tokenId,
        string memory keyPrefix,
        string[] memory sourceArray
    ) private view returns (string memory) {

        uint256 rand = uint256(keccak256(abi.encode(_bagRandomValuesMapping[tokenId], keyPrefix, tokenId)));

        string memory output = sourceArray[rand % sourceArray.length];

        uint256 greatness = rand % 100;
        if (greatness > 66) {
            output = string(
                abi.encodePacked(output, " ", suffixes[rand % suffixes.length])
            );
        }
        if (greatness >= 85) {
            string[2] memory name;
            name[0] = namePrefixes[rand % namePrefixes.length];
            name[1] = nameSuffixes[rand % nameSuffixes.length];
            if (greatness >= 95) {
                output = string(
                    abi.encodePacked(
                        unicode"●",
                        " ",
                        name[0],
                        " ",
                        name[1],
                        " ",
                        unicode"●",
                        " ",
                        output,
                        " +1"
                    )
                );
            } else {
                output = string(
                    abi.encodePacked(
                        unicode"●",
                        " ",
                        name[0],
                        " ",
                        name[1],
                        " ",
                        unicode"●",
                        " ",
                        output
                    )
                );
            }
        }
        return output;
    }

    function getMax(uint256[] memory values) private pure returns (uint256) {
        require(values.length > 0);
        uint256 largest = 0;
        for (uint256 i = 0; i < values.length; i++) {
            if (values[i] > largest) {
                largest = values[i];
            }
        }
        return largest;
    }

    function getMin(uint256[] memory values) private pure returns (uint256) {
        require(values.length > 0);
        uint256 smallest = 999999 ether;
        for (uint256 i = 0; i < values.length; i++) {
            if (values[i] < smallest) {
                smallest = values[i];
            }
        }
        return smallest;
    }

