pragma solidity >=0.8.0 <0.9.0;

import "./IERC20.sol";
import "./SafeERC20.sol";


contract MetaInfinity {
    using SafeERC20 for IERC20;
    address public manager;
    uint8 public constant totalPackage = 9;

    bool pause;
    
    IERC20 token;

    address depositAddress;

    struct UserAffiliateDetails {
        uint userId;
        address sponsor;
        mapping(uint => bool) kitId;
        uint joiningDateTime;
        uint latestPackage;
    }

    modifier onlymanager() {
        require(msg.sender == manager, "Not manager");
        _;
    }

    constructor(address _token)  {
        
      token = IERC20(_token);
      manager = msg.sender;
    }
    
    mapping(uint => address) public userIdToAddress;
    uint256[10] public packagePrice = [0, 20, 36 , 84 ,240 ,540 ,1200, 2640, 5760, 15600];
    mapping (address => UserAffiliateDetails) public _UserAffiliateDetails;

    
    //Get User Id
    function getUserId(address user) external view returns (uint) {
        return (_UserAffiliateDetails[user].userId);
    }

    //Get Sponsor Id
    function getSponsorId(address user) external view returns (address) {
        return (_UserAffiliateDetails[user].sponsor);
    }
    
    // Admin Can Check Is User Exists Or Not
    function _IsUserExists(address user) public view returns (bool) {
        return (_UserAffiliateDetails[user].userId != 0);
    }
    
    function registration(address user, address referrer,uint256 amount) external onlymanager {     
        
        uint packageprice=packagePrice[1];
        
        require(pause == false, "Contract is paused");
        require(!_IsUserExists(user), "Already Registered !"); 
        require(amount == packageprice,"Invalid Package !"); 
        uint32 size;
        assembly { size := extcodesize(user) }	
        require(size == 0, "Smart Contract !");
        amount = amount * (10**18);
        token.safeTransferFrom(user, address(this), amount);
        //Updating user details
        uint TimeStamp=block.timestamp;
        _UserAffiliateDetails[user].userId = TimeStamp;
        _UserAffiliateDetails[user].joiningDateTime= TimeStamp;
        _UserAffiliateDetails[user].kitId[1] = true;
        _UserAffiliateDetails[user].sponsor = referrer;
        _UserAffiliateDetails[user].latestPackage = 20; 
        
        userIdToAddress[TimeStamp] = user;
        
    }

    function whitelist(address user, address referrer, uint256 amount, uint256 kitId) external onlymanager {     
        
        uint packageprice=packagePrice[kitId];
        
        require(pause == false, "Contract is paused");
        require(!_IsUserExists(user), "Already Registered !"); 
        require(amount == packageprice,"Invalid Package !"); 
        uint32 size;
        assembly { size := extcodesize(user) }	
        require(size == 0, "Smart Contract !");

        //Updating user details
        uint TimeStamp=block.timestamp;
        _UserAffiliateDetails[user].userId = TimeStamp;
        _UserAffiliateDetails[user].joiningDateTime= TimeStamp;
        _UserAffiliateDetails[user].kitId[kitId] = true;
        _UserAffiliateDetails[user].sponsor = referrer;
        _UserAffiliateDetails[user].latestPackage = amount; 
        
        userIdToAddress[TimeStamp] = user;
        
    }

    function upgradePackage(address user, uint256 amount, uint256 kitID) external {
        require(_UserAffiliateDetails[user].kitId[kitID-1] == true, "Not Eligible");
        require(_UserAffiliateDetails[user].kitId[kitID] == false, "Already on the Package");
        uint packageprice=packagePrice[kitID];
        require(amount == packageprice,"Package Price is not correct !");
        amount = amount * (10**18);
        token.safeTransferFrom(user, address(this), amount);
        _UserAffiliateDetails[user].kitId[kitID] = true;
        _UserAffiliateDetails[user].latestPackage = packageprice; 

    }
    
    function Payout(address[] calldata toPayaddr, uint[] calldata toPayamount) external onlymanager {
        
        require(pause == false, "Contract is paused");
        require(toPayaddr.length == toPayamount.length, "Array not Equal");
        for(uint i = 0; i<toPayaddr.length; i++){

            uint amount= toPayamount[i];
            address ref = toPayaddr[i];
            token.safeTransfer(ref, amount);

        }
    }

    function getlatestPackage(address user) external view returns(uint){

        uint package = _UserAffiliateDetails[user].latestPackage;

        return package;
    }

    function pauseContract() external onlymanager{

        pause = true;
    }

    function unpause() external onlymanager{

        pause = false;
    }

    function changeManager(address newmanager) external onlymanager {

        manager = newmanager;
    }

    function withdraw(address user, uint256 amount)external onlymanager {

        token.safeTransfer(user, amount);
    }

    function updatePackagePrice(uint kitID, uint newPrice)external onlymanager{

        packagePrice[kitID] = newPrice;
    }

    }
