    "contracts/Vesting.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.14;

import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "./VestingDeposit.sol";

contract Vesting is
    ReentrancyGuard,
    Ownable,
    Pausable {
    using SafeMath for uint256;

    uint256 constant MAX_SUPPLY = 2**256 - 1;

    IERC20 public token;  

    address public vestingCreatorAddress;  
    address public vestingTokenHolderAddress;  

    address[] public vestingDeposits;

    mapping(address => bool) public isVesting;

    event VestingCreated(
        address indexed beneficiary,
        address indexed vestingContractAddress,
        uint256 amount
    );

    event VestingRetrieve(
        address indexed beneficiary,
        address indexed vestingContractAddress,
        uint256 amount
    );    



    function configureVesting(
        address _tokenAddress, 
        address _vestingCreatorAddress,
        address _vestingTokenHolderAddress)
        external
        onlyOwner
    {
        token = IERC20(_tokenAddress);
        vestingCreatorAddress = _vestingCreatorAddress;
        vestingTokenHolderAddress = _vestingTokenHolderAddress;
    }

    function openVesting(
        uint256[] calldata _amounts,
        uint256[] calldata _dates,
        address _beneficiary
    ) external whenNotPaused nonReentrant onlyVestingCreator {

        require(address(token) != address(0), "Token address is not set");
        require(_amounts.length == _dates.length, "Dates and length should be the same");


        VestingDeposit _vestingDeposit = new VestingDeposit(
            address(token),
            _beneficiary
        );

        uint256 totalAmount = 0;
        for(uint i = 0; i<_dates.length; i++){
            totalAmount = totalAmount.add(_amounts[i]);
            _vestingDeposit.addAllocation(_amounts[i], _dates[i]);
        }
        vestingDeposits.push(address(_vestingDeposit));
        isVesting[address(_vestingDeposit)] = true;

        require(
            _vestingDeposit.allocationTotal() == totalAmount,
            "Allocation does not match"
        );

        require(
            token.allowance(vestingTokenHolderAddress, address(this)) >= totalAmount,
            "Allowance should be greater or equal to the amount vesting"
        );

        token.transferFrom(vestingTokenHolderAddress, address(_vestingDeposit), totalAmount);

        require(
            token.balanceOf(address(_vestingDeposit)) == totalAmount,
            "Transfer not completed"
        );

        emit VestingCreated(
            _beneficiary,
            address(_vestingDeposit),
            totalAmount
        );
    }

    function retrieveVesting(address _vestingContractAddress) external whenNotPaused nonReentrant {
        require(isVesting[_vestingContractAddress], "The parameter is not a vesting deposit");
        VestingDeposit vestingDeposit = VestingDeposit(_vestingContractAddress);        
        uint256 amount = vestingDeposit.retrieve();

        emit VestingRetrieve(
            vestingDeposit.beneficiary(),
            _vestingContractAddress,
            amount
        );
    }

    modifier onlyVestingCreator() {
        require(
            vestingCreatorAddress == msg.sender,
            "Only the vesting creator can call this function."
        );
        _;
    }
}"
