pragma solidity ^0.8.14;
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./Vesting.sol";

contract VestingDeposit is ReentrancyGuard {
    using SafeMath for uint256;

    address public beneficiary;

    IERC20 public token;

    Vesting public vestingContract;

    uint256[] public amounts;

    uint256[] public dates;

    uint256 public depositDate;

    uint256 public lastPaymentTime;

    uint256 public allocationTotal;

    uint256 public retrievedAmount;

    constructor(
        address _tokenAddress,
        address _beneficiary  
    ) {
        token = IERC20(_tokenAddress);
        vestingContract = Vesting(msg.sender);
        beneficiary = _beneficiary;
        depositDate = block.timestamp;
    }    

    function addAllocation(uint256 _amount, uint256 _date) external nonReentrant onlyVestingContract {
        dates.push(_date);
        amounts.push(_amount);
        allocationTotal = allocationTotal.add(_amount);
    }

    function calculateRetrievable() public view returns(uint256 _retrievable) {
        uint256 currentTime = block.timestamp;
        for(uint i = 0; i<dates.length; i++){
            if (dates[i] <= currentTime && dates[i] > lastPaymentTime) {
                _retrievable = _retrievable.add(amounts[i]);
            }
        }
        return _retrievable;
    }


    function retrieve() external nonReentrant onlyVestingContract returns (uint256 _amount) {
        uint256 retrievable = this.calculateRetrievable();
        token.transfer(beneficiary, retrievable);
        retrievedAmount = retrievedAmount.add(retrievable);
        lastPaymentTime = block.timestamp;
        return retrievedAmount;
    }    

    modifier onlyVestingContract() {
        require(
            address(vestingContract) == msg.sender,
            "Only the vesting contract can call this function."
        );
        _;
    }    
}
"
