    "contracts/IRevenueSplitter.sol": {
      "content": "//SPDX-License-Identifier: Unlicensed
pragma solidity ^0.8.4;

interface IRevenueSplitter {
    // Collection Management Events
    // 0d2cef1c634fa3276158c45f67fb4e707809a53f7a2b2a683d562ee009726da9
    event CreateCollectionEvent(address indexed collection);
    event AddPayeeEvent(
        address indexed collection,
        address indexed account,
        uint256 shares
    );
    // 64fd21a7e658d1bb0c35d0005345c260a2c8e0db3271e4d18817ad7930f35269
    event RemovePayeeEvent(address indexed collection, address indexed account);

    // Token $$ Events
    // a2fc0bd51a5bc3589d1bbf031bceaabcd269febe30c17d6a0d84255c586004e1
    event BuyEvent(
        address indexed collection,
        uint256 tokenId,
        uint256 amount,
        uint256 fee
    );

    // 61bbd690186cb1063ba2631cb23de3a44843d555f1b0a3cb1a8868a1367e8c64
    event RoyaltyEvent(address indexed collection, uint256 amount);

    // Collaborator $$ Events
    // 5dba113b49cfa7c90315e8e604e6b506f7abcb909b01dcb19ec39005086e68fc
    event WithdrawEvent(address indexed payee, uint256 amount);

    // 01bee1bfa4116bd0440a1108ef6cb6a2f6eb9b611d8f53260aec20d39e84ee88
    event DepositEvent(
        address indexed collection,
        address indexed payee,
        uint256 amount
    );

    // Payout Events
    // c23709862a4c577a0eb417be82feb9f2563057b7362ab578babf5c138b470056
    event SweepEvent(
        address indexed payee,
        address indexed recipient,
        uint256 amount
    );
    // 80e009436f30a4f32133e25b51b5349f2842ea9e93ea4fb6cdaaa4b4e518c687
    event PayoutEvent(address indexed payee, uint256 amount);

    function createCollection(
        string memory name,
        string memory symbol,
        uint256 royaltyAmount,
        address[] memory _payees,
        uint256[] memory _shares,
        string memory baseURI,
        address minter
    ) external returns (address);

    function addCollectionPayees(
        address _collection,
        address[] memory _payee,
        uint256[] memory shares
    ) external;

    function addCollectionPayee(
        address _collection,
        address _payee,
        uint256 shares
    ) external;

    function removeCollectionPayee(
        address _collection,
        address _payee
    ) external;

    function buyNFT(
        address payable _collection,
        uint256 tokenId,
        uint256 expiryBlock,
        bytes calldata signature
    ) external payable;

    function sweep(address _payee, address payable _recipient) external;

    function payout(address payable _payee) external;

    function withdraw() external;

    function depositRoyalty(address _collection) external payable;
}
"
