    "data/contracts/nfts/RKCitizen.sol": {
      "content": "//SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import '@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol';
import '@openzeppelin/contracts/access/Ownable.sol';
import '@openzeppelin/contracts/utils/Counters.sol';
import '@openzeppelin/contracts/token/common/ERC2981.sol';
import '../utils/ContextMixin.sol';

contract RKCitizen is ERC721URIStorage, Ownable, ContextMixin, ERC2981 {
	using Counters for Counters.Counter;

	constructor(
		string memory _name,
		string memory _symbol,
		uint96 _royaltyFee,
		address _royaltyReceiver
	) ERC721(_name, _symbol) {
		_setDefaultRoyalty(_royaltyReceiver, _royaltyFee);
	}

	function mint(address receiver, uint256 tokenId, string memory tokenURI) public onlyOwner {
		_safeMint(receiver, tokenId);
		_setTokenURI(tokenId, tokenURI);
	}

	/**
	 * @dev See {ERC721-_burn}. This override additionally clears the royalty information for the token.
	 */
	function _burn(uint256 tokenId) internal virtual override {
		super._burn(tokenId);
		_resetTokenRoyalty(tokenId);
	}

	/**
	 * @dev See {IERC165-supportsInterface}.
	 */
	function supportsInterface(bytes4 interfaceId) public view virtual override(ERC721URIStorage, ERC2981) returns (bool) {
		return super.supportsInterface(interfaceId);
	}

	/**
	 * This is used instead of msg.sender as transactions won't be sent by the original token owner, but by OpenSea.
	 * @dev https://docs.opensea.io/docs/polygon-basic-integration
	 */
	function _msgSender() internal view override returns (address sender) {
		return ContextMixin.msgSender();
	}
}
"
    },
    "data/contracts/raffle/RKRaffle.sol": {
      "content": "//SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.19;
pragma abicoder v2; // required to accept structs as function parameters

import '../utils/RKVRF.sol';
import '../utils/RKUtility.sol';
import '../libs/VRFLib.sol';

contract RKRaffle is ReentrancyGuard, RKUtility, RKVRF {
	/// @dev raffles count
	uint256 private _rafflesCount;
	/// @dev raffleId => Raffle
	mapping(uint256 => Raffle) private _raffles;
	/// @dev raffleId => Tickets[]
	mapping(uint256 => Ticket[]) private _purchasedTickets;
	/// @dev raffleId => owner => ticketsCount
	mapping(uint256 => mapping(address => uint256)) private _ticketsCountPerWallet;
	/// @dev randomNumberRequestId -> raffleId
	mapping(uint256 => uint256) private _randomNumberRequests;
	/// @dev raffleId => allowedAddresses
	mapping(uint256 => address) private _allowedAddressPerRaffle;

	/// @dev percentage only, 100 = 1%
	uint256 public featuredFee = 100;
	address payable private fundsReceiver;
	uint256 public customTokenRaffleFee;

	/// @dev percentage only 100 = 100% in this case
	uint256 public featuredFeeCustomToken = 20;

	enum RaffleStatus {
		Active,
		Drawing,
		Drawn,
		Claimed,
		Cancelled,
		Finished
	}

	enum RaffleCurrency {
		Matic,
		ETH,
		Custom
	}

	struct Raffle {
		address creator;
		uint256 ticketPrice;
		uint256 totalTickets;
		uint256 totalTicketsSold;
		uint256 ticketsPerWalletLimit;
		uint256 expirationDate;
		address tokenContractAddress;
		uint256 tokenId;
		uint16 selectedIndex;
		bool withdrawn;
		bool featured;
		RaffleStatus status;
		RaffleCurrency currencyType;
		address currencyContract;
		bool holderOnly;
	}

	struct Ticket {
		address buyer;
		uint256 raffleId;
		uint16 currentIndex;
	}

	event RaffleCreated(
		uint256 indexed raffleId,
		address indexed creator,
		uint256 ticketPrice,
		uint256 totalTickets,
		uint256 ticketsPerWalletLimit,
		uint256 expirationDate,
		address tokenContractAddress,
		uint256 tokenId,
		bool featured,
		RaffleCurrency currencyType,
		address currencyContract,
		bool holderOnly,
		address allowedAddressPerRaffle
	);

	event RaffleWinnerSelected(uint256 indexed raffleId, uint16 selectedIndex);

	event RaffleClaimed(uint256 indexed raffleId, address indexed winner, uint256 ticketId);

	event RaffleWithdrawn(uint256 indexed raffleId, address indexed creator, uint256 amount);

	event RaffleStatusChanged(uint256 indexed raffleId, RaffleStatus status);

	event RaffleNFTRecovered(uint256 indexed raffleId);

	event TicketsPurchased(uint256 raffleId, address indexed buyer, uint256 currentIndex, uint256 quantity);

	function _isRaffleActive(uint256 raffleId) internal view returns (bool) {
		return _raffles[raffleId].status == RaffleStatus.Active;
	}

	function isRaffleCreator(address creator) internal view returns (bool) {
		return creator == msg.sender;
	}

	function _raffleExists(address creator) internal pure returns (bool) {
		return creator != address(0);
	}

	constructor(
		address contractOwner,
		address fundsReceiverAddress,
		uint256 contractFee,
		uint256 percentageFeaturedFee,
		address whitelistContract,
		address silverKnightContract,
		address vrfLinkTokenAddress,
		address vrfCoordinator,
		VRFLib.VRFConfig memory vrfConfig,
		uint256 _customTokenRaffleFee
	)
		RKUtility(contractOwner, 0, contractFee, whitelistContract, silverKnightContract)
		RKVRF(vrfLinkTokenAddress, vrfCoordinator, vrfConfig)
	{
		featuredFee = percentageFeaturedFee;
		fundsReceiver = payable(fundsReceiverAddress);
		customTokenRaffleFee = _customTokenRaffleFee;
	}

	function createRaffle(
		uint256 ticketPrice,
		uint256 totalTickets,
		uint256 ticketsPerWalletLimit,
		uint256 expirationDate,
		address tokenContractAddress,
		uint256 tokenId,
		bool featured,
		RaffleCurrency currencyType,
		address currencyContract,
		bool holderOnly,
		address allowedAddress
	) external payable nonReentrant {
		require(ticketPrice > 0, 'Invalid ticketPrice');
		require(totalTickets > 0 && totalTickets < 5000, 'Invalid totalTickets');
		require(ticketsPerWalletLimit > 0 && ticketsPerWalletLimit <= totalTickets, 'Invalid ticketsPerWalletLimit');
		require(expirationDate > block.timestamp, 'Invalid expirationDate');
		require(isWhitelisted(tokenContractAddress), 'Token not Whitelisted');

		_rafflesCount++;

		uint256 raffleId = _rafflesCount;

		_transferNft(tokenContractAddress, tokenId, msg.sender, address(this), 1);

		if (holderOnly) {
			_addRaffleAllowedAddresses(allowedAddress, raffleId);
		}

		if (currencyType == RaffleCurrency.Custom) {
			uint256 totalCustomFee = customTokenRaffleFee;

			if (featured) {
				totalCustomFee = customTokenRaffleFee + ((customTokenRaffleFee * featuredFeeCustomToken) / 100);
			}

			_transferEther(fundsReceiver, totalCustomFee);
		}

		_raffles[raffleId] = Raffle(
			payable(msg.sender),
			ticketPrice,
			totalTickets,
			0,
			ticketsPerWalletLimit,
			expirationDate,
			tokenContractAddress,
			tokenId,
			0,
			false,
			featured,
			RaffleStatus.Active,
			currencyType,
			currencyContract,
			holderOnly
		);

		emit RaffleCreated(
			raffleId,
			msg.sender,
			ticketPrice,
			totalTickets,
			ticketsPerWalletLimit,
			expirationDate,
			tokenContractAddress,
			tokenId,
			featured,
			currencyType,
			currencyContract,
			holderOnly,
			allowedAddress
		);
	}

	function cancelRaffle(uint256 raffleId) external nonReentrant {
		require(_raffles[raffleId].totalTicketsSold == 0, 'Raffle has already started');
		require(_isRaffleActive(raffleId), 'Raffle not active');
		require(isRaffleCreator(_raffles[raffleId].creator), 'Only creator allowed');

		_raffles[raffleId].status = RaffleStatus.Cancelled;

		_transferNft(
			_raffles[raffleId].tokenContractAddress,
			_raffles[raffleId].tokenId,
			address(this),
			_raffles[raffleId].creator,
			1
		);

		emit RaffleStatusChanged(raffleId, RaffleStatus.Cancelled);
	}

	function recoverNFT(uint256 raffleId) external nonReentrant {
		require(_raffles[raffleId].status == RaffleStatus.Finished, 'Raffle is not finished');
		require(isRaffleCreator(_raffles[raffleId].creator), 'Only creator allowed');

		_transferNft(
			_raffles[raffleId].tokenContractAddress,
			_raffles[raffleId].tokenId,
			address(this),
			_raffles[raffleId].creator,
			1
		);

		emit RaffleNFTRecovered(raffleId);
	}

	function buyTickets(uint256 raffleId, uint256 quantity) external payable nonReentrant {
		handleTicketsTransaction(raffleId, quantity, msg.sender);
	}

	function giftTickets(uint256 raffleId, uint256 quantity, address receiver) external payable nonReentrant {
		handleTicketsTransaction(raffleId, quantity, receiver);
	}

	function handleTicketsTransaction(uint256 raffleId, uint256 quantity, address receiver) internal {
		require(
			quantity + _raffles[raffleId].totalTicketsSold <= _raffles[raffleId].totalTickets,
			'Not enough tickets left'
		);
		require(_raffles[raffleId].expirationDate > block.timestamp, 'Raffle has expired');
		require(
			_ticketsCountPerWallet[raffleId][receiver] + quantity <= _raffles[raffleId].ticketsPerWalletLimit,
			'Tickets by wallet limit reached'
		);
		require(_raffleExists(_raffles[raffleId].creator), 'Only creator allowed');
		require(_isRaffleActive(raffleId), 'Raffle not active');

		if (_raffles[raffleId].holderOnly) {
			require(hasTokenBalance(_allowedAddressPerRaffle[raffleId], receiver), 'Receiver is not a holder');
		}

		if (_raffles[raffleId].currencyType == RaffleCurrency.Custom) {
			_transferToken(
				_raffles[raffleId].currencyContract,
				receiver,
				address(this),
				_raffles[raffleId].ticketPrice * quantity,
				false
			);
		} else {
			require(msg.value == _raffles[raffleId].ticketPrice * quantity, 'Invalid amount');
		}

		uint256 currentIndex = _raffles[raffleId].totalTicketsSold + quantity;

		_purchasedTickets[raffleId].push(Ticket(receiver, raffleId, uint16(currentIndex)));

		_raffles[raffleId].totalTicketsSold = currentIndex;
		_ticketsCountPerWallet[raffleId][receiver] = _ticketsCountPerWallet[raffleId][receiver] + quantity;

		emit TicketsPurchased(raffleId, receiver, currentIndex, quantity);
	}

	function finishRaffle(uint256 raffleId) external isAdmin nonReentrant {
		require(
			_raffles[raffleId].expirationDate < block.timestamp ||
				_raffles[raffleId].totalTicketsSold == _raffles[raffleId].totalTickets,
			'Raffle has not expired or finished yet'
		);
		require(_isRaffleActive(raffleId), 'Raffle not active');
		require(_raffleExists(_raffles[raffleId].creator), 'Only creator allowed');

		if (_raffles[raffleId].totalTicketsSold == 0) {
			_raffles[raffleId].status = RaffleStatus.Finished;
			emit RaffleStatusChanged(raffleId, RaffleStatus.Finished);

			return;
		}

		_raffles[raffleId].status = RaffleStatus.Drawing;
		_randomNumberRequests[_requestRandomNumber()] = raffleId;

		emit RaffleStatusChanged(raffleId, RaffleStatus.Drawing);
	}

	function claimPrize(uint256 raffleId) external nonReentrant {
		require(_raffles[raffleId].status == RaffleStatus.Drawn, 'Raffle is not Drawn');
		require(_raffleExists(_raffles[raffleId].creator), 'Only creator allowed');
		address winner = _getRaffleWinner(raffleId, _raffles[raffleId].selectedIndex);
		require(winner == msg.sender, 'Only the winner can claim the prize');

		_raffles[raffleId].status = RaffleStatus.Claimed;

		_transferNft(_raffles[raffleId].tokenContractAddress, _raffles[raffleId].tokenId, address(this), winner, 1);

		emit RaffleClaimed(raffleId, winner, _raffles[raffleId].totalTicketsSold);
		emit RaffleStatusChanged(raffleId, RaffleStatus.Claimed);
	}

	function claimFunds(uint256 raffleId) external nonReentrant {
		require(
			_raffles[raffleId].status == RaffleStatus.Drawn || _raffles[raffleId].status == RaffleStatus.Claimed,
			'Raffle is not ready to be claimed'
		);
		require(!_raffles[raffleId].withdrawn, 'Funds have already been withdrawn');
		require(isRaffleCreator(_raffles[raffleId].creator), 'Only creator allowed');
		require(_raffleExists(_raffles[raffleId].creator), 'Only creator allowed');
		_raffles[raffleId].withdrawn = true;

		uint256 amountRaised = _raffles[raffleId].totalTicketsSold * _raffles[raffleId].ticketPrice;

		if (_raffles[raffleId].currencyType == RaffleCurrency.Custom) {
			_transferToken(_raffles[raffleId].currencyContract, msg.sender, msg.sender, amountRaised, true);
		} else {
			uint256 appFee = calculatedPercentageFee(amountRaised);
			uint256 appFeaturedFee = _raffles[raffleId].featured &&
				featuredFee != 0 &&
				!hasSilverKnight(_raffles[raffleId].creator)
				? ((amountRaised * featuredFee) / 10000)
				: 0;
			uint totalFee = appFee + appFeaturedFee;

			_transferEther(payable(msg.sender), amountRaised - totalFee);
			_transferEther(fundsReceiver, totalFee);
		}

		emit RaffleWithdrawn(raffleId, msg.sender, amountRaised);
	}

	function _addRaffleAllowedAddresses(address allowedAddress, uint256 raffleId) internal {
		require(allowedAddress != address(0), 'invalid address');
		require(isWhitelisted(allowedAddress), 'Address not Whitelisted');

		_allowedAddressPerRaffle[raffleId] = allowedAddress;
	}

	function updateFees(uint256 _featuredFee, uint256 _customTokenRaffleFee) external isAdmin {
		featuredFee = _featuredFee;
		customTokenRaffleFee = _customTokenRaffleFee;
	}

	function getRaffle(uint256 raffleId) external view returns (Raffle memory) {
		return _raffles[raffleId];
	}

	function _getRaffleWinner(uint256 _raffleId, uint256 _randomIndex) internal view returns (address) {
		return _purchasedTickets[_raffleId][findUpperBound(_purchasedTickets[_raffleId], _randomIndex)].buyer;
	}

	function findUpperBound(Ticket[] memory array, uint256 randomIndex) internal pure returns (uint256) {
		if (array.length == 0) return 0;

		uint256 low = 0;
		uint256 high = array.length;

		while (low < high) {
			uint256 mid = Math.average(low, high);
			if (array[mid].currentIndex > randomIndex) {
				high = mid;
			} else {
				low = mid + 1;
			}
		}

		if (low > 0 && array[low - 1].currentIndex == randomIndex) {
			return low - 1;
		} else {
			return low;
		}
	}

	/// @dev Callback function used by VRF Coordinator, don't change the name
	function fulfillRandomWords(uint256 _requestId, uint256[] memory _randomWords) internal override {
		uint256 raffleId = _randomNumberRequests[_requestId];

		_raffles[raffleId].selectedIndex = uint16((_randomWords[0] % _raffles[raffleId].totalTicketsSold) + 1);
		_raffles[raffleId].status = RaffleStatus.Drawn;

		emit RaffleStatusChanged(raffleId, RaffleStatus.Drawn);
		emit RaffleWinnerSelected(raffleId, _raffles[raffleId].selectedIndex);
	}
}
"
    },
    "data/contracts/utils/ContextMixin.sol": {
      "content": "//SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.19;

/**
 * @dev https://github.com/maticnetwork/pos-portal/blob/master/contracts/common/ContextMixin.sol
 */
abstract contract ContextMixin {
	function msgSender() internal view returns (address payable sender) {
		if (msg.sender == address(this)) {
			bytes memory array = msg.data;
			uint256 index = msg.data.length;
			assembly {
				// Load the 32 bytes word from memory with the address on the lower 20 bytes, and mask those.
				sender := and(mload(add(array, index)), 0xffffffffffffffffffffffffffffffffffffffff)
			}
		} else {
			sender = payable(msg.sender);
		}

		return sender;
	}
}
"
    },
    "data/contracts/utils/RKUtility.sol": {
      "content": "//SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.19;
pragma abicoder v2; // required to accept structs as function parameters

import '@openzeppelin/contracts/token/ERC721/ERC721.sol';
import '@openzeppelin/contracts/token/ERC1155/ERC1155.sol';
import '@openzeppelin/contracts/token/ERC20/ERC20.sol';
import '@openzeppelin/contracts/token/ERC721/ERC721.sol';
import '@openzeppelin/contracts/token/ERC721/IERC721Receiver.sol';
import '@openzeppelin/contracts/security/ReentrancyGuard.sol';
import './RKWhitelist.sol';
import '../nfts/RKCitizen.sol';

contract RKUtility is ReentrancyGuard, IERC721Receiver, IERC1155Receiver {
	address payable internal _contractOwner;
	uint256 private fixedFee;
	uint256 public percentageFee;
	RKWhitelist private _whitelistContract;
	RKCitizen private _silverKnightContract;

	enum TokenType {
		ERC721,
		ERC1155
	}

	constructor(
		address contractOwner,
		uint256 _fixedFee,
		uint256 _percentageFee,
		address whitelistContract,
		address silverKnightContract
	) {
		_contractOwner = payable(contractOwner);
		fixedFee = _fixedFee;
		percentageFee = _percentageFee;
		_whitelistContract = RKWhitelist(whitelistContract);
		_silverKnightContract = RKCitizen(silverKnightContract);
	}

	modifier isAdmin() {
		require(_contractOwner == msg.sender, 'Forbidden');
		_;
	}

	function _getTokenType(address nftContract) internal view returns (TokenType tokenType) {
		if (IERC165(nftContract).supportsInterface(type(IERC1155).interfaceId)) {
			return TokenType.ERC1155;
		}

		if (IERC165(nftContract).supportsInterface(type(IERC721).interfaceId)) {
			return TokenType.ERC721;
		}

		revert('Unsupported contract type');
	}

	function _transferToken(address tokenContract, address from, address to, uint256 amount, bool isWithdraw) internal {
		require(isWhitelisted(tokenContract), 'Not Whitelisted');
		uint8 decimal = ERC20(tokenContract).decimals();

		if (isWithdraw) {
			ERC20(tokenContract).transfer(to, amount * (10 ** decimal));
		} else {
			ERC20(tokenContract).transferFrom(from, to, amount * (10 ** decimal));
		}
	}

	function _transferNft(address nftContract, uint256 tokenId, address from, address to, uint256 amount) internal {
		require(isWhitelisted(nftContract), 'Not Whitelisted');

		TokenType tokenType = _getTokenType(nftContract);

		if (tokenType == TokenType.ERC721) {
			ERC721(nftContract).transferFrom(from, to, tokenId);
		} else if (tokenType == TokenType.ERC1155) {
			ERC1155(nftContract).safeTransferFrom(from, to, tokenId, amount, '');
		}
	}

	function _transferEther(address payable to, uint256 amount) internal {
		if (amount == 0) {
			return;
		}

		require(address(this).balance >= amount, 'insufficient balance');

		(bool success, ) = to.call{ value: amount }('');

		require(success, 'unable to send value');
	}

	function updateWhitelist(address whitelistContract) public isAdmin nonReentrant {
		_whitelistContract = RKWhitelist(whitelistContract);
	}

	function updateSilverKnight(address silverKnightContract) public isAdmin nonReentrant {
		_silverKnightContract = RKCitizen(silverKnightContract);
	}

	function updateFeesAndOwner(
		uint256 _fixedFee,
		uint256 _percentageFee,
		address payable contractOwner
	) public isAdmin nonReentrant {
		fixedFee = _fixedFee;
		percentageFee = _percentageFee;
		_contractOwner = contractOwner;
	}

	function calculatedFixedFee() public view returns (uint256) {
		return fixedFee * (hasSilverKnight(msg.sender) ? 0 : 1);
	}

	// todo add silver knight discount.
	function calculatedPercentageFee(uint256 amount) internal view returns (uint256) {
		return (amount * percentageFee) / 10000;
	}

	function isWhitelisted(address addr) internal view returns (bool) {
		return _whitelistContract.isWhitelisted(addr);
	}

	function hasSilverKnight(address addr) internal view returns (bool) {
		return _silverKnightContract.balanceOf(addr) > 0;
	}

	function hasTokenBalance(address contractAddress, address userAddress) internal view returns (bool) {
		return ERC721(contractAddress).balanceOf(userAddress) > 0;
	}

	function onERC721Received(
		address operator,
		address from,
		uint256 tokenId,
		bytes calldata data
	) external view override returns (bytes4) {
		require(isWhitelisted(msg.sender), 'Not Whitelisted');

		return IERC721Receiver.onERC721Received.selector;
	}

	function onERC1155Received(
		address operator,
		address from,
		uint256 id,
		uint256 value,
		bytes calldata data
	) external view override returns (bytes4) {
		require(isWhitelisted(msg.sender), 'Not Whitelisted');

		return IERC1155Receiver.onERC1155Received.selector;
	}

	function onERC1155BatchReceived(
		address operator,
		address from,
		uint256[] calldata ids,
		uint256[] calldata values,
		bytes calldata data
	) external view override returns (bytes4) {
		require(isWhitelisted(msg.sender), 'Not Whitelisted');

		return IERC1155Receiver.onERC1155BatchReceived.selector;
	}

	function supportsInterface(bytes4 interfaceID) external pure returns (bool) {
		return interfaceID == type(IERC721Receiver).interfaceId || interfaceID == type(IERC1155Receiver).interfaceId;
	}
}
"
    },
    "data/contracts/utils/RKVRF.sol": {
      "content": "//SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.19;
pragma abicoder v2; // required to accept structs as function parameters

import '@chainlink/contracts/src/v0.8/VRFV2WrapperConsumerBase.sol';
import '@chainlink/contracts/src/v0.8/interfaces/VRFCoordinatorV2Interface.sol';
import '../libs/VRFLib.sol';

abstract contract RKVRF is VRFV2WrapperConsumerBase {
	/// @dev https://docs.chain.link/vrf/v2/introduction
	VRFCoordinatorV2Interface private _vrfCoordinator;
	VRFLib.VRFConfig private _vrfConfig;

	address private _contractOwner;

	constructor(
		address vrfLinkTokenAddress,
		address vrfCoordinator,
		VRFLib.VRFConfig memory vrfConfig
	) VRFV2WrapperConsumerBase(vrfLinkTokenAddress, vrfCoordinator) {
		_vrfConfig = vrfConfig;
		_vrfCoordinator = VRFCoordinatorV2Interface(vrfCoordinator);
		_contractOwner = msg.sender;
	}

	function setVrfConfig(
		uint32 callbackGasLimit,
		uint16 requestConfirmations,
		bytes32 keyHash,
		uint64 subscriptionId
	) public {
		if (msg.sender != _contractOwner) {
			revert('RKVRF: Only contract owner can set VRF config');
		}

		_vrfConfig = VRFLib.VRFConfig(callbackGasLimit, requestConfirmations, keyHash, subscriptionId);
	}

	function _requestRandomNumber() internal returns (uint256) {
		return
			_vrfCoordinator.requestRandomWords(
				_vrfConfig.keyHash,
				_vrfConfig.subscriptionId,
				_vrfConfig.requestConfirmations,
				_vrfConfig.callbackGasLimit,
				1 // mumWords
			);
	}
}
"
    },
    "data/contracts/utils/RKWhitelist.sol": {
      "content": "//SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.4;
pragma abicoder v2; // required to accept structs as function parameters

import '@openzeppelin/contracts/utils/Counters.sol';

contract RKWhitelist {
	using Counters for Counters.Counter;

	Counters.Counter private _whitelistedAddressesCount;

	/// @dev contractAddress -> isWhitelisted
	mapping(address => bool) public whitelist;
	/// @dev whitelistId -> address
	mapping(uint8 => address) public whitelistAddresses;

	address public owner;

	constructor(address[] memory addresses) {
		for (uint16 i = 0; i < addresses.length; i++) {
			_addToWhitelist(addresses[i]);
		}

		owner = msg.sender;
	}

	modifier onlyOwner() {
		require(msg.sender == owner, 'Whitelist: caller is not the owner');
		_;
	}

	function updateAllWhitelist(address[] memory addresses) external onlyOwner {
		for (uint8 i = 0; i < addresses.length; i++) {
			_addToWhitelist(addresses[i]);
		}
	}

	function addToWhitelist(address addr) external onlyOwner {
		_addToWhitelist(addr);
	}

	function setOwner(address _owner) external onlyOwner {
		require(_owner != address(0), 'Whitelist: new owner is the zero address');
		owner = _owner;
	}

	function isWhitelisted(address addr) external view returns (bool) {
		return whitelist[addr];
	}

	function removeFromWhitelist(address addr) external onlyOwner returns (bool) {
		require(whitelist[addr], 'Whitelist: address is not whitelisted');

		_whitelistedAddressesCount.decrement();
		whitelist[addr] = false;

		return whitelist[addr];
	}

	function getWhitelistedAddresses() external view returns (address[] memory) {
		address[] memory addresses = new address[](_whitelistedAddressesCount.current());

		for (uint8 i = 0; i < addresses.length; i++) {
			addresses[i] = whitelistAddresses[uint8(i + 1)];
		}

		return addresses;
	}

	function _addToWhitelist(address addr) internal {
		require(!whitelist[addr], 'Whitelist: address is already whitelisted');

		_whitelistedAddressesCount.increment();
		whitelist[addr] = true;
		whitelistAddresses[uint8(_whitelistedAddressesCount.current())] = addr;
	}
}
"
    }
