    "contracts/DepositWithdraw/DepositUpgradeble.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.2;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";

contract DepositUpgradeable is Initializable {

    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    uint public feeRate;

    event Deposited(
        address indexed sender,
        string playerId,
        uint256 amount
    );

    event Withdrawn(
        address indexed sender,
        string playerId,
        uint256 amount,
        string cashId
    );

    IERC20 public token;
    address public admin;
    uint[3] public protocolFee; /* 0 - DAO, 1- burn, 2 - revenue. */

    address[3] public beneficiaryAddress; /* 0 - DAO address, 1- burn address, 2 - revenue address. */

    // constructor(
    //     address[3] memory _beneficiaryAddress,
    //     uint[3] memory _protocolFee
    // ) public {
    //     admin = msg.sender; 
    //     protocolFee = _protocolFee;
    //     beneficiaryAddress = _beneficiaryAddress;
    // }


    function initialize(
        address[3] memory _beneficiaryAddress,
        uint[3] memory _protocolFee
    ) public initializer{
        admin = _beneficiaryAddress[0]; 
        protocolFee = _protocolFee;
        beneficiaryAddress = _beneficiaryAddress;
        feeRate = 4;
    }

    function updateOwner(address _account) public {
        require(msg.sender == admin, "only owner");
        admin = _account;
    }

    /**
     * @notice Setting DAO fee to deduct on buy/sell.
     * @param _DAO new DAO fee
     */
    function setDAOFee( uint _DAO) external {
        require(msg.sender == admin, "only owner");
        protocolFee[0] = _DAO;
    }

    /**
     * @notice Setting burn fee to deduct on buy/sell.
     * @param _burn new burn fee
     */
    function setBurnFee( uint _burn) external{
        require(msg.sender == admin, "only owner");
        protocolFee[1] = _burn;
    }

    /**
     * @notice Setting revenue fee to deduct on buy/sell.
     * @param _revenue new revenue fee
     */
    function setRevenueFee( uint _revenue) external{
        require(msg.sender == admin, "only owner");
        protocolFee[2] = _revenue;
    }

    /**
     * @notice Setting DAO address to send deducted DAO on buy/sell.
     * @param _DAO new DAO address
     */
    function setDAOAdd( address _DAO) external {
        require(msg.sender == admin, "only owner");
        beneficiaryAddress[0] = _DAO;
    }

    /**
     * @notice Setting burn address to send deducted burn on buy/sell.
     * @param _burn new burn address
     */
    function setBurnAdd( address _burn) external {
        require(msg.sender == admin, "only owner");
        beneficiaryAddress[1] = _burn;
    }

    /**
     * @notice Setting revenue address to send deducted revenue on buy/sell.
     * @param _revenue new DAO address
     */
    function setRevenueAdd( address _revenue) external {
        require(msg.sender == admin, "only owner");
        beneficiaryAddress[2] = _revenue;
    }

    function setFeeRate(uint256 _feeRate) external {
        require(msg.sender == admin, "only owner");
        feeRate = _feeRate;
    }

    function getFeeRate() public view returns (uint256) {
        return feeRate;
    }

    function computeProtocalFee(uint256 amount ) public view returns(uint _DAO, uint _burn, uint _revenue, uint _tValue){
        if(feeRate <= 0) {
            (_DAO, _burn, _revenue, _tValue) = (0, 0, 0, amount);
        }else{
            uint _totalFee = amount * feeRate / 100;
            _DAO = (_totalFee*protocolFee[0])/100;
            _burn = (_totalFee*protocolFee[1])/100;
            _revenue = (_totalFee*protocolFee[2])/100;
            _tValue =  amount-(_DAO+_burn+_revenue);
        }
    }

    function deposit(uint256 _amount, address _tokenAddress, string memory _playerId) public {
        IERC20 token = IERC20(_tokenAddress);
        require(_amount > 0, "deposit amount should be more than 0");
        require(token.transferFrom(msg.sender, admin, _amount), "transfer failed");
        emit Deposited(msg.sender, _playerId, _amount);
    }

    function withdrawal(uint256 _amount, address _receive, address _tokenAddress, string memory _playerId, string memory _cashId) external {
        require(msg.sender == admin, "only owner");

        IERC20 token = IERC20(_tokenAddress);

        require(_amount > 0, "withdraw amount should be more than 0");

        (uint256 _DAO, uint256 _burn, uint _revenue, uint _tValue) = computeProtocalFee(_amount);

        if(_DAO > 0) SafeERC20.safeTransferFrom(token, admin, beneficiaryAddress[0],_DAO);

        if(_burn > 0) SafeERC20.safeTransferFrom(token, admin, beneficiaryAddress[1],_burn);

        if(_revenue > 0) SafeERC20.safeTransferFrom(token, admin, beneficiaryAddress[2],_revenue);

        require(token.transferFrom(admin, _receive, _tValue), "transfer failed");

        emit Withdrawn(_receive,_playerId, _tValue, _cashId);
    }
}"
