// File contracts/LPStaking.sol

// 

pragma solidity ^0.8.0;

enum Kind {
    free,
    days90,
    days360
}

enum Status {
    staked,
    withdrawn
}

struct LPStaking {
    uint id;
    Kind kind;
    uint amount;
    uint timestamp;
    uint rewardTimestamp;
    address owner;
    Status status;
}

struct LPNFTStaking {
    uint id;
    uint tokenId;
    uint timestamp;
    uint rewardTimestamp;
    address owner;
    Status status;
}


