pragma solidity ^0.8.0;

library Const {

    uint public constant SHARE_SUPPLY = 5001;
    uint public constant Xe18 = 10 ** 18;
    uint public constant UINT_MAX = 0xffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffff;


    // Rebase

    uint public constant CASH_PRICE_ORACLE_PEROID = 1 days;
    uint public constant CASH_REBASE_PEROID = 7 days;
    uint public constant SHARE_REBASE_PEROID = 1 days;
    
    

    // LPPool
    
    uint public constant CLAIMING_PEROID = 21 days;
    uint public constant CLAIMING_ADJUST_PEROID = 1 days;
    uint public constant LPPOOL_LOCK_PEROID0 = 0;
    uint public constant LPPOOL_LOCK_PEROID1 = 90 days;
    uint public constant LPPOOL_LOCK_PEROID2 = 360 days;
    uint public constant NFT_LOCKING_TIME = 12 * 360 days;
    uint public constant LPPOOL_WITHDRAW_WINDOW = 1 days;


    // SharePool
    uint public constant MIGRATION_REBASE_COUNT = 20;
    uint public constant REWARD_DURATION = 14 days;



    // ReferPool
    uint public constant REFER_POOL_REWARD_DURATION = 90 days;

}


