// File contracts/LPPool1.sol

// 

pragma solidity ^0.8.0;











contract LPPool1 is ReentrancyGuard, OperatorRole {
    using SafeMath for uint;
    using SafeERC20 for IERC20;
    using EnumerableSet for EnumerableSet.UintSet;


    uint8[3]  multiplier = [
            1,
            2,
            4
        ];
    uint[4] lockPeroid = [
            Const.LPPOOL_LOCK_PEROID0,
            Const.LPPOOL_LOCK_PEROID1,
            Const.LPPOOL_LOCK_PEROID2
        ];

    /* ========== STATE VARIABLES ========== */

    IERC20Mintable public rewardsToken;
    IERC20 public stakingToken;
    IReferPool public referPool;
    Referrer public refer;

    uint public periodFinish = 0;
    uint public rewardRate = 0;
    uint public lastUpdateTime;
    uint public rewardPerTokenStored;

    mapping(uint => uint) public userRewardPerTokenPaid;
    mapping(uint => uint) public rewards;

    uint private _totalSupply;
    uint private _index = 20000;
    mapping(uint => LPStaking) private _stakings;
    mapping(address => EnumerableSet.UintSet) private _myStakings;

    /* ========== CONSTRUCTOR ========== */

    constructor(
        address _stakingToken,
        address _rewardsToken,
        address _refer,
        address _referPool
    ) {
        
        stakingToken = IERC20(_stakingToken);
        rewardsToken = IERC20Mintable(_rewardsToken);
        refer = Referrer(_refer);
        referPool = IReferPool(_referPool);

        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
    }

    /* ========== VIEWS ========== */

    function totalSupply() external view returns (uint) {
        return _totalSupply;
    }

    function balanceOf(uint id) public view returns (uint) {
        LPStaking memory s = _stakings[id];
        uint bal = s.amount * multiplier[uint(s.kind)];
        if(s.status == Status.withdrawn){
            return 0;
        }
        if(s.timestamp == 0){
            return 0;
        }
        uint delta = block.timestamp - s.rewardTimestamp;
        if(delta <= Const.CLAIMING_PEROID) {
            return bal;
        }else{
            uint p = (delta - Const.CLAIMING_PEROID) / Const.CLAIMING_ADJUST_PEROID;
            if(p > 20) { p = 20; }
            return bal * 96 ** p / 100 ** p;
        }
    }

    function lastTimeRewardApplicable() public view returns (uint) {
        return block.timestamp;
    }

    function rewardPerToken() public view returns (uint) {
        if (_totalSupply == 0) {
            return rewardPerTokenStored;
        }
        return
            rewardPerTokenStored.add(
                lastTimeRewardApplicable().sub(lastUpdateTime).mul(rewardRate).mul(1e18).div(_totalSupply)
            );
    }

    function earned(uint id) public view returns (uint) {
        return balanceOf(id).mul(rewardPerToken().sub(userRewardPerTokenPaid[id])).div(1e18).add(rewards[id]);
    }

    function stakingCount() public view returns (uint) {
        return _index;
    }

    function staking(uint id) public view returns (LPStaking memory) {
        return _stakings[id];
    }

    function myStakingCount(address account) public view returns (uint) {
        return _myStakings[account].length();
    }

    function myStaking(address account, uint index) public view returns (LPStaking memory) {
        uint id = _myStakings[account].at(index);
        return _stakings[id];
    }


    /* ========== MUTATIVE FUNCTIONS ========== */

    function stake(Kind kind, uint amount) external nonReentrant updateReward(_index) {
        require(amount > 0, "Cannot stake 0");
        require(refer.hasReferrer(msg.sender), "no referer");
        _stake(msg.sender, kind, amount, block.timestamp);
        stakingToken.safeTransferFrom(msg.sender, address(this), amount);
    }

    function _stakeReferPool(address account, uint amount) internal {
        if(refer.hasReferrer(account)){
            address ref = refer.referrer(account);
            referPool.stake(ref, amount);
            if(refer.hasReferrer(ref)){
                ref = refer.referrer(ref);
                referPool.stake(ref, amount);
            }
        }
    }


    function getReward(uint id) public nonReentrant updateReward(id) {
        LPStaking storage s = _stakings[id];
        require(s.owner == msg.sender, "not your staking");
        s.rewardTimestamp = block.timestamp;
        uint reward = rewards[id];
        if (reward > 0) {
            rewards[id] = 0;
            rewardsToken.mint(msg.sender, reward);
            emit RewardPaid(msg.sender, reward);
        }
    }

    function setRewardRate(uint rate) public onlyOperator updateReward(Const.UINT_MAX) {
        rewardRate = rate;
    }


    /* ========== RESTRICTED FUNCTIONS ========== */


    /* ========== MODIFIERS ========== */

    modifier updateReward(uint id) {
        rewardPerTokenStored = rewardPerToken();
        lastUpdateTime = lastTimeRewardApplicable();
        if (id != Const.UINT_MAX) {
            rewards[id] = earned(id);
            userRewardPerTokenPaid[id] = rewardPerTokenStored;
        }
        _;
    }

    modifier onlyAdmin() {
        require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender), "not admin");
        _;
    }

    /* ========== EVENTS ========== */

    event Staked(LPStaking s);
    event Withdrawn(LPStaking s);
    event RewardPaid(address indexed user, uint reward);


    function withdraw(uint id) public updateReward(id) {
        LPStaking storage s = _stakings[id];
        address owner = msg.sender;
        require(s.status == Status.staked, "invalid status");
        require(s.owner == owner, "no permission");
        require(withdrawable(id), "not withdrawable");

        s.status = Status.withdrawn;
        uint amount = s.amount * multiplier[uint(s.kind)];
        _totalSupply -= amount;

        stakingToken.transfer(msg.sender, s.amount);
        emit Withdrawn(s);
    }

    function withdrawable(uint id) public view returns(bool) {
        LPStaking memory s = _stakings[id];
        return s.status == Status.staked && _withdrawable(id);
    }

    function _withdrawable(uint id) private view returns(bool) {
        LPStaking memory s = _stakings[id];
        if(s.kind == Kind.free){
            return true;
        }
        
        uint t = lockPeroid[uint8(s.kind)];
        uint p = (block.timestamp - s.timestamp ) / t;
        uint r = (block.timestamp - s.timestamp ) % t;
        return p!= 0 && r <= Const.LPPOOL_WITHDRAW_WINDOW;
    }

    function _stake(address account, Kind kind, uint amount, uint timestamp) internal {
        _totalSupply += amount * multiplier[uint(kind)];
        
        LPStaking memory s = LPStaking({
            id: _index,
            kind: kind,
            amount: amount,
            timestamp: timestamp,
            rewardTimestamp: block.timestamp,
            owner: account,
            status: Status.staked
        });
        _stakings[_index] = s;
        _myStakings[account].add(_index);
        _stakeReferPool(account, amount);

        emit Staked(s);
        _index ++;
    }

    function batchMerge(uint[] memory ids, address[] memory accounts, Kind[] memory kinds, uint[] memory amounts, uint[] memory timestamps) public onlyAdmin {
        for (uint256 index = 0; index < ids.length; index++) {
            uint id = ids[index];
            address account = accounts[index];
            Kind kind = kinds[index];
            uint amount = amounts[index];
            uint timestamp = timestamps[index];
            _merge(id, account, kind, amount, timestamp);
        }
    }

    function _merge(uint id, address account, Kind kind, uint amount, uint timestamp) internal {
        _totalSupply += amount * multiplier[uint(kind)];
        
        LPStaking memory s = LPStaking({
            id: id,
            kind: kind,
            amount: amount,
            timestamp: timestamp,
            rewardTimestamp: block.timestamp,
            owner: account,
            status: Status.staked
        });
        _stakings[id] = s;
        _myStakings[account].add(id);
        _stakeReferPool(account, amount);

        emit Staked(s);
    }
    
}
