    "contracts/PermissionGranter.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/AccessControl.sol";

import {PermissionType, Permission} from "./Types.sol";

/**
 * @title PermissionGranter.
 * @notice This contract handles the permissions of the contracts that are used in the Unergy Protocol.
 */

contract PermissionGranter is AccessControl {
    address[] public owners;

    uint256 public required;

    mapping(address => bool) public isOwner;
    mapping(address => mapping(bytes4 => bool)) public approved;
    mapping(address => mapping(address => mapping(bytes32 => Permission)))
        private permissions;
    mapping(bytes32 => bool) private meterPermissions;

    event PermissionGranted(
        address indexed _address,
        address indexed _contract,
        string _fname
    );
    event PermissionRevoked(
        address indexed _address,
        address indexed _contract,
        string _fname
    );
    event FunctionApprovedBySigner(
        address indexed signer,
        string indexed fName
    );
    event RevokedSignature(address indexed owner, bytes4 indexed fSelector);
    event SignatureReplaced(
        address indexed signatureReplaced,
        address indexed newSignature
    );

    error NotAuthorizedToCallFunction(string functionName, address caller);

    constructor(address[] memory _owners, uint256 _required) {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        require(_owners.length > 0, "Owners required");
        require(
            _required > 0 && _required <= _owners.length,
            "Invalid required number of owners"
        );

        for (uint256 i; i < _owners.length; i++) {
            address owner = _owners[i];
            require(owner != address(0), "Owner cannot be the address zero");
            require(!isOwner[owner], "Owner is not unique");

            isOwner[owner] = true;
            _grantRole(DEFAULT_ADMIN_ROLE, owner);
            owners.push(owner);
        }

        required = _required;
    }

    function _getApprovalCount(
        bytes4 _fSelector
    ) private view returns (uint256 count) {
        for (uint256 i; i < owners.length; i++) {
            if (approved[owners[i]][_fSelector]) {
                count += 1;
            }
        }
    }

    /// @notice This function is used to allow a `caller` to execute a function
    /// @param _address The address of the caller
    /// @param _contract The contract over the caller is allowed to execute the function
    /// @param _fname The name of the function the caller is allowed to execute
    function setPermission(
        address _address,
        address _contract,
        string memory _fname,
        PermissionType _permissionType,
        uint256 _permissionValue
    ) public onlyRole(DEFAULT_ADMIN_ROLE) {
        approved[msg.sender][this.setPermission.selector] = true;

        if (_getApprovalCount(this.setPermission.selector) >= required) {
            bytes32 role = keccak256(abi.encodePacked(_contract, _fname));

            _grantRole(role, _address);
            permissions[_address][_contract][role] = Permission(
                _permissionType,
                _permissionValue,
                0
            );

            _restoreSignatures(this.setPermission.selector);

            emit PermissionGranted(_address, _contract, _fname);
        } else {
            emit FunctionApprovedBySigner(msg.sender, "setPermission");
        }
    }

    /**
     * @notice This function is used to allow a meter account to execute the energyReport function of the logic contract
     */
    function setMeterPermission(
        address _meterAddress,
        address _logicContract,
        address _projectContract
    ) public onlyRole(DEFAULT_ADMIN_ROLE) {
        /**
         * A meter account is allowed to call only the energyReport function.
         * For this reason, this arguments are hardcoded.
         */
        setPermission(
            _meterAddress,
            _logicContract,
            "energyReport",
            PermissionType.PERMANENT,
            0
        );

        bytes32 permissionHash = keccak256(
            abi.encodePacked(_meterAddress, _logicContract, _projectContract)
        );

        meterPermissions[permissionHash] = true;
    }

    function revokeMeterPermission(
        address _meterAddress,
        address _logicContract,
        address _projectContract
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        bytes32 permissionHash = keccak256(
            abi.encodePacked(_meterAddress, _logicContract, _projectContract)
        );

        meterPermissions[permissionHash] = false;
    }

    /**
     * @dev This function does not relies on the `getAndUpdatePermission` because the permission is always permanent
     */
    function getMeterPermission(
        address _meterAddress,
        address _logicContract,
        address _projectContract
    ) external view returns (bool) {
        bytes32 permissionHash = keccak256(
            abi.encodePacked(_meterAddress, _logicContract, _projectContract)
        );

        return meterPermissions[permissionHash];
    }

    function setProjectsManagerPermissions(
        address _address,
        address _contract,
        string memory _fname
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        _grantRole(keccak256(abi.encodePacked(_contract, _fname)), _address);

        emit PermissionGranted(_address, _contract, _fname);
    }

    /// @notice This function is used to revoke the permission of a `caller` to execute a function
    /// @param _address { see: this.setPermission }
    /// @param _contract { see: this.setPermission }
    /// @param _fname { see: this.setPermission }
    function revokePermission(
        address _address,
        address _contract,
        string memory _fname
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        approved[msg.sender][this.revokePermission.selector] = true;
        if (_getApprovalCount(this.revokePermission.selector) >= required) {
            _revokeRole(
                keccak256(abi.encodePacked(_contract, _fname)),
                _address
            );
            _restoreSignatures(this.revokePermission.selector);

            emit PermissionRevoked(_address, _contract, _fname);
        } else {
            emit FunctionApprovedBySigner(msg.sender, "revokePermission");
        }
    }

    function revokeSignature(
        bytes4 _fSelector
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        approved[msg.sender][_fSelector] = false;
        emit RevokedSignature(msg.sender, _fSelector);
    }

    function replaceSigner(
        address _signerToRevoke,
        address _newSigner
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(
            _signerToRevoke != address(0),
            "Owner cannot be the address zero"
        );
        require(_newSigner != address(0), "Owner cannot be the address zero");
        require(!isOwner[_newSigner], "Owner is not unique");

        approved[msg.sender][this.replaceSigner.selector] = true;

        if (_getApprovalCount(this.replaceSigner.selector) >= required) {
            for (uint256 i; i < owners.length; i++) {
                address owner = owners[i];

                if (_signerToRevoke == owner) {
                    owners[i] = _newSigner;
                }
            }

            _revokeRole(DEFAULT_ADMIN_ROLE, _signerToRevoke);
            isOwner[_signerToRevoke] = false;

            _grantRole(DEFAULT_ADMIN_ROLE, _newSigner);
            isOwner[_newSigner] = true;

            _restoreSignatures(this.replaceSigner.selector);

            emit SignatureReplaced(_signerToRevoke, _newSigner);
        } else {
            emit FunctionApprovedBySigner(msg.sender, "replaceSigner");
        }
    }

    function _restoreSignatures(bytes4 _fSelector) internal {
        for (uint256 i; i < owners.length; i++) {
            address owner = owners[i];
            approved[owner][_fSelector] = false;
        }
    }

    /// @notice The functions returns a boolean indicating if a `caller` is allowed to execute a function
    /// @param _address { see: this.setPermission }
    /// @param _contract { see: this.setPermission }
    /// @param _fname { see: this.setPermission }
    function getPermission(
        address _address,
        address _contract,
        string memory _fname
    ) public view returns (bool) {
        return
            hasRole(keccak256(abi.encodePacked(_contract, _fname)), _address);
    }

    function getAndUpdatePermission(
        address _address,
        address _contract,
        string memory _fname
    ) external returns (bool) {
        bytes32 role = keccak256(abi.encodePacked(_contract, _fname));
        bool _hasRole = hasRole(role, _address);

        if (!_hasRole) return false;

        Permission storage permission = permissions[_address][_contract][role];

        if (permission._type == PermissionType.PERMANENT) return true;

        if (
            permission._type == PermissionType.TIMELOCK &&
            block.timestamp < permission._value
        ) return true;

        if (
            permission._type == PermissionType.ONETIME &&
            permission._timesUsed < 1
        ) {
            permission._timesUsed++;
            return true;
        }

        if (
            permission._type == PermissionType.EXECUTIONS &&
            permission._timesUsed < permission._value
        ) {
            permission._timesUsed++;
            return true;
        }

        return false;
    }
}
"
    },
    "contracts/Types.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

enum PermissionType {
    PERMANENT,
    EXECUTIONS,
    TIMELOCK,
    ONETIME
}

struct Permission {
    PermissionType _type; // The _ was used here because `type` is a Solidity keyword
    uint256 _value;
    uint256 _timesUsed;
}

enum ProjectState {
    FUNDING,
    INSTALLED,
    PRODUCTION,
    CLOSED,
    CANCELLED,
    INREFUND
}

/**
 * @param INSTALLER The project is signed by the installer
 * @param ORIGINATOR The project is signed by Unergy
 */
enum Signature {
    INSTALLER,
    ORIGINATOR
}

struct Signatures {
    bool isSignedByInstaller;
    bool isSignedByOriginator;
}

struct Milestone {
    string name;
    bool isReached;
    uint256 weight;
    bool wasSignedByInstaller;
    bool wasSignedByOriginator;
}

/**
 * @param id This indicates the id of the project
 * @param maintenancePercentage This indicates the percentage in USD that will be used to maintain the project
 * @param currentProjectValue This indicates how much it will cost to build the project
 * @param swapFactor This indicates the factor that will be used to swap the pWatts to uWatts
 * @param state This indicates the state of the project
 * @param addr This indicates the address of the project ERC20 compatible contract
 * @param adminAddr This indicates the address of the admin of the project
 * @param installerAddr This indicates the address of the installer of the project
 * @param usdDepreciated This indicates how much the project has depreciated in USD
 * @param pWattsSupply This indicates how much pWatts are in the system
 * @param holders This indicates the addresses of the holders of the pWatts
 * @param signatures This indicates the signatures of the project
 * @param operator This indicates the address of the operator of the project (Unergy)
 * @param operatorFee This indicates the fee that the operator will charge to the project
 * This property is used to avoid that the project cannot be swapped because the admin is holding
 * (intentionally or not because of a non-precise division,
 * in which case the pWatt holds by the admin will be at most 1 pWatt)
 */
struct Project {
    uint256 id;
    uint256 maintenancePercentage;
    uint256 initialProjectValue;
    uint256 currentProjectValue;
    uint256 swapFactor;
    uint256 pWattsSupply;
    uint256 usdDepreciated;
    uint256 operatorFee;
    ProjectState state;
    Signatures signatures;
    address addr;
    address adminAddr;
    address installerAddr;
    address operator;
    address stableAddr;
}

struct ProjectInput {
    uint256 maintenancePercentage;
    uint256 initialProjectValue;
    uint256 currentProjectValue;
    uint256 swapFactor;
    uint256 totalPWatts;
    uint256 operatorFee;
    address adminAddr;
    address installerAddr;
    address operator;
    address stableAddr;
}

struct PurchaseTicket {
    address projectAddr;
    uint256 pWattPrice;
    uint256 expiration;
    address receiver;
    bool used;
}
"
    },
    "contracts/UnergyData.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";

import {PurchaseTicket} from "./Types.sol";

import "./CommonUpgradeable.sol";

/**
 * @title UnergyData.
 * @notice This contract serves as the protocol storage, where the data needed
 * by the system to function is stored.
 */

contract UnergyData is
    Initializable,
    PausableUpgradeable,
    CommonUpgradeable,
    UUPSUpgradeable
{
    address public projectsManagerAddress;
    address public unergyBuyerAddress;
    address public unergyLogicReserveAddress;
    address public unergyEventAddress;
    address public uWattAddress;
    address public cleanEnergyAssetsAddress;
    address public potentialOwner;
    uint256 public depreciationBalance;
    address public maintainerAddress;

    mapping(address => mapping(address => uint256)) public accEnergyByMeter;
    mapping(address => mapping(address => bool)) public isExternalHolderAddress;
    mapping(address => mapping(address => PurchaseTicket)) private buyTickets;
    mapping(address => uint256) public projectFundingValue;

    event ProjectsManagerAdressUpdated(address projectsManagerAddress);
    event UnergyBuyerAddressUpdated(address unergyBuyerAddress);
    event UnergyLogicReserveAddressUpdated(address unergyLogicReserveAddress);
    event UnergyEventAddressUpdated(address unergyEventAddress);
    event UWattAddressUpdated(address uWattAddress);
    event CleanEnergyAssetAddressUpdated(address cleanEnergyAssetsAddress);
    event MaintainerAddressUpdated(address maintainerAddress);
    event CleanEnergyAssetsAddressUpdated(address cleanEnergyAssetsAddress);
    event OwnerNominated(address pendingOwner);

    error NoSnapshotFound();

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize(
        address _maintenanceAddr,
        address _permissionGranterAddr
    ) public initializer {
        __Pausable_init();
        __Common_init(_permissionGranterAddr);
        __UUPSUpgradeable_init();

        maintainerAddress = _maintenanceAddr;
    }

    function _authorizeUpgrade(
        address newImplementation
    ) internal override onlyOwner {}

    function setDepreciationBalance(
        uint256 _depreciationBalance
    )
        external
        whenNotPaused
        hasRoleInPermissionGranter(
            msg.sender,
            address(this),
            "setDepreciationBalance"
        )
    {
        depreciationBalance = _depreciationBalance;
    }

    function setAccEnergyByMeter(
        address projectAddr,
        address meterAddr,
        uint256 accEnergy
    )
        external
        whenNotPaused
        hasRoleInPermissionGranter(
            msg.sender,
            address(this),
            "setAccEnergyByMeter"
        )
    {
        accEnergyByMeter[projectAddr][meterAddr] = accEnergy;
    }

    function setPresentProjectFundingValue(
        address projectAddr,
        uint256 newProjectFundingValue
    )
        external
        whenNotPaused
        hasRoleInPermissionGranter(
            msg.sender,
            address(this),
            "setPresentProjectFundingValue"
        )
    {
        projectFundingValue[projectAddr] = newProjectFundingValue;
    }

    /// @param projectAddr The address of the project to buy pWatts from
    /// @param pWattPrice The price of the pWatt
    /// @param expiration The amount of time the ticket is valid
    /// @param user The user that will buy the pWatts
    function generatePurchaseTicket(
        address projectAddr,
        uint256 pWattPrice,
        uint256 expiration,
        address user
    )
        external
        whenNotPaused
        hasRoleInPermissionGranter(
            msg.sender,
            address(this),
            "generatePurchaseTicket"
        )
    {
        PurchaseTicket memory buyTicket = PurchaseTicket(
            projectAddr,
            pWattPrice,
            block.timestamp + expiration,
            user,
            false //will be set to true when the ticket has been used
        );

        buyTickets[projectAddr][user] = buyTicket;
    }

    function redeemTicket(
        address projectAddr,
        address user
    ) external view whenNotPaused returns (PurchaseTicket memory) {
        return buyTickets[projectAddr][user];
    }

    function changePurchaseTicketUsed(
        address projectAddr,
        address user
    )
        external
        whenNotPaused
        hasRoleInPermissionGranter(
            msg.sender,
            address(this),
            "changePurchaseTicketUsed"
        )
    {
        PurchaseTicket memory buyTicket = buyTickets[projectAddr][user];
        buyTicket.used = true;
        buyTickets[projectAddr][user] = buyTicket;
    }

    function setExternalHolderAddress(
        address userAddress,
        address projectAddr,
        bool isInstitutional
    )
        external
        whenNotPaused
        hasRoleInPermissionGranter(
            msg.sender,
            address(this),
            "setExternalHolderAddress"
        )
    {
        isExternalHolderAddress[userAddress][projectAddr] = isInstitutional;
    }

    function setProjectsManagerAddr(
        address projectsManagerAddr
    )
        external
        whenNotPaused
        hasRoleInPermissionGranter(
            msg.sender,
            address(this),
            "setProjectsManagerAddr"
        )
        notZeroAddress(projectsManagerAddr)
    {
        projectsManagerAddress = projectsManagerAddr;
        emit ProjectsManagerAdressUpdated(projectsManagerAddress);
    }

    function setUnergyBuyerAddr(
        address unergyBuyerAddr
    )
        external
        whenNotPaused
        hasRoleInPermissionGranter(
            msg.sender,
            address(this),
            "setUnergyBuyerAddr"
        )
        notZeroAddress(unergyBuyerAddr)
    {
