    event CartBought(uint256[] cart, address user);
    event TicketBoughtForUser(address user, uint256 ticketId);

    constructor(string memory _baseURI, address mentaAddress, string memory _name, string memory _symbol, string memory _eventId) ERC721(_name, _symbol){
        baseURI = _baseURI;
        eventId = _eventId;
        _setupRole(DEFAULT_ADMIN_ROLE, mentaAddress);
    }

    // @dev Function that sets the TokenURI for each tokenId
    function tokenURI(uint256 tokenId)
        public
        view
        virtual
        override
        returns (string memory)
    {
        return
            bytes(baseURI).length > 0
                ? string(abi.encodePacked(baseURI, eventId, "/metadata/polygon/token/", tokenId.toString()))
                : baseURI;
    }

    function supportsInterface(bytes4 interfaceId) public view virtual override(ERC721, AccessControl) returns (bool) {
        return super.supportsInterface(interfaceId);
    }

        /// @dev Restricted to members of the community.
    modifier onlyMember() {
        require(isMember(msg.sender), "Restricted to members.");
        _;
    }

    // ERC721 functions
    function mint(address _to, uint256 _tokenId) public onlyMember {
        _mint(_to, _tokenId);
    }

    /// @dev Return `true` if the `account` belongs to the community.
    function isMember(address account) public view virtual returns (bool) {
        return hasRole(DEFAULT_ADMIN_ROLE, account);
    }

    /// @dev Add a member of the community.
    function addMember(address account) public virtual onlyMember {
        grantRole(DEFAULT_ADMIN_ROLE, account);
    }

    /// @dev Add many members to DEFAULT_ADMIN_ROLE
    function addManyMembers(address[] calldata addr) public onlyMember {
        for (uint256 i = 0; i < addr.length; i++) {
            grantRole(DEFAULT_ADMIN_ROLE, addr[i]);
        }
    }

    // @dev this function is for menta to mint tickets for users that bought the ticket with credit/debit
    // @param ticketAmount => amount of tickets you want to buy
    function buyTicketForUser(address user, uint256 id) public payable onlyMember {
        _mint(user, id);
        emit TicketBoughtForUser(user, id);    
    }

    // @dev this function is for menta to airdrop tickets to users
    // @param _to => is an array that hodls the users addresses like this (¨0xfda..¨, ¨0xfdas¨)
    // @param _id => id of ticket 
    // @param _amount => amount you want to send to each user
    function airdrop(address[] calldata _to, uint256 _id) public onlyMember{
        for (uint256 i = 0; i < _to.length; i++) {
            _mint(_to[i], _id);
        }
    }

    // @dev This function lets you buy many tickets from different id's
    // @param uint[] memory ids => this function lets you add a huge cart of a user
    function mintMultiple(address recipient, uint[] memory ids) public onlyMember {
        for (uint i = 0; i < ids.length; i++) {
            _mint(recipient, ids[i]);
        }
        emit CartBought(ids, recipient);
    }

    // Set the baseURI
    function setBaseURI(string memory _newBaseURI) public onlyMember {
        baseURI = _newBaseURI;
    }

}
