    "contracts/ERC1155/UNXResourcesV2.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.16;

import "@openzeppelin/contracts-upgradeable/token/ERC1155/ERC1155Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC1155/extensions/ERC1155BurnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC1155/extensions/ERC1155SupplyUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "../GenericTypedMessage.sol";
import "../OperatorFilter/IOperatorFilterRegistry.sol";

contract UNXResourcesV2 is
    Initializable,
    ERC1155Upgradeable,
    AccessControlUpgradeable,
    PausableUpgradeable,
    ERC1155BurnableUpgradeable,
    ERC1155SupplyUpgradeable,
    ReentrancyGuardUpgradeable,
    GenericTypedMessage
{
    bytes32 public constant URI_SETTER_ROLE = keccak256("URI_SETTER_ROLE");
    bytes32 public constant PAUSER_ROLE = keccak256("PAUSER_ROLE");
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    bytes32 private constant _AUTHORIZED_BATCH_MINT_TYPEHASH = keccak256("Authorization(address signer,address account,uint256[] ids,uint256[] amounts,uint256 deadline,bytes32 authorization)");

    string public name;
    string public symbol;
    address public owner;
    mapping (uint256 => string) private _tokenURIs;
    string private _uri;

    IOperatorFilterRegistry public _OPERATOR_FILTER_REGISTRY;

    uint256[] private _registeredTokens;

    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);

    struct Authorization {
        bytes32 authorization;
        address account;
        uint256[] ids;
        uint256[] amounts;
        uint32 deadline;
        address signer;
        uint8 v;
        bytes32 r;
        bytes32 s;
    }

    struct Balance {
        uint256 id;
        uint256 amount;
    }

    modifier onlyRegisteredIds(uint256[] memory ids) {
        for (uint i = 0; i < ids.length; i++) {
            require(_isRegistered(ids[i]), "UNXResources: token is not registered");
        }
        _;
    }

    /**
     * @dev A helper function to check if an operator is allowed.
     */
    modifier onlyAllowedOperator(address from) virtual {
        // Allow spending tokens from addresses with balance
        // Note that this still allows listings and marketplaces with escrow to transfer tokens if transferred
        // from an EOA.
        if (from != msg.sender) {
            _checkFilterOperator(msg.sender);
        }
        _;
    }

    /**
     * @dev A helper function to check if an operator approval is allowed.
     */
    modifier onlyAllowedOperatorApproval(address operator) virtual {
        _checkFilterOperator(operator);
        _;
    }

    function initialize(
        string calldata _name,
        string calldata _symbol,
        address _operator
    )
        public
        initializer
    {
        __ERC1155_init("");
        __AccessControl_init();
        __Pausable_init();
        __ERC1155Burnable_init();
        __ERC1155Supply_init();
        __GenericTypedMessage_init();
        __ReentrancyGuard_init();

        _grantRole(DEFAULT_ADMIN_ROLE, _operator);

        name = _name;
        symbol = _symbol;
        owner = _operator;
        _uri = "https://ipfs.io/ipfs/";

        emit OwnershipTransferred(address(0), _operator);
    }

    /**************************************
     ROLE-RESTRICTED WRITE FUNCTIONS
    ***************************************/

    function setURI(string memory newuri) public onlyRole(URI_SETTER_ROLE) {
        _setURI(newuri);
    }

    function pause() public onlyRole(PAUSER_ROLE) {
        _pause();
    }

    function unpause() public onlyRole(PAUSER_ROLE) {
        _unpause();
    }

    function mint(
        address account,
        uint256 id,
        uint256 amount,
        bytes memory data
    )
        public
        onlyRole(MINTER_ROLE)
    {
        require(_isRegistered(id), "UNXResources: token is not registered");

        _mint(account, id, amount, data);
    }

    function mintBatch(
        address to,
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data
    )
        public
        onlyRegisteredIds(ids)
        onlyRole(MINTER_ROLE)
    {
        _mintBatch(to, ids, amounts, data);
    }

    function authorizedMintBatch(
        Authorization calldata _authorization
    )
        external
        nonReentrant
        whenNotPaused
        onlyRegisteredIds(_authorization.ids)
    {
        require(hasRole(MINTER_ROLE, _authorization.signer), "UNXResources: signer is not a MINTER");
        require(_verifySignature(_authorization), "UNXResources: invalid authorization");

        _mintBatch(
            _authorization.account,
            _authorization.ids,
            _authorization.amounts,
            new bytes(0)
        );
    }

    function _verifySignature(
        Authorization memory _authorization
    )
        internal
        returns (bool)
    {
        bytes32 structHash = keccak256(
            abi.encode(
                _AUTHORIZED_BATCH_MINT_TYPEHASH,
                _authorization.signer,
                _authorization.account,
                keccak256(abi.encodePacked(_authorization.ids)),
                keccak256(abi.encodePacked(_authorization.amounts)),
                _authorization.deadline,
                _authorization.authorization
            )
        );

        return _verifyTypedMessage(
            structHash,
            _authorization.authorization,
            _authorization.deadline,
            _authorization.signer,
            _authorization.v,
            _authorization.r,
            _authorization.s
        );
    }

    function registerId(
        uint256 id,
        string calldata cid
    )
        external
        onlyRole(MINTER_ROLE)
    {
        require(!_isRegistered(id), "UNXResources: token already registered");

        _setTokenURI(id, cid);
    }

    function registerIdBatch(
        uint256[] calldata ids,
        string[] calldata cids
    )
        external
        onlyRole(MINTER_ROLE)
    {
        require(ids.length == cids.length, "UNXResources: ids and cids length mismatch");

        for (uint i = 0; i < ids.length; i++) {
            require(!_isRegistered(ids[i]), "UNXResources: token already registered");
            _setTokenURI(ids[i], cids[i]);
        }
    }

    function updateTokenURI(
        uint256 id,
        string memory cid
     )
        external
        onlyRole(ADMIN_ROLE)
    {
        require(_isRegistered(id), "UNXResources: token not registered");

        _setTokenURI(id, cid);
    }

    function setRegisteredIds(
        uint256[] memory _ids
     )
        external
        onlyRole(ADMIN_ROLE)
    {
        _registeredTokens = _ids;
    }

    /**
     * @dev Internal function to set the token cid for a given token.
     * @param tokenId uint256 ID of the token to set its URI
     * @param cid string ipfs cid to assign
     */
    function _setTokenURI(uint256 tokenId, string memory cid) internal {
        _tokenURIs[tokenId] = string.concat(_uri, cid);

        _registeredTokens.push(tokenId);

        emit URI(_tokenURIs[tokenId], tokenId);
    }

    function baseURI() public view returns (string memory) {
        return super.uri(0);
    }

    /**
     * @dev Updates owner
     * @param _newOwner new owner's address
     */
    function setOwner(address _newOwner)
      external
      onlyRole(ADMIN_ROLE)
    {
        require(_newOwner != address(0), "UNXResources: invalid owner address");

        emit OwnershipTransferred(owner, _newOwner);

        owner = _newOwner;
    }

    function collectionOf(
        address account
    ) external view returns (Balance[] memory) {
        uint256[] memory tokens = _registeredTokens;
        Balance[] memory balances = new Balance[](tokens.length);

        for (uint256 i = 0; i < tokens.length; ++i) {
            balances[i] = Balance(tokens[i], balanceOf(account, tokens[i]));
        }

        return balances;
    }

    function _beforeTokenTransfer(
        address operator,
        address from,
        address to,
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data
    )
        internal
        override(ERC1155Upgradeable, ERC1155SupplyUpgradeable)
        whenNotPaused
    {
        super._beforeTokenTransfer(operator, from, to, ids, amounts, data);
    }

    function uri(uint256 _id) public view override returns (string memory) {
        require(_isRegistered(_id), "UNXResources: token not registered");

        return _tokenURI(_id);
    }

    function isRegistered(
        uint256 id
    ) external view returns (bool) {
        return _isRegistered(id);
    }

    function _isRegistered(uint256 id) internal view returns (bool) {
        return bytes(_tokenURIs[id]).length != 0;
    }

    /**
     * @dev Returns an URI for a given token ID. If tokenId is 0, returns _uri
     * @param tokenId uint256 ID of the token to query
     */
    function _tokenURI(uint256 tokenId) internal view returns (string memory) {
        return _tokenURIs[tokenId];
    }

    function setOperatorRegistry(
      IOperatorFilterRegistry _registry
    )
      external
      onlyRole(ADMIN_ROLE)
    {
        _OPERATOR_FILTER_REGISTRY = _registry;
    }

     /**
     * @dev A helper function to check if an operator is allowed.
     */
    function _checkFilterOperator(address operator) internal view virtual {
        if (address(_OPERATOR_FILTER_REGISTRY).code.length > 0) {
            require(_OPERATOR_FILTER_REGISTRY.isOperatorAllowed(address(this), operator), "Operator not allowed");
        }
    }

    /**
     * @dev See {IERC1155-setApprovalForAll}.
     *      Added modifier ensures that the operator is allowed by the OperatorFilterRegistry.
     */
    function setApprovalForAll(address operator, bool approved) public override onlyAllowedOperatorApproval(operator) {
        super.setApprovalForAll(operator, approved);
    }

    /**
     * @dev See {IERC1155-safeTransferFrom}.
     *      Added modifier ensures that the operator is allowed by the OperatorFilterRegistry.
     */
    function safeTransferFrom(address from, address to, uint256 tokenId, uint256 amount, bytes memory data)
        public
        override
        onlyAllowedOperator(from)
    {
        super.safeTransferFrom(from, to, tokenId, amount, data);
    }

    /**
     * @dev See {IERC1155-safeBatchTransferFrom}.
     *      Added modifier ensures that the operator is allowed by the OperatorFilterRegistry.
     */
    function safeBatchTransferFrom(
        address from,
        address to,
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data
    ) public virtual override onlyAllowedOperator(from) {
        super.safeBatchTransferFrom(from, to, ids, amounts, data);
    }

    // The following functions are overrides required by Solidity.
    function supportsInterface(bytes4 interfaceId)
        public
        view
        override(ERC1155Upgradeable, AccessControlUpgradeable)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }
}
"
    },
    "contracts/GenericTypedMessage.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "@openzeppelin/contracts-upgradeable/utils/cryptography/draft-EIP712Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/cryptography/ECDSAUpgradeable.sol";

/**
 * @dev Helper contract to verify signed typed messages
 */
contract GenericTypedMessage is EIP712Upgradeable {

    mapping(address => mapping(bytes32 => bool)) internal _authorizationStates;

    event AuthorizationUsed(address indexed authorizer, bytes32 indexed nonce);

    function __GenericTypedMessage_init() internal onlyInitializing {
        __EIP712_init("GenericTypedMessage", "1");
    }

    /**
     * @dev check whether a typed data signature is valid.
     * `_structHash` should be calculated using the type hash and typed data parameters, eg.:
     *    bytes32 _PERMIT_TYPEHASH = keccak256("Permit(address owner,bytes32 authorization,address operator,bool approved,bytes32 nonce,uint256 deadline)");
     *    bytes32 structHash = keccak256(
     *       abi.encode(
     *           _PERMIT_TYPEHASH, => typehash
     *           signer, => typed data param
     *           authorization, => typed data param
     *           operator, => typed data param
     *           approved, => typed data param
     *           nonce, => typed data param
     *           deadline => typed data param
     *       )
     *   );
     * This implementation uses non-sequential authorizations instead of nonces. Each time
     * the function processes a valid signature, the authorization is burned and cannot be used again.
     * @notice It's the inheriting contract resposibility to include the authorization in the struct hash
     * otherwise the authorization logic will have no effect
     * @param _structHash keccak256-abi-encoded message
     * @param _authorization one-time authorization
     * @param _deadline signature expiration time
     * @param _signer the message signer
     * @param _v signature v
     * @param _r signature r
     * @param _s signature s
     * @return true if checks passed
     */
    function _verifyTypedMessage(
        bytes32 _structHash,
        bytes32 _authorization,
        uint32 _deadline,
        address _signer,
        uint8 _v,
        bytes32 _r,
        bytes32 _s
    )
        internal
        returns (bool)
    {
        require(block.timestamp <= _deadline, "GenericTypedMessage: expired deadline");
        require(
            !_authorizationStates[_signer][_authorization],
            "GenericTypedMessage: authorization already used"
        );

        bytes32 hash = _hashTypedDataV4(_structHash);

        address signer = ECDSAUpgradeable.recover(hash, _v, _r, _s);
        require(signer == _signer, "GenericTypedMessage: invalid signature");

        _authorizationStates[_signer][_authorization] = true;

        emit AuthorizationUsed(_signer, _authorization);

        return true;
    }

    /**
     * @dev returns the state of a given authorization
     * @param authorizer signer address
     * @param authorization the authorization
     * @return the autorization state
     */
    function authorizationState(address authorizer, bytes32 authorization)
        external
        view
        returns (bool)
    {
        return _authorizationStates[authorizer][authorization];
    }

    /**
     * @dev See {IERC20Permit-DOMAIN_SEPARATOR}.
     */
    function DOMAIN_SEPARATOR() external view returns (bytes32) {
        return _domainSeparatorV4();
    }

}
"
