    "contracts/FirstColony.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.22;

import "erc721a-upgradeable/contracts/extensions/ERC721AQueryableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "erc721a/contracts/interfaces/IERC721A.sol";

error FirstColony__NotEnabled();
error FirstColony__QtyExceedsMaxSupply();
error FirstColony__PaymentInsufficient();

contract FirstColony is
  Initializable,
  UUPSUpgradeable,
  ERC721AQueryableUpgradeable,
  OwnableUpgradeable,
  ReentrancyGuardUpgradeable
{
  /////////////////////
  // State Variables //
  /////////////////////

  uint256 private constant MAX_SUPPLY = 10000;

  /**
   * @notice The price to mint
   */
  uint256 private s_price;

  string private s_baseTokenURI;

  bool private s_mintEnabled;

  /////////////////////
  // Events          //
  /////////////////////

  /**
   * @dev Emitted when minting
   */
  event Minted(address indexed minter, uint256 indexed quantity);

  ////////////////////
  // Overrides      //
  ////////////////////

  /**
   * @notice Start token IDs at 1 instead of 0
   */
  function _startTokenId() internal view virtual override returns (uint256) {
    return 1;
  }

  ////////////////////
  // Main Functions //
  ////////////////////

  /// @custom:oz-upgrades-unsafe-allow constructor
  constructor() {
    _disableInitializers();
  }

  function initialize() public initializer initializerERC721A {
    __ERC721AQueryable_init();
    __Ownable_init();
    __UUPSUpgradeable_init();
    __ReentrancyGuard_init();
    __ERC721A_init("1st Colony", "1STCLNY");

    s_price = 1.5 ether;
  }

  /**
   * @notice Mints tokens to an sender
   * @param qty the quantity to mint
   */
  function mint(uint256 qty) external payable nonReentrant {
    if (!s_mintEnabled) {
      revert FirstColony__NotEnabled();
    }
    if (msg.value < s_price * qty) {
      revert FirstColony__PaymentInsufficient();
    }
    if (_totalMinted() + qty > MAX_SUPPLY) {
      revert FirstColony__QtyExceedsMaxSupply();
    }
    _mint(msg.sender, qty);
    emit Minted(msg.sender, qty);
  }

  /////////////////////
  // Admin Functions //
  /////////////////////

  /**
   * @notice upgrades the contract to a new implementation.
   * @param newImplementation Address of the new implementation.
   */
  function _authorizeUpgrade(
    address newImplementation
  ) internal override onlyOwner {}

  /**
   * @notice Mints tokens to an address
   * @param to the address to airdrop to
   * @param qty the quantity to airdrop
   */
  function airdrop(address to, uint256 qty) external onlyOwner {
    _mint(to, qty);
    emit Minted(to, qty);
  }

  /**
   * @notice Sets whether minting is enabled
   * @param enabled whether minting is enabled
   */
  function setMintEnabled(bool enabled) external onlyOwner {
    s_mintEnabled = enabled;
  }

  /**
   * @notice Sets the price to mint
   * @param newPrice the new price
   */
  function setPrice(uint256 newPrice) external onlyOwner {
    s_price = newPrice;
  }

  function setBaseURI(string memory newUri) external onlyOwner {
    s_baseTokenURI = newUri;
  }

  /**
   * @notice Withdraws funds to owner
   */
  function withdraw() external onlyOwner {
    payable(owner()).transfer(address(this).balance);
  }

  ////////////////////
  // View Functions //
  ////////////////////

  function getMintEnabled() external view returns (bool) {
    return s_mintEnabled;
  }

  function getPrice() external view returns (uint256) {
    return s_price;
  }

  function _baseURI() internal view virtual override returns (string memory) {
    return s_baseTokenURI;
  }
}
"
    },
