abstract contract Ownable is Context {
    address private _owner;

    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);
    constructor() {
        _transferOwnership(_msgSender());
    }
    modifier onlyOwner() {
        _checkOwner();
        _;
    }
    function owner() public view virtual returns (address) {
        return _owner;
    }
    function _checkOwner() internal view virtual {
        require(owner() == _msgSender(), "Ownable: caller is not the owner");
    }
    function renounceOwnership() public virtual onlyOwner {
        _transferOwnership(address(0));
    }
    function transferOwnership(address newOwner) public virtual onlyOwner {
        require(newOwner != address(0), "Ownable: new owner is the zero address");
        _transferOwnership(newOwner);
    }
    function _transferOwnership(address newOwner) internal virtual {
        address oldOwner = _owner;
        _owner = newOwner;
        emit OwnershipTransferred(oldOwner, newOwner);
    }
}

contract Gateway is Ownable {

    enum TokenType{
        ERC20,
        ERC721,
        ERC1155
    }

    struct Bundle {
        TokenType token_type;
        address token;
        uint256 number;
    }

    function swap(address peer1, address peer2, Bundle[] calldata bundle1, Bundle[] calldata bundle2) public onlyOwner {
        for (uint256 i = 0; i<bundle1.length; i++) {
            transfer(peer1, peer2, bundle1[i]);
        }

        for (uint256 i = 0; i<bundle2.length; i++) {
            transfer(peer2, peer1, bundle2[i]);
        }

    }

    function transfer(address from, address to, Bundle calldata bundle) private {
        if (bundle.token_type == TokenType.ERC20) {
            IERC20(bundle.token).transferFrom(from, to, bundle.number);
        } else if (bundle.token_type == TokenType.ERC1155) {
            IERC1155(bundle.token).safeTransferFrom(from, to, bundle.number, 1, new bytes(0));
        } else {
            IERC721(bundle.token).transferFrom(from, to, bundle.number);
        }
    }
}
