      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.1;

import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721Receiver.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/IERC721Metadata.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import "@openzeppelin/contracts/utils/Context.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "@openzeppelin/contracts/utils/introspection/ERC165.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract MGK is Context, ERC165, IERC721, IERC721Metadata, Ownable {
    // TODO: if haven`t another fields - return one line string
    struct TokenMetaData {
        string URI;
    }

    using Address for address;
    using Strings for uint256;

    using Counters for Counters.Counter;
    using EnumerableSet for EnumerableSet.UintSet;

    Counters.Counter private _tokenIdCounter;

    // Token name
    string private _name;

    // Token symbol
    string private _symbol;

    // Token baseURI
    string private _baseURI;

    // Token Total Supply
    uint256 private _totalSupply;

    // Mapping from token ID to owner address
    mapping(uint256 => address) private _owners;

    // Mapping owner address to token count
    mapping(address => uint256) private _balances;

    // Mapping from token ID to approved address
    mapping(uint256 => address) private _tokenApprovals;

    // Mapping from token Id to uri
    mapping(uint256 => string) private uris;

    // Mapping from owner to operator approvals
    mapping(address => mapping(address => bool)) private _operatorApprovals;

    mapping(uint256 => TokenMetaData) private _tokenMetaDatas;
    mapping(address => EnumerableSet.UintSet) private _holderTokens;

    event SetBaseURI(string indexed baseURI);

    constructor(string memory name_, string memory symbol_, uint256 totalSupply_) {
        _name = name_;
        _symbol = symbol_;
        _totalSupply = totalSupply_;
        _tokenIdCounter.increment();
        _baseURI = "ipfs://";
    }

    function totalSupply() public view returns (uint256) {
        return _totalSupply;
    }

    function supportsInterface(bytes4 interfaceId)
        public
        view
        virtual
        override(ERC165, IERC165)
        returns (bool)
    {
        return
            interfaceId == type(IERC721).interfaceId ||
            interfaceId == type(IERC721Metadata).interfaceId ||
            super.supportsInterface(interfaceId);
    }

    function userTokens(address owner)
        external
        view
        virtual
        returns (uint256[] memory)
    {
        require(owner != address(0), "MGK: balance query for the zero address");

        uint256[] memory result = new uint256[](_holderTokens[owner].length());

        for (uint256 i; i < _holderTokens[owner].length(); i++) {
            result[i] = _holderTokens[owner].at(i);
        }
        return result;
    }

    function balanceOf(address owner)
        public
        view
        virtual
        override
        returns (uint256)
    {
        require(owner != address(0), "MGK: address zero is not a valid owner");
        return _balances[owner];
    }

    function ownerOf(uint256 tokenId)
        public
        view
        virtual
        override
        returns (address)
    {
        address owner = _owners[tokenId];
        require(owner != address(0), "MGK: owner query for nonexistent token");
        return owner;
    }

    function name() public view virtual override returns (string memory) {
        return _name;
    }

    function symbol() public view virtual override returns (string memory) {
        return _symbol;
    }

    function tokenURI(uint256 tokenId)
        public
        view
        virtual
        override
        returns (string memory)
    {
        require(_exists(tokenId), "MGK: URI query for nonexistent token");

        string memory _tokenURI = uris[tokenId];
        string memory base = baseURI();

        if (bytes(base).length == 0) {
            return _tokenURI;
        }

        if (bytes(_tokenURI).length > 0) {
            return string(abi.encodePacked(base, _tokenURI));
        }

        return string(abi.encodePacked(base, tokenId.toString()));
    }

    function baseURI() public view virtual returns (string memory) {
        return _baseURI;
    }

    function setBaseURI(string memory baseURI_) external virtual onlyOwner {
        _baseURI = baseURI_;
        emit SetBaseURI(baseURI_);
    }

    function approve(address to, uint256 tokenId) public virtual override {
        address owner = MGK.ownerOf(tokenId);
        require(to != owner, "MGK: approval to current owner");

        require(
            _msgSender() == owner || isApprovedForAll(owner, _msgSender()),
            "MGK: approve caller is not owner nor approved for all"
        );

        _approve(to, tokenId);
    }

    function getApproved(uint256 tokenId)
        public
        view
        virtual
        override
        returns (address)
    {
        require(_exists(tokenId), "MGK: approved query for nonexistent token");

        return _tokenApprovals[tokenId];
    }

    function setApprovalForAll(address operator, bool approved)
        public
        virtual
        override
    {
        _setApprovalForAll(_msgSender(), operator, approved);
    }

    function isApprovedForAll(address owner, address operator)
        public
        view
        virtual
        override
        returns (bool)
    {
        return _operatorApprovals[owner][operator];
    }

    function transferFrom(
        address from,
        address to,
        uint256 tokenId
    ) public virtual override {
        //solhint-disable-next-line max-line-length
        require(
            _isApprovedOrOwner(_msgSender(), tokenId),
            "MGK: transfer caller is not owner nor approved"
        );

        _transfer(from, to, tokenId);
    }

    function safeTransferFrom(
        address from,
        address to,
        uint256 tokenId
    ) public virtual override {
        safeTransferFrom(from, to, tokenId, "");
    }

    function safeTransferFrom(
        address from,
        address to,
        uint256 tokenId,
        bytes memory _data
    ) public virtual override {
        require(
            _isApprovedOrOwner(_msgSender(), tokenId),
            "MGK: transfer caller is not owner nor approved"
        );
        _safeTransfer(from, to, tokenId, _data);
    }

    function _safeTransfer(
        address from,
        address to,
        uint256 tokenId,
        bytes memory _data
    ) internal virtual {
        _transfer(from, to, tokenId);
        require(
            _checkOnERC721Received(from, to, tokenId, _data),
            "MGK: transfer to non ERC721Receiver implementer"
        );
    }

    function _exists(uint256 tokenId) internal view virtual returns (bool) {
        return _owners[tokenId] != address(0);
    }

    function _isApprovedOrOwner(address spender, uint256 tokenId)
        internal
        view
        virtual
        returns (bool)
    {
        require(_exists(tokenId), "MGK: operator query for nonexistent token");
        address owner = MGK.ownerOf(tokenId);
        return (spender == owner ||
            isApprovedForAll(owner, spender) ||
            getApproved(tokenId) == spender);
    }

    // function userTokens(address owner) external view virtual returns (uint[] memory) {
    //     require(owner != address(0), "MGK: balance query for the zero address");

    //     uint[] memory result = new uint[](_holderTokens[owner].length());

    //     for (uint i; i < _holderTokens[owner].length(); i++) {
    //         result[i] = _holderTokens[owner].at(i);
    //     }
    //     return result;
    // }

    function safeMint(address to, string memory uri) public onlyOwner {
        require(_balances[to] == 0, "MGK: you may have one NFT");
        _safeMint(to, _tokenIdCounter.current(), uri, "");
        _tokenIdCounter.increment();
    }

    function _safeMint(
        address to,
        uint256 tokenId,
        string memory uri,
        bytes memory _data
    ) internal {
        uris[tokenId] = uri;
        _mintTo(to, tokenId);
        require(
            _checkOnERC721Received(address(0), to, tokenId, _data),
            "MGK: transfer to non ERC721Receiver implementer"
        );
    }

    function _mintTo(address to, uint256 tokenId) internal virtual {
        require(msg.sender != address(0), "MGK: can mint only owner");

        _balances[to] += 1;
        _owners[tokenId] = to;
        _holderTokens[to].add(tokenId);

        emit Transfer(address(0), to, tokenId);
    }

    function updateURI(uint256 tokenId, string memory uri) public onlyOwner {
        uris[tokenId] = uri;
    }

    function updateUriBatch(uint256[] memory tokenId, string[] memory uri)
        public
        onlyOwner
    {
        for (uint256 i; i < tokenId.length; i++) {
            uris[tokenId[i]] = uri[i];
        }
    }

    function _transfer(
        address from,
        address to,
        uint256 tokenId
    ) internal virtual {
        require(
            MGK.ownerOf(tokenId) == from,
            "MGK: transfer from incorrect owner"
        );
        require(to != address(0), "MGK: transfer to the zero address");

        // Clear approvals from the previous owner
        _approve(address(0), tokenId);

        _balances[from] -= 1;
        _balances[to] += 1;
        _owners[tokenId] = to;
        _holderTokens[from].remove(tokenId);
        _holderTokens[to].add(tokenId);

        emit Transfer(from, to, tokenId);
    }

    function _approve(address to, uint256 tokenId) internal virtual {
        _tokenApprovals[tokenId] = to;
        emit Approval(MGK.ownerOf(tokenId), to, tokenId);
    }

    function _setApprovalForAll(
        address owner,
        address operator,
        bool approved
    ) internal virtual {
        require(owner != operator, "MGK: approve to caller");
        _operatorApprovals[owner][operator] = approved;
        emit ApprovalForAll(owner, operator, approved);
    }

    function _checkOnERC721Received(
        address from,
        address to,
        uint256 tokenId,
        bytes memory _data
    ) private returns (bool) {
        if (to.isContract()) {
            try
                IERC721Receiver(to).onERC721Received(
                    _msgSender(),
                    from,
                    tokenId,
                    _data
                )
            returns (bytes4 retval) {
                return retval == IERC721Receiver.onERC721Received.selector;
            } catch (bytes memory reason) {
                if (reason.length == 0) {
                    revert("MGK: transfer to non ERC721Receiver implementer");
                } else {
                    assembly {
                        revert(add(32, reason), mload(reason))
                    }
                }
            }
        } else {
            return true;
        }
    }
}
