/**
 * @title Interface for collectID core contract
 * @author collectID, AG
 * @custom:site https://collectid.io
 * @custom:email info@collectid.io
 */
interface ICollectIdCore {
  event DelegateTransfer(
    address indexed from,
    address indexed to,
    uint256 indexed tokenId
  );

  function exists(uint256 _tokenId) external view returns (bool);
  function tokenURI(uint256 _tokenId) external view returns (string memory);
  function ownerOf(uint256 _tokenId) external view returns (address);
  function supportsInterface(bytes4 _interfaceId) external returns (bool);

  function burn(
    uint256 _tokenId
  ) external;

  function mint(
    address _to,
    uint256 _tokenId
  ) external;

  function mint(
    address _to,
    uint256 _tokenId,
    string calldata _tokenURI
  ) external;

  function mintMany(
    address _to,
    uint256[] calldata _tokenIds,
    string[] calldata _tokenURIs
  ) external;

  function transferFor(
    bytes calldata _delegationSig,
    address _from,
    address _to,
    uint256 _tokenId
  ) external;
}

