/**
 * @title Token contract for minting and managing collectID assets
 * @dev Tokens are bridgeable to Ethereum mainnet
 * @author collectID, AG
 * @custom:site https://collectid.io
 * @custom:email info@collectid.io
 */
contract CollectIdCore is ICollectIdCore, IChildToken, ERC721, ERC721Enumerable, AccessControl {
  using ECDSA for bytes32;
  using Strings for uint256;

  event WithdrawnBatch(
    address indexed user,
    uint256[] tokenIds
  );

  event TransferWithMetadata(
    address indexed from,
    address indexed to,
    uint256 indexed tokenId,
    bytes metaData
  );

  mapping(uint256 => string) private tokenURIs;
  mapping(uint256 => bool) public uriFrozen;
  mapping(uint256 => bool) public withdrawnTokens;

  uint256 public constant BATCH_LIMIT = 20;
  bytes32 public constant DEPOSITOR_ROLE = keccak256("DEPOSITOR_ROLE");

  /**
   * @dev Sets up owner and depositor role used for bridging
   * @param _childChainManager address of child chain manager contract
   */
  constructor(address _childChainManager) ERC721("collectID", "CID") {
    _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
    _setupRole(DEPOSITOR_ROLE, _childChainManager);
  }

  /**
   * @dev Public function returns whether or not a token exists
   * @param _tokenId uint256 hexadecimal tag ID represented as an integer
   * @return bool flag indicating whether or not the token exists
   */
  function exists(uint256 _tokenId)
    public
    view
    returns (bool)
  {
    return _exists(_tokenId);
  }

  /**
   * @dev Public function overrides ERC721 tokenURI function
   * @param _tokenId uint256 hexadecimal tag ID represented as an integer
   * @return string URI describing additional token data
   */
  function tokenURI(uint256 _tokenId)
    public
    view
    virtual
    override(ICollectIdCore, ERC721)
    returns (string memory)
  {
    require(_exists(_tokenId), "Token does not exist");

    return tokenURIs[_tokenId];
  }

  /**
   * @dev Public function overrides ERC721 ownerOf function
   * @param _tokenId uint256 hexadecimal tag ID represented as an integer
   * @return address owner of the specified token
   */
  function ownerOf(uint256 _tokenId)
    public
    view
    virtual
    override(ICollectIdCore, ERC721)
    returns (address)
  {
    return ERC721.ownerOf(_tokenId);
  }

  /**
   * @dev Admin function for setting token URI
   * @param _tokenId uint256 hexadecimal tag ID represented as an integer
   * @param _tokenURI string representing the token metadata URI
   */
  function setTokenURI(uint256 _tokenId, string calldata _tokenURI)
    public
    onlyRole(DEFAULT_ADMIN_ROLE)
  {
    require(_exists(_tokenId), "Token does not exist");
    require(!uriFrozen[_tokenId], "Token URI is frozen");

    tokenURIs[_tokenId] = _tokenURI;
  }

  /**
   * @dev Admin function for setting multiple token URIs
   * @param _tokenIds uint256[] hexadecimal tag ID represented as an integer
   * @param _tokenURIs string[] representing the token metadata URI
   */
  function setTokenURIs(uint256[] calldata _tokenIds, string[] calldata _tokenURIs)
    public
    onlyRole(DEFAULT_ADMIN_ROLE)
  {
    require(_tokenIds.length == _tokenURIs.length, "Must include a URI for each token");

    for (uint256 i = 0; i < _tokenIds.length; i++) {
      setTokenURI(_tokenIds[i], _tokenURIs[i]);
    }
  }

  /**
   * @dev Admin function for freezing token metadata
   * @param _tokenIds uint256[] hexadecimal tag ID represented as an integer
   */
  function freezeTokenURIs(uint256[] calldata _tokenIds)
    public
    onlyRole(DEFAULT_ADMIN_ROLE)
  {
    for (uint256 i = 0; i < _tokenIds.length; i++) {
      require(bytes(tokenURIs[_tokenIds[i]]).length != 0, "Cannot freeze empty token URI");

      uriFrozen[_tokenIds[i]] = true;
    }
  }

  /**
   * @dev Admin function for burning tokens
   * @dev Required for bridging to and from Ethereum
   * @param _tokenId uint256 token to burn
   */
  function burn(uint256 _tokenId) public onlyRole(DEFAULT_ADMIN_ROLE) {
    _burn(_tokenId);
  }

  /**
   * @dev Public function to mint a new collectible
   * @dev Token ID is equal to the 8 byte tag ID burned into each collectID NFC tag
   * @param _to address of the owner to receive the token
   * @param _tokenId uint256 hexadecimal tag ID represented as an integer
   */
  function mint(address _to, uint256 _tokenId)
    public
    override
    onlyRole(DEFAULT_ADMIN_ROLE)
  {
    require(!withdrawnTokens[_tokenId], "Token is withdrawn");

    _mint(_to, _tokenId);
  }

  /**
   * @dev Public function to mint a new collectible and set metadata
   * @dev Token ID is equal to the 8 byte tag ID burned into each collectID NFC tag
   * @param _to address of the owner to receive the token
   * @param _tokenId uint256 hexadecimal tag ID represented as an integer
   * @param _tokenURI string representing the token metadata URI
   */
  function mint(address _to, uint256 _tokenId, string memory _tokenURI)
    public
    override
    onlyRole(DEFAULT_ADMIN_ROLE)
  {
    require(!withdrawnTokens[_tokenId], "Token is withdrawn");

    _mint(_to, _tokenId);
    tokenURIs[_tokenId] = _tokenURI;
  }

  /**
   * @dev Public function to mint multiple new collectibles
   * @dev Token IDs are equal to the 8 byte tag ID burned into each collectID NFC tag
   * @param _to address of the owner to receive the token
   * @param _tokenIds uint256[] hexadecimal tag IDs represented as integers
   * @param _tokenURIs string[] corresponding URI for each token
   */
  function mintMany(
    address _to,
    uint256[] calldata _tokenIds,
    string[] calldata _tokenURIs
  )
    public
    override
    onlyRole(DEFAULT_ADMIN_ROLE)
  {
    require(_tokenIds.length == _tokenURIs.length, "Must include a URI for each token");

    for (uint256 i = 0; i < _tokenIds.length; i++) {
      mint(_to, _tokenIds[i], _tokenURIs[i]);
    }
  }

  /**
   * @dev Public function to transfer a token on behalf of the sender
   * @param _delegationSig bytes32 message signed by the sender
   * @param _from address of the sender of the token (must be owner of the token)
   * @param _to address of the receiver of the token
   * @param _tokenId uint256 ID of the token to be transferred (must be owned by the sender)
   */
  function transferFor(
    bytes memory _delegationSig,
    address _from,
    address _to,
    uint256 _tokenId
  )
    public
    override
    onlyRole(DEFAULT_ADMIN_ROLE)
  {
    bytes32 _delegationDigest = ECDSA.toEthSignedMessageHash(
      keccak256(abi.encode("Transfer", _tokenId, _to))
    );

    require(
      _isApprovedOrOwner(ECDSA.recover(_delegationDigest, _delegationSig), _tokenId),
      "Signer is not approved or owner"
    );

    _transfer(_from, _to, _tokenId);

    emit DelegateTransfer(_from, _to, _tokenId);
  }

  /**
   * @notice called when token is deposited on root chain
   * @dev Should be callable only by ChildChainManager
   * Should handle deposit by minting the required tokenId(s) for user
   * Should set `withdrawnTokens` mapping to `false` for the tokenId being deposited
   * Minting can also be done by other functions
   * @param user address for whom deposit is being done
   * @param depositData abi encoded tokenIds. Batch deposit also supported.
   */
  function deposit(address user, bytes calldata depositData)
    external
    override
    onlyRole(DEPOSITOR_ROLE)
  {
    if (depositData.length == 32) {
      uint256 tokenId = abi.decode(depositData, (uint256));
      withdrawnTokens[tokenId] = false;
      _mint(user, tokenId);
    } else {
      uint256[] memory tokenIds = abi.decode(depositData, (uint256[]));
      for (uint256 i; i < tokenIds.length; i++) {
        withdrawnTokens[tokenIds[i]] = false;
        _mint(user, tokenIds[i]);
      }
    }
  }

  /**
   * @notice called when user wants to withdraw token back to root chain
   * @dev Should handle withdraw by burning user's token.
   * Should set `withdrawnTokens` mapping to `true` for the tokenId being withdrawn
   * This transaction will be verified when exiting on root chain
   * @param _tokenId tokenId to withdraw
   */
  function withdraw(uint256 _tokenId) external {
    require(_msgSender() == ownerOf(_tokenId), "Caller is not owner");
    withdrawnTokens[_tokenId] = true;
    _burn(_tokenId);
  }

  /**
   * @notice called when user wants to withdraw multiple tokens back to root chain
   * @dev Should burn user's tokens. This transaction will be verified when exiting on root chain
   * @param _tokenIds tokenId list to withdraw
   */
  function withdrawBatch(uint256[] calldata _tokenIds) external {
    uint256 length = _tokenIds.length;
    require(length <= BATCH_LIMIT, "Exceeds batch limit");

    for (uint256 i; i < length; i++) {
      uint256 tokenId = _tokenIds[i];

      require(_msgSender() == ownerOf(tokenId), "Caller is not owner");
      withdrawnTokens[tokenId] = true;
      _burn(tokenId);
    }

    // Used in MintableERC721 predicate contract on L1 while verifying burn proof
    emit WithdrawnBatch(_msgSender(), _tokenIds);
  }

  /**
   * @notice called when user wants to withdraw token back to root chain with token URI
   * @dev Should handle withdraw by burning user's token.
   * Should set `withdrawnTokens` mapping to `true` for the tokenId being withdrawn
   * This transaction will be verified when exiting on root chain
   * @param _tokenId tokenId to withdraw
   */
  function withdrawWithMetadata(uint256 _tokenId) external {
    require(_msgSender() == ownerOf(_tokenId), "Caller is not owner");
    withdrawnTokens[_tokenId] = true;

    // Encoding metadata associated with tokenId & emitting event
    emit TransferWithMetadata(ownerOf(_tokenId), address(0), _tokenId, encodeTokenMetadata(_tokenId));

    _burn(_tokenId);
  }

  /**
   * @notice This method is supposed to be called by client when withdrawing token with metadata
   * and pass return value of this function as second parameter of `withdrawWithMetadata` method
   * @param _tokenId Token for which URI to be fetched
   * @return bytes encoded metadata for a specified token
   */
  function encodeTokenMetadata(uint256 _tokenId)
    public
    view
    virtual
    returns (bytes memory)
  {
    return abi.encode(tokenURI(_tokenId));
  }

  /**
   * @dev Private function overrides ERC721 _beforeTokenTransfer function
   * @param _from address of the owner sending the token
   * @param _to address of the owner to receive the token
   * @param _tokenId uint256 hexadecimal tag ID represented as an integer
   */
  function _beforeTokenTransfer(
    address _from,
    address _to,
    uint256 _tokenId
  )
    internal
    virtual
    override(ERC721, ERC721Enumerable)
  {
    super._beforeTokenTransfer(_from, _to, _tokenId);
  }

  /**
   * @dev Public function overrides ERC721 supportsInterface function
   * @param _interfaceId byte string identifying the contract's interface
   * @return bool flag indicating whether or not the contract supports the specified interface
   */
  function supportsInterface(bytes4 _interfaceId)
    public
    view
    virtual
    override(ICollectIdCore, ERC721, ERC721Enumerable, AccessControl)
    returns (bool)
  {
    return super.supportsInterface(_interfaceId);
  }
}
