      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.7;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

contract Staking is ReentrancyGuard, Ownable {
    IERC20 public stakingToken;
    IERC20 public rewardToken;

    uint256 public constant SECONDIN_DAY = 86400;
    uint256 public constant REWARD_RATE = 1e16; // 1% per day in wei (0.01 * 1e18)
    uint256 public totalStakedBalance;
    uint256 public rewardPerTokenStored;
    uint256 public lastUpdateTime;

    /** @dev Mapping from address to the amount the user has staked */
    mapping(address => uint256) public balances;

    /** @dev Mapping from address to the amount the user has been rewarded */
    mapping(address => uint256) public userRewardPerTokenPaid;

    /** @dev Mapping from address to the rewards claimable for the user */
    mapping(address => uint256) public rewards;

    constructor(address _stakingToken, address _rewardToken) {
        stakingToken = IERC20(_stakingToken);
        rewardToken = IERC20(_rewardToken);
    }

    modifier updateReward(address account) {
        rewardPerTokenStored = rewardPerToken();
        lastUpdateTime = block.timestamp;
        rewards[account] = earned(account);
        userRewardPerTokenPaid[account] = rewardPerTokenStored;
        _;
    }

    modifier moreThanZero(uint256 amount) {
        require(amount > 0, "Staking__NeedsMoreThanZero");
        _;
    }

    function earned(address account) public view returns (uint256) {
        uint256 currentBalance = balances[account];
        uint256 amountPaid = userRewardPerTokenPaid[account];
        uint256 currentRewardPerToken = rewardPerToken();
        uint256 pastRewards = rewards[account];
        uint256 _earned = ((currentBalance * (currentRewardPerToken - amountPaid)) / 1e18) + pastRewards;
        return _earned;
    }

    function rewardPerToken() public view returns (uint256) {
        if (totalStakedBalance == 0) {
            return rewardPerTokenStored;
        } else {
            return rewardPerTokenStored + ((block.timestamp - lastUpdateTime) * REWARD_RATE) / totalStakedBalance;
        }
    }

    function stake(uint256 amount) external updateReward(msg.sender) moreThanZero(amount) {
        balances[msg.sender] += amount;
        totalStakedBalance += amount;
        require(stakingToken.transferFrom(msg.sender, address(this), amount), "Staking__TransferFailed");
    }

    function withdraw(uint256 amount) external updateReward(msg.sender) moreThanZero(amount) {
        balances[msg.sender] -= amount;
        totalStakedBalance -= amount;
        require(stakingToken.transfer(msg.sender, amount), "Withdraw__TransferFailed");
    }

    function claimReward() external updateReward(msg.sender) {
        uint256 reward = rewards[msg.sender];
        require(rewardToken.transfer(msg.sender, reward), "Staking__TransferFailed");
    }

    function ownerWithdrawStakedBalance() external onlyOwner {
        uint256 balanceToWithdraw = stakingToken.balanceOf(address(this));
        require(stakingToken.transfer(owner(), balanceToWithdraw), "Staking__TransferFailed");
        totalStakedBalance = 0;
        lastUpdateTime = block.timestamp;
    }

    // Getter function for UI
    function getStaked(address account) public view returns (uint256) {
        return balances[account];
    }

    function getTotalStakedBalance() public view returns (uint256) {
        return totalStakedBalance;
    }

    // Retrieve the total staked balance by user
    function getTotalStakedBalanceByUser(address user) public view returns (uint256) {
        return balances[user];
    }

    // Retrieve the rewards earned per second by user
    function getRewardsEarnedPerSecondByUser(address user) public view returns (uint256) {
        uint256 userBalance = balances[user];
        uint256 currentRewardRate = REWARD_RATE;
        uint256 rewardsPerSecond = (userBalance * currentRewardRate) / 1e18;
        return rewardsPerSecond;
    }

    // Retrieve the total rewards claimed by user
    function getRewardsClaimedByUser(address user) public view returns (uint256) {
        return rewards[user];
    }
}"
