pragma solidity 0.8.18;

import "Ownable.sol";
import "Strings.sol";
import "IERC20.sol";

interface IContractPriceFeed {
    function getPriceByTimestamp(string memory _tokenPair, uint256 _timestamp)
        external
        view
        returns (uint256);
}

contract QuickSwing_116 is Ownable {
    IContractPriceFeed public priceFeed;
    IERC20 public usdt; // ERC-20 Token (USDT) Interface

    enum BetDirection {
        BIGGER,
        SMALLER
    }
    mapping(bytes32 => bool) public hashCheckMap;
    mapping(bytes32 => BetRecord) public hashBetRecord;
    address payable public contractOwner; //onwer who is deploy this contract

    enum BetRecordStatus {
        BET,
        WIN,
        LOSE
    }

    struct BetRecord {
        address bettor;
        uint256 amount;
        string tokenPair;
        uint256 roundTimeMill;
        BetDirection direction;
        bytes32 hash;
        BetRecordStatus status;
    }

    struct Round {
        uint256 closingPrice;
        bool priceSet;
        BetRecord[] bets;
        uint256 biggerTotal;
        uint256 smallerTotal;
    }

    // mapping(string => Round) public rounds;

    mapping(string => mapping(uint256 => Round)) public roundMap;

    event BetPlaced(bytes32 hash, uint256 round);
    event Win(bytes32 hash, uint256 amount);

    event ClosingRound(
        uint256 currentRound,
        uint256 previousRound,
        string tokenPair,
        BetDirection direction
    );

    constructor() {
        priceFeed = IContractPriceFeed(
            0x694620C4EE8e6b433C09a213FAEA49B2F8723C93
        );
        usdt = IERC20(0xc2132D05D31c914a87C6611C10748AEb04B58e8F); // Initialize USDT
        contractOwner = payable(msg.sender);
    }

    function setPriceFeed(address addr) public onlyOwner {
        priceFeed = IContractPriceFeed(addr);
    }

    function withdrawToOwner(uint256 amount) public onlyOwner {
        contractOwner.transfer(amount);
    }

    function withdrawUSDTToOwner(uint256 amount) public onlyOwner {
        require(usdt.transfer(contractOwner, amount), "Withdraw failed");
    }

    function getRoundInfo(uint256 timestamp, string memory tokenPair)public view returns(Round memory){
        return roundMap[tokenPair][timestamp];
    }

    function getCurrentBetRoundTime() public view returns (uint256) {
        uint256 palceTimestamp = block.timestamp;
        uint256 nowRoundTimestamp = (palceTimestamp / 300) * 300 + 299;

        uint256 betRoundTimestamp = nowRoundTimestamp + 300;

        uint256 betRoundTimestampMilli = betRoundTimestamp * 1000 + 999;
        return betRoundTimestampMilli;
    }

    function getCurrentRunningRoundTime() public view returns (uint256) {
        uint256 palceTimestamp = block.timestamp;
        uint256 nowRoundTimestamp = (palceTimestamp / 300) * 300 + 299;

        uint256 betRoundTimestamp = nowRoundTimestamp;

        uint256 betRoundTimestampMilli = betRoundTimestamp * 1000 + 999;
        return betRoundTimestampMilli;
    }

    function checkAllowance(address user) public view returns (uint256) {
        return usdt.allowance(user, address(this));
    }

    function getBetRecord(bytes32 hash) public view returns (BetRecord memory) {
        return hashBetRecord[hash];
    }

    function placeBet(
        string memory _tokenPair,
        BetDirection direction,
        bytes32 _hash,
        uint256 amount
    ) public {
        // uint256 amountToBet = usdt.allowance(msg.sender, address(this));
        require(amount > 0, "BetRecord amount must be greater than 0");
        require(!hashCheckMap[_hash], "hash duplicated");

        uint256 palceTimestamp = block.timestamp;
        uint256 nowRoundTimestamp = (palceTimestamp / 300) * 300 + 299;
        require(
            palceTimestamp < nowRoundTimestamp - 30,
            "Betting for this round is closed"
        );
        uint256 betRoundTimestamp = nowRoundTimestamp + 300;
        uint256 betRoundTimestampMilli = betRoundTimestamp * 1000 + 999;

        Round storage round = roundMap[_tokenPair][betRoundTimestampMilli];
        BetRecord memory bet = BetRecord(
            msg.sender,
            amount,
            _tokenPair,
            betRoundTimestampMilli,
            direction,
            _hash,
            BetRecordStatus.BET
        );
        round.bets.push(bet);
        hashBetRecord[_hash] = bet;

        if (direction == BetDirection.BIGGER) {
            round.biggerTotal += amount;
        } else {
            round.smallerTotal += amount;
        }

        hashCheckMap[_hash] = true;
        require(
            usdt.transferFrom(msg.sender, address(this), amount),
            "Transfer of USDT failed"
        );

        emit BetPlaced(_hash, betRoundTimestampMilli);
    }

    function checkBettor(string memory _tokenPair, uint256 _timestamp)
        public
        view
        returns (BetRecord[] memory)
    {
        return roundMap[_tokenPair][_timestamp].bets;
    }

    function closeRounds(uint256 roundTimestamp, string[] memory _tokenPairs)
        public
        onlyOwner
    {
        for (uint256 i = 0; i < _tokenPairs.length; i++) {
            closeRound(roundTimestamp, _tokenPairs[i]);
        }
    }

    function closeRound(uint256 roundTimestamp, string memory _tokenPair)
        private
        onlyOwner
    {
        Round storage round = roundMap[_tokenPair][roundTimestamp];
        require(!round.priceSet, "Round already closed");
        // require(block.timestamp > roundTimestamp, "Round has not finished yet");

        // Get the closing price for the bet round and the previous round
        uint256 currentClosingPrice = priceFeed.getPriceByTimestamp(
            _tokenPair,
            roundTimestamp
        );
        uint256 previousClosingPrice = priceFeed.getPriceByTimestamp(
            _tokenPair,
            roundTimestamp - 300000
        );
        require(currentClosingPrice > 0, "can't get current price");
        require(previousClosingPrice > 0, "can't get previous price");

        // Check if the price has gone up or down
        bool priceWentUp = currentClosingPrice > previousClosingPrice;

        // Based on whether the price went up or down, determine the winning direction
        BetDirection winningDirection = priceWentUp
            ? BetDirection.BIGGER
            : BetDirection.SMALLER;
        uint256 winningPool = priceWentUp
            ? round.biggerTotal
            : round.smallerTotal;

        // Total bet amount in the winning direction
        uint256 prizePoolTotal = (round.biggerTotal + round.smallerTotal);

        // Payout to winners
        for (uint256 i = 0; i < round.bets.length; i++) {
            BetRecord storage bet = round.bets[i];
            if (bet.direction == winningDirection) {
                // 1.5% for operation fee
                uint256 reward = (((bet.amount * prizePoolTotal) /
                    winningPool) * 985) / 1000;
                require(usdt.transfer(bet.bettor, reward), "Payout failed");

                bet.status = BetRecordStatus.WIN;            
                emit Win(bet.hash, reward);
            } else {
                bet.status = BetRecordStatus.LOSE;            
                emit Win(bet.hash, 0);
            }
            hashBetRecord[bet.hash] = bet;
        }

        round.closingPrice = uint256(currentClosingPrice);
        round.priceSet = true;

        emit ClosingRound(
            currentClosingPrice,
            previousClosingPrice,
            _tokenPair,
            winningDirection
        );
    }
}
