    "contracts/MyHomiesAirdrops.sol": {
      "content": "// SPDX-License-Identifier: GPL3
/// @title: My Homies In Dreamland Airdrop Contract
/// @author: DropHero LLC
pragma solidity ^0.8.9;

import "@openzeppelin/contracts/token/ERC1155/extensions/ERC1155Supply.sol";
import "@openzeppelin/contracts/token/ERC1155/extensions/ERC1155Pausable.sol";
import "@openzeppelin/contracts/access/AccessControlEnumerable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "./lib/MetaTx/EIP712MetaTransaction.sol";
import "./lib/SimpleERC2981Royalties.sol";

contract MyHomiesAirdrops is ERC1155Supply, ERC1155Pausable, Ownable, SimpleERC2981Royalties, EIP712MetaTransaction {
    mapping(uint256 => bool) private _supplyLocked;
    string _ipfsHash;

    constructor(string memory ipfsHash_) ERC1155("") SimpleERC2981Royalties(msg.sender, 1000)
    {
        _initializeEIP712(name());
        _ipfsHash = ipfsHash_;
    }

    function name() public pure returns (string memory) {
        return "Cheech and Chong Poster Art";
    }

    function symbol() public pure returns (string memory) {
        return "CNCPA";
    }

    function pause() public onlyOwner {
        _pause();
    }

    function unpause() public onlyOwner {
        _unpause();
    }

    function setIPFSHash(string calldata hash_) external onlyOwner {
        _ipfsHash = hash_;
    }

    function uri(uint256 tokenId) public view override returns (string memory) {
        return string(abi.encodePacked("ipfs://", _ipfsHash, "/", Strings.toString(tokenId), ".json"));
    }

    function mintToken(
        uint256 tokenId,
        address[] calldata recipients
    ) external onlyOwner whenNotPaused {
        require(!_supplyLocked[tokenId], "TOKEN SUPPLY LOCKED");

        for(uint i = 0; i < recipients.length; i++) {
            _mint(recipients[i], tokenId, 1, "");
        }
    }

    function lockSupply(uint256 tokenId) external onlyOwner {
        _supplyLocked[tokenId] = true;
    }

    function supplyLocked(uint256 tokenId) external view returns(bool) {
        return _supplyLocked[tokenId];
    }

    function setRoyaltiesPercentage(uint24 basisPoints) external onlyOwner {
        _setRoyaltiesPercentage(basisPoints);
    }

    function setRoyaltiesAddress(address addr) external onlyOwner {
        _setRoyaltiesAddress(addr);
    }

    function supportsInterface(bytes4 interfaceId)
        public
        view
        virtual
        override(ERC1155, IERC165)
        returns (bool)
    {
        return interfaceId == type(IERC2981).interfaceId ||
        super.supportsInterface(interfaceId);
    }

    function _beforeTokenTransfer(
        address operator,
        address from,
        address to,
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data
    ) internal virtual override(ERC1155Pausable, ERC1155Supply) {
        super._beforeTokenTransfer(operator, from, to, ids, amounts, data);
    }

    function _msgSender()
        internal
        override
        view
        returns (address sender)
    {
        return EIP712MetaTransaction.msgSender();
    }
}
"
    },
