    "contracts/lib/SimpleERC2981Royalties.sol": {
      "content": "// SPDX-License-Identifier: GPL3
/// @title: ERC2971 Royalties Contract
/// @author: DropHero LLC
pragma solidity ^0.8.9;
import "@openzeppelin/contracts/interfaces/IERC2981.sol";

abstract contract SimpleERC2981Royalties is IERC2981 {
    struct RoyaltyInfo {
        address recipient;
        uint24 basisPoints;
    }

    RoyaltyInfo private _royalties;

    constructor(address royaltiesAddress, uint24 royaltiesBasisPoints) {
        _royalties.recipient = royaltiesAddress;
        _royalties.basisPoints = royaltiesBasisPoints;
    }

    function _setRoyaltiesPercentage(uint24 basisPoints) internal {
        require(basisPoints <= 10000, 'BASIS_POINTS_TOO_HIGH');
        _royalties.basisPoints = basisPoints;
    }

    function _setRoyaltiesAddress(address addr) internal {
        _royalties.recipient = addr;
    }

    function royaltyInfo(uint256, uint256 salePrice)
        external
        view
        returns (address receiver, uint256 royaltyAmount)
    {
        RoyaltyInfo memory royalties = _royalties;
        receiver = royalties.recipient;
        royaltyAmount = (salePrice * royalties.basisPoints) / 10000;
    }
}
"
