    "contracts/lib/MetaTx/EIP712Base.sol": {
      "content": "//SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract EIP712Base {

    struct EIP712Domain {
        string name;
        string version;
        address verifyingContract;
        bytes32 salt;
    }

    string constant public ERC712_VERSION = "1";

    bytes32 internal constant EIP712_DOMAIN_TYPEHASH = keccak256(bytes("EIP712Domain(string name,string version,address verifyingContract,bytes32 salt)"));

    bytes32 internal domainSeparator;

    /*
     * Using a constructor to initialize can cause some unexpected behavior if relying
     * on things like `name()` set by another constructor for ERC721 or similar, as the
     * ordering is not reliable. This should instead be called inside the constructor body.
     */
    function _initializeEIP712(string memory name) internal
    {
        require(domainSeparator == bytes32(0), 'Already initialized');

        domainSeparator = keccak256(abi.encode(
            EIP712_DOMAIN_TYPEHASH,
            keccak256(bytes(name)),
            keccak256(bytes(ERC712_VERSION)),
            address(this),
            bytes32(getChainID())
        ));
    }

    function getChainID() public view returns (uint256 id) {
        assembly {
            id := chainid()
        }
    }

    function getDomainSeparator() public view returns(bytes32) {
        return domainSeparator;
    }

    /**
    * Accept message hash and returns hash message in EIP712 compatible form
    * So that it can be used to recover signer from signature signed using EIP712 formatted data
    * https://eips.ethereum.org/EIPS/eip-712
    * "\\x19" makes the encoding deterministic
    * "\\x01" is the version byte to make it compatible to EIP-191
    */
    function toTypedMessageHash(bytes32 messageHash) internal view returns(bytes32) {
        require(domainSeparator != bytes32(0), "EIP712 not initialized.");

        return keccak256(abi.encodePacked("\x19\x01", getDomainSeparator(), messageHash));
    }
}
